import React, { useState, useEffect } from 'react';

/**
 * Groups timeline items by "year marker"—an item with no `company` and no `content`.
 * Returns an array of objects: { marker: string, items: [] }
 */
function groupTimeline(timeline) {
  const groups = [];
  let currentGroup = null;

  timeline.forEach((item) => {
    const isYearMarker = !item.company && !item.content;
    if (isYearMarker) {
      if (currentGroup) {
        groups.push(currentGroup);
      }
      currentGroup = { marker: item.date, items: [] };
    } else {
      if (!currentGroup) {
        currentGroup = { marker: '', items: [] };
      }
      currentGroup.items.push(item);
    }
  });

  if (currentGroup) {
    groups.push(currentGroup);
  }

  return groups;
}

function Showcase() {
  // LLM Timeline data
  const llmTimeline = [
    { date: '2022, Pre-training / Scaling Law / Chatbot' },
    { date: 'Mar 15th, 2022', company: 'OpenAI', content: 'OpenAI released GPT-3.5 text-davinci-002 API' },
    { date: 'Nov 30th, 2022', company: 'OpenAI', content: 'OpenAI released ChatGPT with GPT-3.5' },
    { date: '2023, Post-training / Agent' },
    { date: 'Jan 23rd, 2023', company: 'OpenAI', content: 'Microsoft announced a new multiyear investment in OpenAI, amounting to $10 billion. This investment valued OpenAI at approximately $29 billion' },
    { date: 'Mar 14th, 2023', company: 'OpenAI', content: 'OpenAI released GPT-4' },
    { date: '2024, Reasoning / Virtual Productivity' },
    { date: 'Sep 20th, 2024', company: 'OpenAI', content: 'OpenAI released o1-preview, 1st reasoning model' },
    { date: 'Oct 2nd, 2024', company: 'OpenAI', content: 'OpenAI announced the completion of a $6.6 billion funding round, elevating valuation to $157 billion' },
    { date: '2025, Open source / Social Impact' },
    { date: 'Jan 20th, 2025', company: 'Deepseek', content: 'Deepseek released Deepseek-R1, 1st open-weights reasoning model' }
  ];

  // Robot Timeline data
  const robotTimeline = [
    { date: '2025, RL / VLM / Reasoning' },
    {
      date: 'Feb 20th, 2025',
      company: 'Figure',
      content: 'Figure released reasoning robot with collaboration; valuation jumped from $2.6B (Cursor $2.5B) to $39.5B (Anthropic $60B)',
    },
    { date: '2026, Mass Production / Human-Robot Interaction' },
    {
      date: 'By Mid 2026',
      company: 'Predict',
      content: 'The humanoid robot industry is gradually maturing, signaling the potential for large-scale mass production',
    },
    {
      date: 'By Late 2026',
      company: 'Predict',
      content: 'As the price of humanoid robots decreases, human-robot interaction becomes a trending field',
    },
    { date: '2027, Physical Productivity' },
    {
      date: 'By Late 2027',
      company: 'Predict',
      content: 'Mass-produced robots are increasingly automating physical work in real world',
    },
    { date: '2028, Open Source / Social Impact' },
    {
      date: 'By Early 2028',
      company: 'Predict',
      content: 'More companies are entering the market and adopting open-source strategies, significantly reducing the cost of robots. This further expands their applications and creates a broader societal impact',
    },
  ];

  const llmGroups = groupTimeline(llmTimeline);
  const robotGroups = groupTimeline(robotTimeline);

  const [llmOpenStates, setLlmOpenStates] = useState(llmGroups.map(() => true));
  const [robotOpenStates, setRobotOpenStates] = useState(robotGroups.map(() => true));

  function toggleLlmGroup(index) {
    setLlmOpenStates((prev) => {
      const copy = [...prev];
      copy[index] = !copy[index];
      return copy;
    });
  }

  function toggleRobotGroup(index) {
    setRobotOpenStates((prev) => {
      const copy = [...prev];
      copy[index] = !copy[index];
      return copy;
    });
  }

  useEffect(() => {
    const script = document.createElement('script');
    script.async = true;
    script.src = 'https://platform.twitter.com/widgets.js';
    script.charset = 'utf-8';
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  function getIconByCompany(company) {
    switch (company) {
      case 'OpenAI':
        return '/images/OpenAI.png';
      case 'Deepseek':
        return '/images/Deepseek.png';
      case 'Figure':
        return '/images/Figure.png';
      case 'Predict':
        return '/images/Q.png';
      default:
        return '/images/OpenAI.png';
    }
  }

  return (
    <div style={{ position: 'relative', padding: '0', margin: '0' }}>
      <style>{`
        * {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
        }
        body {
          font-family: Arial, sans-serif;
          background: #fff;
        }
        /* Visually hidden text for accessibility */
        .sr-only {
          position: absolute;
          width: 1px;
          height: 1px;
          padding: 0;
          margin: -1px;
          overflow: hidden;
          clip: rect(0, 0, 0, 0);
          white-space: nowrap;
          border: 0;
        }
        /* ================= Introduction Section ================= */
        .section-intro {
          background-color: #E6F2FF;
          padding: 2rem 1rem;
          text-align: center;
        }
        .section-intro h3 {
          font-size: 2rem;
          margin-bottom: 0.5rem;
          color: #333;
        }
        .section-intro h4 {
          font-size: 1.3rem;
          font-weight: 400;
          margin-bottom: 1.5rem;
          color: #666;
        }
        .author-info {
          margin-bottom: 1rem;
        }
        .author-link {
          color: #000;
          text-decoration: none;
          font-weight: bold;
          transition: color 0.2s;
        }
        .author-link:hover {
          color: #555;
          text-decoration: underline;
        }
        .intro-text {
          max-width: 980px;
          margin: 0 auto;
          text-align: left;
          line-height: 1.6;
          font-size: 1rem;
          color: #333;
          display: flex;
          align-items: flex-start;
          gap: 0.5em;
        }
        .intro-text img {
          width: 300px;
          height: 300px;
          object-fit: contain;
          padding-right: 2em;
        }
        .intro-text .text-block {
          padding-top: 1em;
        }
        /* ================= Timeline Section ================= */
        .timeline-section-container {
          background-color: #f8f9f9;
          padding: 2rem 1rem;
          /* 让它能放下整条线 */
          position: relative;
        }
        .timeline-section-title {
          text-align: center;
          margin-bottom: 1.5rem;
          font-size: 2rem;
          color: #333;
        }
        .timeline-section {
          display: flex;
          flex-direction: column;
          gap: 2rem;
          max-width: 1100px;
          margin: 0 auto;
          /* 父容器加 position: relative，准备画两条不间断竖线 */
          position: relative;
        }
        /* 左边整条线（从上到下） */
        .timeline-section::before {
          content: '';
          position: absolute;
          top: 0;
          bottom: 0;
          left: 10%; /* 你可以根据需要微调 */
          width: 4px;
          background: linear-gradient(to bottom, #ff7f50, #ffd080);
          border-radius: 2px;
        }
        /* 右边整条线（从上到下） */
        .timeline-section::after {
          content: '';
          position: absolute;
          top: 0;
          bottom: 0;
          right: 10%; /* 你可以根据需要微调 */
          width: 4px;
          background: linear-gradient(to bottom, #ff7f50, #ffd080);
          border-radius: 2px;
        }

        /* 行容器：存放左右两部分 */
        .timeline-row {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          gap: 2rem;
        }

        /* 给左侧和右侧各自一个 class，用来做偏移 */
        .timeline-wrapper.left {
          /* 让到左侧那条线的右边 */
          width: 45%;
          margin-left: calc(10% + 40px); 
          position: relative;
        }
        .timeline-wrapper.right {
          /* 让到右侧那条线的左边 */
          width: 45%;
          margin-right: calc(10% + 40px);
          position: relative;
        }

        .timeline-wrapper h2 {
          text-align: center;
          margin-bottom: 1.5rem;
          font-size: 1.2rem;
          color: #333;
        }

        /* 原先 .timeline-wrapper::before 的竖线去掉了，只保留小圆点 */
        /* timeline-item 保持不变，只是去掉大竖线部分 */
        .timeline-item {
          position: relative;
          margin: 0.8rem 0;
          padding-left: 70px; /* 给内容留一点左间距, 可微调 */
          opacity: 0;
          animation-duration: 0.8s;
          animation-fill-mode: forwards;
        }
        .timeline-icon {
          position: absolute;
          left: 10px; /* 与上面padding-left:70px配合，让icon落在合适位置 */
          top: 10px;
          width: 40px;
          height: 40px;
          border-radius: 50%;
          overflow: hidden;
          display: flex;
          align-items: center;
          justify-content: center;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
          background-color: #fff;
        }
        .timeline-icon img {
          width: 100%;
          height: auto;
          object-fit: contain;
        }
        .timeline-details {
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
        }
        .timeline-date {
          font-weight: bold;
          color: #555;
        }
        .timeline-content {
          background-color: #f7f7f7;
          padding: 10px 15px;
          border-radius: 8px;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
          border-left: 3px solid #ff7f50;
        }

        /* 保留小圆点 */
        .timeline-item::before {
          content: '';
          position: absolute;
          left: 28px;
          top: 20px;
          width: 12px;
          height: 12px;
          background-color: #ff7f50;
          border-radius: 50%;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
        }

        @keyframes fadeInLeft {
          from { transform: translateX(-30px); opacity: 0; }
          to { transform: translateX(0); opacity: 1; }
        }
        @keyframes fadeInRight {
          from { transform: translateX(30px); opacity: 0; }
          to { transform: translateX(0); opacity: 1; }
        }
        .fade-in-left { animation-name: fadeInLeft; }
        .fade-in-right { animation-name: fadeInRight; }

        .year-marker {
          display: inline-block;
          margin: 0.5rem 0;
          padding: 0.4rem 0.8rem;
          font-size: 1rem;
          font-weight: bold;
          color: #333;
          background: #fff;
          border: 1px solid #ddd;
          border-radius: 6px;
          box-shadow: 0 2px 5px rgba(0,0,0,0.05);
          cursor: pointer;
          transition: background-color 0.2s, box-shadow 0.2s;
        }
        .year-marker:hover {
          background-color: #f7f7f7;
          box-shadow: 0 3px 7px rgba(0,0,0,0.1);
        }
        .year-marker::before { display: none; }
        .year-marker .timeline-icon { display: none; }

        /* ================= Event Section ================= */
        .event-section {
          background-color: #ebedef;
          padding: 3rem 1rem;
        }
        .event-section .section-title {
          text-align: center;
          font-size: 2rem;
          margin-bottom: 2rem;
          color: #333;
        }
        .event-container {
          max-width: 1100px;
          margin: 0 auto;
          display: flex;
          flex-direction: row;
          align-items: flex-start;
          justify-content: center;
          gap: 2rem;
          background: #ffffff;
          border-radius: 12px;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
          padding: 2rem;
        }
        .event-image {
          width: 40%;
          height: auto;
          object-fit: cover;
          border-radius: 8px;
        }
        .event-info {
          width: 60%;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
        }
        .event-time-location-organizer {
          display: flex;
          flex-direction: row;
          gap: 3rem;
        }
        .event-time-location, .event-organizers {
          flex: 1;
        }
        .event-time-location h3,
        .event-organizers h3 {
          margin: 0;
          margin-bottom: 0.8rem;
          font-size: 1.3rem;
          color: #333;
        }
        .location-list {
          list-style: none;
          padding: 0;
          margin: 0;
          line-height: 1.8;
        }
        .location-list li { margin-bottom: 0.4rem; }
        .event-organizers {
          display: flex;
