import React, { useState } from 'react';

function Simulator() {
  // ======= 状态管理 =======
  const [playerCards, setPlayerCards] = useState([]);
  const [bankerCards, setBankerCards] = useState([]);
  const [result, setResult] = useState('');
  const [balance, setBalance] = useState(1000);
  const [betType, setBetType] = useState('');
  const [betAmount, setBetAmount] = useState(0);
  const [message, setMessage] = useState('');
  // 记牌器：保存每局赢家（Player/Banker/Tie）
  const [history, setHistory] = useState([]);

  // 牌组：1～9 对应数字，10/J/Q/K 均记作 0
  const cardDeck = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0, 0, 0, 0];

  // 随机抽一张牌
  function drawCard() {
    const randomIndex = Math.floor(Math.random() * cardDeck.length);
    return cardDeck[randomIndex];
  }

  // 计算牌点数（取个位数）
  function modSum(cards) {
    return cards.reduce((acc, card) => acc + card, 0) % 10;
  }

  // 模拟一局百家乐游戏
  function simulateGame() {
    let pCards = [drawCard(), drawCard()];
    let bCards = [drawCard(), drawCard()];
    let playerTotal = modSum(pCards);
    let bankerTotal = modSum(bCards);
    let playerThird = null;

    // 如果任一方拿到自然牌（8 或 9），则不补牌
    if (!(playerTotal === 8 || playerTotal === 9 || bankerTotal === 8 || bankerTotal === 9)) {
      // 玩家规则：点数 0～5 补一张牌
      if (playerTotal <= 5) {
        playerThird = drawCard();
        pCards.push(playerThird);
        playerTotal = modSum(pCards);
      }
      // 庄家规则
      if (bankerTotal <= 2) {
        bCards.push(drawCard());
        bankerTotal = modSum(bCards);
      } else {
        if (playerThird === null) {
          if (bankerTotal <= 5) {
            bCards.push(drawCard());
            bankerTotal = modSum(bCards);
          }
        } else {
          const pt = playerThird;
          if (bankerTotal === 3) {
            if (pt !== 8) {
              bCards.push(drawCard());
              bankerTotal = modSum(bCards);
            }
          } else if (bankerTotal === 4) {
            if (pt >= 2 && pt <= 7) {
              bCards.push(drawCard());
              bankerTotal = modSum(bCards);
            }
          } else if (bankerTotal === 5) {
            if (pt >= 4 && pt <= 7) {
              bCards.push(drawCard());
              bankerTotal = modSum(bCards);
            }
          } else if (bankerTotal === 6) {
            if (pt === 6 || pt === 7) {
              bCards.push(drawCard());
              bankerTotal = modSum(bCards);
            }
          }
          // 庄家 7 点时不补牌
        }
      }
    }

    // 判定胜负：结果为 "Player"、"Banker" 或 "Tie"
    let gameResult = '';
    if (playerTotal > bankerTotal) {
      gameResult = 'Player';
    } else if (bankerTotal > playerTotal) {
      gameResult = 'Banker';
    } else {
      gameResult = 'Tie';
    }

    setPlayerCards(pCards);
    setBankerCards(bCards);
    setResult(gameResult);

    // 记录到历史记录
    setHistory((prev) => [...prev, gameResult]);

    // 结算
    resolveBet(gameResult);
  }

  // 结算：只对赢的情况收取 2% 抽水
  function resolveBet(gameResult) {
    if (!betType || betAmount <= 0) {
      setMessage('请先下注！');
      return;
    }
    let newBalance = balance;

    if (gameResult === betType) {
      // 用户押注正确 => 赢
      const commission = betAmount * 0.02; // 2% 抽水
      // 只对赢的部分收取抽水，这里简化为赢得等额下注，并从中扣 2%
      newBalance += betAmount - commission;
      setMessage(`恭喜，你赢了 ${betAmount}，抽水 ${commission.toFixed(2)}`);
    } else if (gameResult === 'Tie' && betType !== 'Tie') {
      // 和局且用户没押和 => 退还本金
      setMessage(`和局，无输无赢`);
      // 注意：如果真实百家乐要退还本金，需要先扣除本金再退回，
      // 这里简化为不动余额
    } else if (gameResult !== 'Tie' && gameResult !== betType) {
      // 用户压错 => 输
      newBalance -= betAmount;
      setMessage(`很遗憾，你输了 ${betAmount}`);
    } else if (gameResult === 'Tie' && betType === 'Tie') {
      // 押和 => 赢，依旧收 2% 抽水（这里也简化为 1:1 的赔率）
      const commission = betAmount * 0.02;
      newBalance += betAmount - commission;
      setMessage(`恭喜，你赢了 ${betAmount}，抽水 ${commission.toFixed(2)}`);
    }

    setBalance(newBalance);
  }

  // 下注并开始游戏
  function handleBetSubmit(e) {
    e.preventDefault();
    setMessage('');
    if (betAmount > balance) {
      setMessage('下注金额不能超过余额！');
      return;
    }
    simulateGame();
  }

  // 重置牌局（不重置余额和历史记录）
  function resetGame() {
    setPlayerCards([]);
    setBankerCards([]);
    setResult('');
    setMessage('');
  }

  // ======= 样式定义 =======
  // 外层背景：渐变 + 居中
  const outerStyle = {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #ff9a9e, #fad0c4)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '20px'
  };

  // 统一的绿色背景容器，内部包含庄家区、玩家区、下注区、历史记录等
  const tableContainerStyle = {
    width: '800px',
    background: '#0b6623',
    borderRadius: '15px',
    boxShadow: '0 0 20px rgba(0,0,0,0.5)',
    color: '#fff',
    padding: '20px'
  };

  // 牌区（庄家/玩家）的样式
  const areaStyle = {
    textAlign: 'center',
    marginBottom: '20px'
  };

  const cardRowStyle = {
    display: 'flex',
    justifyContent: 'center',
    margin: '10px 0'
  };

  const cardStyle = {
    width: '60px',
    height: '90px',
    border: '2px solid #fff',
    borderRadius: '8px',
    backgroundColor: '#fff',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '24px',
    color: '#000',
    margin: '0 5px'
  };

  // 下注表单与按钮
  const formStyle = {
    display: 'inline-block',
    textAlign: 'left',
    marginTop: '10px'
  };

  const inputStyle = {
    marginLeft: '10px',
    width: '80px',
    padding: '5px'
  };

  const radioLabelStyle = {
    marginRight: '15px'
  };

  const buttonStyle = {
    marginTop: '10px',
    padding: '8px 16px',
    border: 'none',
    borderRadius: '5px',
    background: '#ff6f61',
    color: '#fff',
    cursor: 'pointer',
    marginRight: '10px'
  };

  // 历史记录区域
  const historyAreaStyle = {
    textAlign: 'center',
    marginTop: '20px'
  };

  const historyRowStyle = {
    display: 'flex',
    justifyContent: 'center',
    flexWrap: 'wrap',
    marginTop: '10px'
  };

  const historyCircleStyle = {
    width: '30px',
    height: '30px',
    borderRadius: '50%',
    margin: '5px',
    color: '#fff',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  };

  return (
    <div style={outerStyle}>
      <div style={tableContainerStyle}>
        {/* ======= 庄家牌区 ======= */}
        <div style={areaStyle}>
          <h2>庄家</h2>
          <div style={cardRowStyle}>
            {bankerCards.map((card, index) => (
              <div key={index} style={cardStyle}>
                {card}
              </div>
            ))}
          </div>
          <p>总点数：{bankerCards.length ? modSum(bankerCards) : 0}</p>
        </div>

        <hr style={{ borderColor: '#fff', opacity: 0.5 }} />

        {/* ======= 玩家牌区 ======= */}
        <div style={areaStyle}>
          <h2>玩家</h2>
          <div style={cardRowStyle}>
            {playerCards.map((card, index) => (
              <div key={index} style={cardStyle}>
                {card}
              </div>
            ))}
          </div>
          <p>总点数：{playerCards.length ? modSum(playerCards) : 0}</p>
          {result && (
            <p>
              结果：
              {result === 'Player'
                ? '玩家获胜'
                : result === 'Banker'
                ? '庄家获胜'
                : '和局'}
            </p>
          )}
        </div>

        <hr style={{ borderColor: '#fff', opacity: 0.5 }} />

        {/* ======= 下注区 ======= */}
        <div style={areaStyle}>
          <h2>下注区</h2>
          <p>当前余额：{balance.toFixed(2)}</p>
          <form onSubmit={handleBetSubmit} style={formStyle}>
            <div>
              <label>
                下注金额：
                <input
                  type="number"
                  value={betAmount}
                  onChange={(e) => setBetAmount(Number(e.target.value))}
                  min="1"
                  style={inputStyle}
                />
              </label>
            </div>
            <div style={{ marginTop: '10px' }}>
              <label style={radioLabelStyle}>
                <input
                  type="radio"
                  name="betType"
                  value="Player"
                  onChange={(e) => setBetType(e.target.value)}
                  checked={betType === 'Player'}
                />
                玩家
              </label>
              <label style={radioLabelStyle}>
                <input
                  type="radio"
                  name="betType"
                  value="Banker"
                  onChange={(e) => setBetType(e.target.value)}
                  checked={betType === 'Banker'}
                />
                庄家
              </label>
              <label style={radioLabelStyle}>
                <input
                  type="radio"
                  name="betType"
                  value="Tie"
                  onChange={(e) => setBetType(e.target.value)}
                  checked={betType === 'Tie'}
                />
                和局
              </label>
            </div>
            <button type="submit" style={buttonStyle}>
              下注并发牌
            </button>
            <button type="button" onClick={resetGame} style={{ ...buttonStyle, background: '#999' }}>
              重置桌面
            </button>
          </form>
          {message && <p style={{ marginTop: '10px' }}>{message}</p>}
        </div>

        {/* ======= 历史记录（记牌器） ======= */}
        <div style={historyAreaStyle}>
          <h3>历史记录</h3>
          <div style={historyRowStyle}>
            {history.map((res, idx) => {
              let color;
              let text;
              if (res === 'Player') {
                color = '#3498db'; // 蓝色
                text = 'P';
              } else if (res === 'Banker') {
                color = '#e74c3c'; // 红色
                text = 'B';
              } else {
                color = '#2ecc71'; // 绿色
                text = 'T';
              }
              return (
                <div key={idx} style={{ ...historyCircleStyle, backgroundColor: color }}>
                  {text}
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Simulator;
