              const newXMetricValue = xScale.invert(xx);
              const newYMetricValue = yScale.invert(yy);

              const oldXMetricValue = d[xAxisMetric];
              const oldYMetricValue = d[yAxisMetric];
              const deltaX = newXMetricValue - oldXMetricValue;
              const deltaY = newYMetricValue - oldYMetricValue;

              let modifications = [];
              if (Math.abs(deltaX) > 5) {
                modifications.push({ metric: xAxisMetric, direction: deltaX > 0 ? 'increase' : 'decrease' });
              }
              if (Math.abs(deltaY) > 5) {
                modifications.push({ metric: yAxisMetric, direction: deltaY > 0 ? 'increase' : 'decrease' });
              }

              // 复位
              d3.select(this).attr('transform', `translate(${xScale(d[xAxisMetric])}, ${yScale(d[yAxisMetric])})`);

