            fillRule="evenodd"
            clipRule="evenodd"
            d="M6.04199 14.3403C6.69785 14.3403 7.22949 13.8087 7.22949 13.1528C7.22949 12.497 6.69785 11.9653 6.04199 11.9653C5.38614 11.9653 4.85449 12.497 4.85449 13.1528C4.85449 13.8087 5.38614 14.3403 6.04199 14.3403Z"
            fill="#141414"
          />
          <path
            fillRule="evenodd"
            clipRule="evenodd"
            d="M13.167 12.7571C13.8228 12.7571 14.3545 12.2254 14.3545 11.5696C14.3545 10.9137 13.8228 10.3821 13.167 10.3821C12.5111 10.3821 11.9795 10.9137 11.9795 11.5696C11.9795 12.2254 12.5111 12.7571 13.167 12.7571Z"
            fill="#141414"
          />
        </g>
      </svg>
    );

    const tabStyle = {
      display: 'flex',
      alignItems: 'center',
      padding: '10px 16px',
      cursor: 'pointer',
      borderRadius: '9999px',
      transition: 'all 0.2s ease',
      fontSize: '0.875rem',
      fontWeight: 500,
    };

    const explorationActiveStyle = showTree
      ? { backgroundColor: '#fff', color: '#141414' }
      : { backgroundColor: 'transparent', color: '#A0AEC0' };

    const evaluationActiveStyle = !showTree
      ? { backgroundColor: '#fff', color: '#141414' }
      : { backgroundColor: 'transparent', color: '#A0AEC0' };

    return (
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          backgroundColor: '#0F172A', // 类似深色
          padding: '10px 20px',
        }}
      >
        {/* Exploration View Tab */}
        <div style={{ ...tabStyle, ...explorationActiveStyle, marginRight: '6px' }} onClick={() => setShowTree(true)}>
          {explorationIcon}
          Exploration View
        </div>

        {/* Evaluation View Tab */}
        <div style={{ ...tabStyle, ...evaluationActiveStyle }} onClick={() => setShowTree(false)}>
          {evaluationIcon}
          Evaluation View
        </div>
      </div>
    );
  };

  // Dashboard 组件
  // Dashboard 组件
  const Dashboard = ({ node }) => {
    if (!node) {
      return (
        <div
          style={{
            padding: '24px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            height: '160px',
            color: '#9ca3af',
          }}
        >
          <p>Click on a node to view details</p>
        </div>
      );
    }

    return (
      // 整个 dashboard
      <div
        style={{
          border: '1px solid #E5E7EB',
          borderRadius: '8px',
          backgroundColor: '#FFFFFF',
          padding: '16px',
        }}
      >
        {/* 标题 */}
        <h2
          style={{
            fontSize: '1.25rem',
            fontWeight: '700',
            marginBottom: '1rem',
            color: '#111827',
          }}
        >
          {node.title || 'Hypothesis Title'}
        </h2>

        {/* (4) 标签：Complex */}
        <div style={{ width: '100%', height: '100%', position: 'relative', marginBottom: '16px' }}>
          <div
            style={{
              width: 64.3,
              height: 20.65,
              left: 0,
              top: 0,
              position: 'absolute',
              background: '#F9E3E2',
              borderRadius: 57,
            }}
          />
          <div
            style={{
              left: 9.15,
              top: 3.83,
              position: 'absolute',
              color: '#A21919',
              fontSize: 11,
              fontFamily: 'Inter',
              fontWeight: '400',
              wordWrap: 'break-word',
            }}
          >
            Complex
          </div>
        </div>

        {/* IV / DV 卡片（独立 Switch） */}
        <IVDVCard node={node} />

        {/* Novelty / Feasibility / Impact 卡片（独立 Switch） */}
        <ThreeFactorsCard node={node} />

        {showTree ? (
          // Exploration View 下方替换成：Add context + Generate
          <ContextAndGenerateCard />
        ) : (
          // Evaluation View 显示得分对比
          <ScoreComparison node={node} />
        )}
      </div>
    );
  };

  // 1) IV/DV/Relationship 卡片 + 独立 Switch
  const IVDVCard = ({ node }) => {
    const [isAfterIVDV, setIsAfterIVDV] = useState(true);

    // after/before switch
    const SwitchButton = () => (
      <div
        onClick={() => setIsAfterIVDV((prev) => !prev)}
        style={{
          position: 'absolute',
          top: '12px',
          right: '12px',
          cursor: 'pointer',
        }}
      >
        <div
          style={{
            width: '100%',
            height: '100%',
            background: '#EEEEEE',
            borderRadius: 62,
            overflow: 'hidden',
            justifyContent: 'flex-start',
            alignItems: 'center',
            display: 'inline-flex',
          }}
        >
          <div
            style={{
              width: 42,
              height: 17,
              background: isAfterIVDV ? '#2E2E2E' : '#EEEEEE',
              borderRadius: 62,
              overflow: 'hidden',
              justifyContent: 'center',
              alignItems: 'center',
              display: 'flex',
              transition: 'all 0.2s ease',
            }}
          >
            <div
              style={{
                color: isAfterIVDV ? '#EEEEEE' : '#2E2E2E',
                fontSize: 10,
                fontFamily: 'Inter',
                fontWeight: 500,
                wordWrap: 'break-word',
              }}
            >
              After
            </div>
          </div>
          <div
            style={{
              width: 40,
              height: 20,
              background: isAfterIVDV ? '#EEEEEE' : '#2E2E2E',
              borderRadius: 62,
              overflow: 'hidden',
              justifyContent: 'center',
              alignItems: 'center',
              display: 'flex',
              transition: 'all 0.2s ease',
            }}
          >
            <div
              style={{
                color: isAfterIVDV ? '#2E2E2E' : '#EEEEEE',
                fontSize: 10,
                fontFamily: 'Inter',
                fontWeight: 500,
                wordWrap: 'break-word',
              }}
            >
              Before
            </div>
          </div>
        </div>
      </div>
    );

    return (
      <div
        style={{
          position: 'relative',
          border: '1px solid #e5e7eb',
          borderRadius: '8px',
          backgroundColor: '#FAFAFA',
          padding: '16px',
          marginBottom: '16px',
        }}
      >
        <SwitchButton />

        {/* IV / DV */}
        <div style={{ display: 'flex', gap: '8px', marginBottom: '12px' }}>
          <div
            style={{
              backgroundColor: '#ECFDF5',
              color: '#065F46',
              padding: '6px 8px',
              borderRadius: '4px',
              fontSize: '0.875rem',
            }}
          >
            <strong>IV:</strong> Gender, Race
          </div>
          <div
            style={{
              backgroundColor: '#F0F9FF',
              color: '#1E3A8A',
              padding: '6px 8px',
              borderRadius: '4px',
              fontSize: '0.875rem',
            }}
          >
            <strong>DV:</strong> Income
          </div>
        </div>

        {/* Relationship */}
        <div
          style={{
            backgroundColor: '#F9FAFB',
            padding: '12px',
            borderRadius: '6px',
          }}
        >
          <div
            style={{
              fontSize: '0.875rem',
              fontWeight: '500',
              color: '#6b7280',
              marginBottom: '4px',
            }}
          >
            Relationship / Hypothesis ({isAfterIVDV ? 'After' : 'Before'})
          </div>
          <div
            style={{
              marginTop: '4px',
              color: '#374151',
              fontSize: '0.875rem',
              lineHeight: '1.5',
              whiteSpace: 'pre-wrap',
            }}
          >
            {node.content}
          </div>
        </div>
      </div>
    );
  };

  // 2) Novelty / Feasibility / Impact 卡片 + 独立 Switch
  const ThreeFactorsCard = ({ node }) => {
    const [isAfterFactors, setIsAfterFactors] = useState(true);

    const SwitchButton = () => (
      <div
        onClick={() => setIsAfterFactors((prev) => !prev)}
        style={{
          position: 'absolute',
          top: '12px',
          right: '12px',
          width: '82px',
          height: '20px',
          background: '#EEEEEE',
          borderRadius: 62,
          overflow: 'hidden',
          display: 'flex',
          cursor: 'pointer',
        }}
      >
        {/* After */}
        <div
          style={{
            width: 42,
            height: 17,
            background: isAfterFactors ? '#2E2E2E' : '#EEEEEE',
            borderRadius: 62,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            transition: 'all 0.2s ease',
          }}
        >
          <div
            style={{
              color: isAfterFactors ? '#EEEEEE' : '#2E2E2E',
              fontSize: 10,
              fontWeight: 500,
            }}
          >
            After
          </div>
        </div>

        {/* Before */}
        <div
          style={{
            width: 40,
            height: 20,
            background: isAfterFactors ? '#EEEEEE' : '#2E2E2E',
            borderRadius: 62,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            transition: 'all 0.2s ease',
          }}
        >
          <div
            style={{
              color: isAfterFactors ? '#2E2E2E' : '#EEEEEE',
              fontSize: 10,
              fontWeight: 500,
            }}
          >
            Before
          </div>
        </div>
      </div>
    );

    const noveltyText = node.noveltyText || 'Exploring the gender income dynamics within different racial groups.';
    const feasibilityText =
      node.feasibilityText || 'Considering the influence of age, education, and race on income...';
    const impactText =
      node.impactText || 'This hypothesis can be evaluated by comparing the average income...';

    const renderCardItem = (color, title, text) => (
      <div style={{ display: 'flex', marginBottom: '16px' }}>
        <div style={{ marginRight: '12px', marginTop: '4px' }}>
          <div
            style={{
              width: '12px',
              height: '12px',
              borderRadius: '50%',
              backgroundColor: color,
            }}
          ></div>
        </div>
        <div>
          <div
            style={{
              color,
              fontSize: '1rem',
              fontWeight: '600',
              marginBottom: '4px',
            }}
          >
            {title}
          </div>
          <div
            style={{
              color: '#111827',
              fontSize: '1rem',
              lineHeight: '1.4',
            }}
          >
            {text}
          </div>
        </div>
      </div>
    );

    return (
      <div
        style={{
          position: 'relative',
          border: '1px solid #e5e7eb',
          borderRadius: '8px',
          backgroundColor: '#FAFAFA',
          padding: '16px',
          marginBottom: '16px',
        }}
      >
        <SwitchButton />

        {/* 三要素 */}
        {renderCardItem('#FB7185', 'Novelty', noveltyText + ` (${isAfterFactors ? 'After' : 'Before'})`)}
        {renderCardItem('#FBBF24', 'Feasibility', feasibilityText + ` (${isAfterFactors ? 'After' : 'Before'})`)}
        {renderCardItem('#3B82F6', 'Impact', impactText + ` (${isAfterFactors ? 'After' : 'Before'})`)}
      </div>
    );
  };

  // Exploration 下方的 Add context + Generate 新卡片（在 Dashboard 最后）
  const ContextAndGenerateCard = () => {
    return (
      <div
        style={{
          border: '1px solid #e5e7eb',
          borderRadius: '8px',
          backgroundColor: '#FAFAFA',
          padding: '16px',
          marginBottom: '16px',
        }}
      >
        <div style={{ marginBottom: '8px', fontSize: '0.875rem', color: '#6b7280' }}>
          Add context for new hypotheses (option)
        </div>
        <textarea
          rows={2}
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          style={{
            width: '100%',
            fontSize: '0.875rem',
            padding: '8px',
            border: '1px solid #d1d5db',
            borderRadius: '4px',
            resize: 'none',
            marginBottom: '10px',
          }}
        />

        <button
          onClick={generateChildNodes}
          disabled={!selectedNode || isGenerating || isEvaluating || !isAnalysisSubmitted}
          style={{
            padding: '8px 16px',
            backgroundColor: '#4C84FF',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            fontSize: '0.875rem',
            cursor: !selectedNode || isGenerating || isEvaluating || !isAnalysisSubmitted ? 'not-allowed' : 'pointer',
            opacity: !selectedNode || isGenerating || isEvaluating || !isAnalysisSubmitted ? 0.6 : 1,
          }}
        >
          Generate New Hypotheses
        </button>
      </div>
    );
  };

  // 3) Evaluation 下方分数对比
  const ScoreComparison = ({ node }) => {
    const hasBefore = !!node.previousState;
    const noveltyBefore = hasBefore ? node.previousState.noveltyScore ?? 'N/A' : null;
    const noveltyAfter = node.noveltyScore ?? 'N/A';
    const feasibilityBefore = hasBefore ? node.previousState.feasibilityScore ?? 'N/A' : null;
    const feasibilityAfter = node.feasibilityScore ?? 'N/A';
    const impactBefore = hasBefore ? node.previousState.impactScore ?? 'N/A' : null;
    const impactAfter = node.impactScore ?? 'N/A';

    // 在最下方补充 Score Reason
    const reason = node.rankingReason ?? '(No reason provided)';

    // ③ 如果数字大小相同，则用以下横线icon放在两个数字之间
    const getArrowDiv = (beforeVal, afterVal) => {
      const b = parseFloat(beforeVal);
      const a = parseFloat(afterVal);
      if (isNaN(b) || isNaN(a)) return null;
      if (a > b) {
        // green arrow
        return (
          <svg
            width="17"
            height="17"
            viewBox="0 0 17 17"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            style={{ margin: '0 6px' }}
          >
            <g>
              <path
                d="M7.01221 4.32837L13.0122 4.32837L13.0122 10.3284"
                stroke="#37A219"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
              <path
                d="M4.52682 12.8137L13.0121 4.32845"
                stroke="#37A219"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </g>
          </svg>
        );
      } else if (a < b) {
        // red arrow
        return (
          <svg
            width="17"
            height="17"
            viewBox="0 0 17 17"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            style={{ margin: '0 6px' }}
          >
            <g>
              <path
                d="M12.9168 7.19702V13.197H6.91684"
                stroke="#A21919"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
              <path
                d="M4.43146 4.71167L12.9167 13.1969"
                stroke="#A21919"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </g>
          </svg>
        );
      } else {
        // 分数相同 -> 横线 icon
        return (
          <svg
            width="16"
            height="17"
            viewBox="0 0 16 17"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            style={{ margin: '0 6px' }}
          >
            <g id="Frame">
              <path
                id="Vector"
                d="M2.97964 8.17383L13.0204 8.17383"
                stroke="#A2A2A2"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </g>
          </svg>
        );
      }
    };

    const renderMetricCard = (label, color, beforeVal, afterVal) => {
      const showScore = hasBefore ? (
        <div style={{ display: 'flex', alignItems: 'center', fontSize: '1rem', color: '#374151' }}>
          <span>{beforeVal}</span>
          {getArrowDiv(beforeVal, afterVal)}
          <span>{afterVal}</span>
        </div>
      ) : (
        <div style={{ fontSize: '1rem', color: '#374151' }}>Score: {afterVal}</div>
      );

      return (
        <div
          key={label}
          style={{
            backgroundColor: '#F9FAFB',
            border: '1px solid #E5E7EB',
            borderRadius: '8px',
            padding: '16px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: '6px',
          }}
        >
          <div style={{ fontSize: '1rem', fontWeight: '600', color }}>{label}</div>
          {showScore}

          {/* Score reason */}
          <div
            style={{
              fontSize: '0.875rem',
              color: '#6b7280',
              textAlign: 'center',
              marginTop: '4px',
              lineHeight: '1.3',
            }}
          >
            {reason}
          </div>
        </div>
      );
    };

    return (
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: '1fr 1fr 1fr',
          gap: '16px',
          marginTop: '16px',
        }}
      >
        {renderMetricCard('Novelty', '#DC2626', noveltyBefore, noveltyAfter)}
        {renderMetricCard('Feasibility', '#D97706', feasibilityBefore, feasibilityAfter)}
        {renderMetricCard('Impact', '#2563EB', impactBefore, impactAfter)}
      </div>
    );
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif' }}>
      {/* 顶部改用 Tab 而不是 Switch 按钮 */}
      <TopNav />

      {/* Analysis Intent 输入：仅在 showTree && !isAnalysisSubmitted 时显示 */}
      {showTree && !isAnalysisSubmitted && (
        <div
          style={{
            backgroundColor: '#1f2937',
            display: 'flex',
            alignItems: 'center',
            padding: '10px 20px',
          }}
        >
          <form onSubmit={handleAnalysisIntentSubmit} style={{ display: 'flex', alignItems: 'center' }}>
            <input
              type="text"
              value={analysisIntent}
              onChange={(e) => setAnalysisIntent(e.target.value)}
              placeholder="Enter analysis intent"
              style={{
                padding: '6px 10px',
                borderRadius: '4px',
                border: '1px solid #d1d5db',
                marginRight: '8px',
              }}
            />
            <button
              type="submit"
              disabled={isGenerating || isEvaluating}
              style={{
                padding: '6px 12px',
                backgroundColor: '#4C84FF',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: isGenerating || isEvaluating ? 'not-allowed' : 'pointer',
                opacity: isGenerating || isEvaluating ? 0.7 : 1,
              }}
            >
              {isEvaluating ? 'Evaluating...' : isGenerating ? 'Generating...' : 'Submit'}
            </button>
            {error && (
              <div style={{ marginLeft: '16px', color: '#dc2626', fontSize: '0.875rem' }}>{error}</div>
            )}
          </form>
        </div>
      )}

      {/* Main Content */}
      <div
        style={{
          display: 'flex',
          padding: '20px',
          maxWidth: '1600px',
          margin: '0 auto',
          boxSizing: 'border-box',
        }}
      >
        {/* 左侧 - Exploration / Evaluation 图表 - 60% */}
        <div style={{ flexBasis: '60%', marginRight: '20px' }}>
          <svg ref={svgRef} />
        </div>

        {/* 右侧 - Dashboard - 40% */}
        <div style={{ flexBasis: '40%' }}>
          {(isGenerating || isEvaluating) && (
            <div style={{ textAlign: 'center', marginBottom: '10px', color: '#4C84FF' }}>
              {isGenerating ? 'Generating...' : 'Evaluating...'}
            </div>
          )}
          <Dashboard node={nodes.find((n) => n.id === (hoveredNode?.id || selectedNode?.id))} />
        </div>
      </div>
    </div>
  );
};

export default TreePlotVisualization;
