import React, { useRef, useEffect, useState } from 'react';

function StudyAbroadSimulator() {
  const canvasRef = useRef(null);

  // 存储所有“留学进程”（小球）：{ x, y, vx, vy, radius, lastCollisionTime }
  const ballsRef = useRef([]);

  // 发射点（模拟留学开始的地点）
  const spawnX = 50;
  const spawnY = 300;

  // “方向”和“动力”控制（发射参数）
  const [direction, setDirection] = useState(45); // 范围 0°～90°
  const [power, setPower] = useState(15);

  // 记录留学中经历的事件（右侧显示）
  const [studyHistory, setStudyHistory] = useState([]);
  // 碰到事件后，是否暂停“留学进程”
  const [isPaused, setIsPaused] = useState(false);

  // 当前弹出的事件信息（对话框中展示）
  const [currentEvent, setCurrentEvent] = useState(null);

  /**
   * 留学事件数组，包含更具体的事件
   */
  const studyEvents = [
    {
      id: 1,
      x: 100,
      y: 350,
      radius: 10,
      elasticity: 1.2,
      color: 'blue',
      title: '入学报到',
      description: '抵达美国后办理入学手续，激动开启全新生活！',
    },
    {
      id: 2,
      x: 200,
      y: 300,
      radius: 8,
      elasticity: 1.2,
      color: 'green',
      title: '校园导览',
      description: '参加迎新活动，熟悉校园环境与设施。',
    },
    {
      id: 3,
      x: 300,
      y: 250,
      radius: 12,
      elasticity: 1.2,
      color: 'purple',
      title: '选课注册',
      description: '根据专业规划选择课程，规划学术和生活节奏。',
    },
    {
      id: 4,
      x: 350,
      y: 200,
      radius: 10,
      elasticity: 1.2,
      color: 'orange',
      title: '加入实验室',
      description: '与导师见面，确定研究方向，开启科研之旅。',
    },
    {
      id: 5,
      x: 400,
      y: 150,
      radius: 10,
      elasticity: 1.2,
      color: 'cyan',
      title: '实习面试',
      description: '投递简历，参加面试，为未来实习积累经验。',
    },
    {
      id: 6,
      x: 250,
      y: 100,
      radius: 8,
      elasticity: 1.2,
      color: 'red',
      title: '论文提交',
      description: '完成论文写作与答辩，为学术成果画上句点。',
    },
    {
      id: 7,
      x: 150,
      y: 50,
      radius: 10,
      elasticity: 1.2,
      color: 'brown',
      title: '毕业典礼',
      description: '经过努力顺利毕业，开启人生新篇章。',
    },
    {
      id: 8,
      x: 450,
      y: 80,
      radius: 8,
      elasticity: 1.2,
      color: 'magenta',
      title: '全职工作',
      description: '收到全职工作offer，正式步入职场，迎接新挑战。',
    },
  ];

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    const gravity = 0.2;

    // 每帧更新“留学进程”
    const update = () => {
      if (isPaused) return;

      ballsRef.current.forEach((ball) => {
        // 模拟重力
        ball.vy += gravity;
        ball.x += ball.vx;
        ball.y += ball.vy;

        // 画布边界检测
        if (ball.y + ball.radius > canvas.height) {
          ball.y = canvas.height - ball.radius;
          ball.vy = -ball.vy * 0.8;
          ball.vx *= 0.9;
        }
        if (ball.y - ball.radius < 0) {
          ball.y = ball.radius;
          ball.vy = -ball.vy * 0.8;
        }
        if (ball.x - ball.radius < 0) {
          ball.x = ball.radius;
          ball.vx = -ball.vx * 0.8;
        }
        if (ball.x + ball.radius > canvas.width) {
          ball.x = canvas.width - ball.radius;
          ball.vx = -ball.vx * 0.8;
        }

        // 与留学事件的碰撞检测
        studyEvents.forEach((evt) => {
          const dx = ball.x - evt.x;
          const dy = ball.y - evt.y;
          const dist = Math.sqrt(dx * dx + dy * dy);

          if (dist < ball.radius + evt.radius) {
            const now = Date.now();
            // 防止频繁触发
            if (now - ball.lastCollisionTime > 500) {
              ball.lastCollisionTime = now;
              // 记录事件，并更新右侧历史记录
              const eventInfo = `${new Date().toLocaleTimeString()} - 你经历了【${evt.title}】`;
              setStudyHistory((prev) => [...prev, eventInfo]);
              // 显示事件详细信息
              setCurrentEvent({
                title: evt.title,
                description: evt.description,
              });
              // 暂停“留学进程”
              setIsPaused(true);
            }

            // 简单法线反射 + 纠正重叠
            const nx = dx / dist;
            const ny = dy / dist;
            const overlap = ball.radius + evt.radius - dist;
            ball.x += nx * overlap;
            ball.y += ny * overlap;

            const dot = ball.vx * nx + ball.vy * ny;
            ball.vx = ball.vx - 2 * dot * nx;
            ball.vy = ball.vy - 2 * dot * ny;
            ball.vx *= evt.elasticity;
            ball.vy *= evt.elasticity;
          }
        });
      });
    };

    // 每帧绘制
    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // 绘制留学事件
      studyEvents.forEach((evt) => {
        ctx.beginPath();
        ctx.arc(evt.x, evt.y, evt.radius, 0, Math.PI * 2);
        ctx.fillStyle = evt.color;
        ctx.fill();
        ctx.closePath();
      });

      // 绘制所有“留学进程”小球
      ballsRef.current.forEach((ball) => {
        ctx.beginPath();
        ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
        ctx.fillStyle = 'red';
        ctx.fill();
        ctx.closePath();
      });

      // 绘制方向箭头（相对于发射点）
      const arrowStartX = spawnX;
      const arrowStartY = spawnY + 10; // 往下移动一点
      const arrowLength = 50;
      const rad = (direction * Math.PI) / 180;
      const arrowEndX = arrowStartX + arrowLength * Math.cos(rad);
      const arrowEndY = arrowStartY - arrowLength * Math.sin(rad);

      ctx.beginPath();
      ctx.moveTo(arrowStartX, arrowStartY);
      ctx.lineTo(arrowEndX, arrowEndY);
      ctx.strokeStyle = 'black';
      ctx.lineWidth = 2;
      ctx.stroke();

      // 箭头头部
      const headLength = 10;
      const angle1 = rad + Math.PI / 8;
      const angle2 = rad - Math.PI / 8;
      ctx.beginPath();
      ctx.moveTo(arrowEndX, arrowEndY);
      ctx.lineTo(
        arrowEndX - headLength * Math.cos(angle1),
        arrowEndY + headLength * Math.sin(angle1)
      );
      ctx.lineTo(
        arrowEndX - headLength * Math.cos(angle2),
        arrowEndY + headLength * Math.sin(angle2)
      );
      ctx.lineTo(arrowEndX, arrowEndY);
      ctx.fillStyle = 'black';
      ctx.fill();
    };

    const loop = () => {
      update();
      draw();
      animationFrameId = requestAnimationFrame(loop);
    };
    animationFrameId = requestAnimationFrame(loop);

    return () => cancelAnimationFrame(animationFrameId);
  }, [studyEvents, isPaused, direction]);

  // 每次“开始留学”时重置之前的状态，然后发射一个新小球
  const launchBall = () => {
    // 清空之前的弹球和历史记录
    ballsRef.current = [];
    setStudyHistory([]);
    const rad = (direction * Math.PI) / 180;
    const newBall = {
      x: spawnX,
      y: spawnY,
      vx: power * Math.cos(rad),
      vy: -power * Math.sin(rad),
      radius: 5,
      lastCollisionTime: 0,
    };
    ballsRef.current.push(newBall);
    setIsPaused(false);
    setCurrentEvent(null);
  };

  // 左右移动，调整“方向”
  const adjustDirectionLeft = () => setDirection((prev) => Math.max(0, prev - 5));
  const adjustDirectionRight = () => setDirection((prev) => Math.min(90, prev + 5));

  // 对话框中点击“继续前行”
  const handleContinue = () => {
    setIsPaused(false);
    setCurrentEvent(null);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'row' }}>
      {/* 左侧画布及控制面板 */}
      <div>
        <div style={{ position: 'relative' }}>
          <canvas
            ref={canvasRef}
            width={500}
            height={400}
            style={{ border: '1px solid #000' }}
          />
          {/* 对话框：遇到事件时弹出 */}
          {isPaused && currentEvent && (
            <div
              style={{
                position: 'absolute',
                top: '40%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
                backgroundColor: 'rgba(255,255,255,0.9)',
                border: '1px solid #ccc',
                padding: '20px',
                zIndex: 10,
                textAlign: 'center',
              }}
            >
              <h3>你经历了：{currentEvent.title}</h3>
              <p>{currentEvent.description}</p>
              <button onClick={handleContinue}>继续前行</button>
            </div>
          )}
        </div>
        {/* 控制面板 */}
        <div style={{ marginTop: 20, textAlign: 'center' }}>
          <div style={{ marginBottom: 10 }}>
            <button onClick={adjustDirectionLeft}>左移</button>
            <span style={{ margin: '0 10px' }}>方向: {direction}°</span>
            <button onClick={adjustDirectionRight}>右移</button>
          </div>
          <div style={{ marginBottom: 10 }}>
            <span>动力: {power}</span>
            <input
              type="range"
              min="5"
              max="30"
              value={power}
              onChange={(e) => setPower(Number(e.target.value))}
              style={{ marginLeft: 10 }}
            />
          </div>
          <button onClick={launchBall} style={{ fontSize: '16px', padding: '10px 20px' }}>
            ✈️ 开始留学
          </button>
        </div>
      </div>

      {/* 右侧：留学经历（事件）历史 */}
      <div
        style={{
          marginLeft: 20,
          padding: 10,
          width: 250,
          minHeight: 400,
          border: '1px solid #aaa',
          backgroundColor: '#f9f9f9',
          overflowY: 'auto',
        }}
      >
        <h4>经历</h4>
        {studyHistory.length > 0 ? (
          <ul style={{ paddingLeft: 20 }}>
            {studyHistory.map((msg, index) => (
              <li key={index}>{msg}</li>
            ))}
          </ul>
        ) : (
          <div>期待每一段旅程</div>
        )}
      </div>
    </div>
  );
}

export default StudyAbroadSimulator;
