import React, { useState } from 'react';

function EventItem({ event }) {
  const [hover, setHover] = useState(false);

  // 鼠标悬停时显示的浮层样式
  const tooltipStyle = {
    position: 'absolute',
    top: '-120px',
    left: '50%',
    transform: 'translateX(-50%)',
    padding: '10px 14px',
    backgroundColor: '#fff',
    border: '1px solid #ccc',
    borderRadius: '8px',
    whiteSpace: 'nowrap',
    zIndex: 10,
    boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
    minWidth: '220px',
  };

  return (
    <div
      style={{
        position: 'relative',
        marginBottom: '60px', // 控制事件之间的垂直间距
        cursor: 'pointer',
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      {/* 时间线左侧的蓝色圆点 */}
      <div
        style={{
          position: 'absolute',
          left: '-8px', // 让圆点中心与时间线重合
          top: '0',
          width: '16px',
          height: '16px',
          borderRadius: '50%',
          backgroundColor: '#007BFF',
        }}
      />

      {/* 日期与标题部分（放在右侧） */}
      <div style={{ marginLeft: '24px' }}>
        {/* 日期外框 */}
        <div
          style={{
            border: '2px solid #ddd',
            borderRadius: '20px',
            padding: '10px 16px',
            background: '#fff',
            fontSize: '15px',
            minWidth: '130px',
            textAlign: 'center',
            marginBottom: '6px',
          }}
        >
          {event.date}
        </div>
        {/* 事件标题（始终可见） */}
        <div style={{ fontSize: '14px', color: '#333', textAlign: 'center' }}>
          <strong>{event.title}</strong>
        </div>
      </div>

      {/* 悬浮时显示事件的详细信息 */}
      {hover && (
        <div style={tooltipStyle}>
          <strong style={{ display: 'block', marginBottom: '6px' }}>
            {event.title}
          </strong>
          <div style={{ fontSize: '13px', color: '#333' }}>{event.detail}</div>
        </div>
      )}
    </div>
  );
}

function Timeline({ wave }) {
  // 时间线高度，可根据事件数量或UI需求进行调节
  const timelineHeight = 600;
  const totalEvents = wave.events.length;

  return (
    <div style={{ flex: '1', margin: '0 20px' }}>
      {/* 浪潮标题与时期 */}
      <h2 style={{ fontSize: '16px', marginBottom: '5px', textAlign: 'center' }}>
        {wave.title}
      </h2>
      <div style={{ fontSize: '14px', color: '#666', marginBottom: '20px', textAlign: 'center' }}>
        {wave.period}
      </div>

      {/* 垂直时间线 */}
      <div
        style={{
          position: 'relative',
          width: '2px',
          backgroundColor: '#bbb',
          height: timelineHeight + 'px',
          marginLeft: '0',
        }}
      >
        {/* 事件列表 */}
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {wave.events.map((event, index) => {
            // 计算事件节点在时间线上的垂直位置
            const pos =
              totalEvents === 1
                ? timelineHeight / 2
                : (index / (totalEvents - 1)) * timelineHeight;

            return (
              <div
                key={index}
                style={{
                  position: 'absolute',
                  top: pos + 'px',
                  width: '220px', // 控制每个事件在水平方向的可用空间
                }}
              >
                <EventItem event={event} />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

