import React, { useState } from 'react';

function WikiSearchDemo() {
  const [responseData, setResponseData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const callApi = async () => {
    setLoading(true);
    setError(null);

    try {
      // Use the proxy endpoint instead of the full external URL
      const response = await fetch('/api/wikipedia', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: ["What is GPT-4?", "What is LLaMA-3?"],
          rerank: true,
          num_blocks_to_rerank: 10,
          num_blocks: 3,
        }),
      });

      if (!response.ok) {
        throw new Error(`Error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      setResponseData(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };


  return (
    <div style={{ padding: '2rem' }}>
      <h1>Wikipedia Search API Demo</h1>
      <button onClick={callApi} disabled={loading}>
        {loading ? 'Loading...' : 'Call API'}
      </button>
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}
      {responseData && (
        <div>
          <h2>Response:</h2>
          <pre style={{ background: '#f4f4f4', padding: '1rem' }}>
            {JSON.stringify(responseData, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

export default WikiSearchDemo;
