}

/**
 * 单条浪潮的时间线组件
 * 添加 eventId 用于区分各个事件，同时传入 onEventClick 处理手动连接事件
 */
function Timeline({ wave, waveIndex, registerPosition, onEventClick, selectedEvents }) {
  // 延长时间线20%：之前为480，现在变为480*1.2 = 576
  const timelineHeight = 576;
  const timestamps = wave.events.map(e => new Date(e.date).getTime());
  const earliestTime = Math.min(...timestamps);
  const latestTime = Math.max(...timestamps);
  const totalTimeSpan = latestTime - earliestTime;
  const safeTimeSpan = totalTimeSpan === 0 ? 1 : totalTimeSpan;

  return (
    <div style={{ flex: '1', margin: '0 20px', position: 'relative' }}>
      <h2 style={{ fontSize: '16px', marginBottom: '5px', textAlign: 'center' }}>
        {wave.title}
      </h2>
      <div style={{ fontSize: '14px', color: '#666', marginBottom: '20px', textAlign: 'center' }}>
        {wave.period}
      </div>

      <div
        style={{
          position: 'relative',
          width: '2px',
          backgroundColor: '#bbb',
          height: timelineHeight + 'px',
          marginLeft: '0',
          borderRadius: '1px',
        }}
      >
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {wave.events.map((event, index) => {
            const currentTime = new Date(event.date).getTime();
            const offset = currentTime - earliestTime;
            const ratio = offset / safeTimeSpan;
            const pos = ratio * timelineHeight;
            const eventId = `${waveIndex}-${index}`;
            return (
              <div
                key={index}
                style={{
                  position: 'absolute',
                  top: pos + 'px',
                  width: '220px',
                }}
              >
                <EventItem
                  event={event}
                  eventId={eventId}
                  registerPosition={registerPosition}
                  onEventClick={onEventClick}
                  isSelected={selectedEvents.includes(eventId)}
                />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

/**
 * 用于绘制连接线的组件，使用 SVG 实现
 */
function ConnectionsLayer({ connections, eventPositions }) {
  return (
    <svg
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        pointerEvents: 'none',
        width: '100%',
        height: '100%',
      }}
    >
      {connections.map((conn, index) => {
        const fromPos = eventPositions[conn.from];
        const toPos = eventPositions[conn.to];
        if (!fromPos || !toPos) return null;
        const midX = (fromPos.x + toPos.x) / 2;
        const midY = (fromPos.y + toPos.y) / 2;
        return (
          <g key={index}>
            <line
              x1={fromPos.x}
              y1={fromPos.y}
              x2={toPos.x}
              y2={toPos.y}
              stroke="#f00"
              strokeWidth="2"
            />
            <text x={midX} y={midY - 5} fill="#f00" fontSize="12" textAnchor="middle">
              {conn.reason}
            </text>
          </g>
        );
      })}
    </svg>
  );
}

