import React, { useState, useEffect } from 'react';

/**
 * Groups timeline items by "year marker"—an item with no `company` and no `content`.
 * Returns an array of objects: { marker: string, items: [] }
 */
function groupTimeline(timeline) {
  const groups = [];
  let currentGroup = null;

  timeline.forEach((item) => {
    const isYearMarker = !item.company && !item.content;
    if (isYearMarker) {
      // 完成当前分组
      if (currentGroup) {
        groups.push(currentGroup);
      }
      // 开启一个新的分组
      currentGroup = { marker: item.date, items: [] };
    } else {
      // 普通条目
      if (!currentGroup) {
        // 如果还没有开启分组，就创建一个默认分组
        currentGroup = { marker: '', items: [] };
      }
      currentGroup.items.push(item);
    }
  });

  // 推入最后一个分组
  if (currentGroup) {
    groups.push(currentGroup);
  }

  return groups;
}

function Showcase() {
  // LLM Timeline data
  const llmTimeline = [
    { date: '2022, Pre-training / Scaling Law / Chatbot' },
    { date: 'Mar 15th, 2022', company: 'OpenAI', content: 'OpenAI released GPT-3.5 text-davinci-002 API' },
    { date: 'Nov 30th, 2022', company: 'OpenAI', content: 'OpenAI released ChatGPT with GPT-3.5' },
    { date: '2023, Post-training / Agent' },
    { date: 'Jan 23rd, 2023', company: 'OpenAI', content: 'Microsoft announced a new multiyear investment in OpenAI, amounting to $10 billion. This investment valued OpenAI at approximately $29 billion' },
    { date: 'Mar 14th, 2023', company: 'OpenAI', content: 'OpenAI released GPT-4' },
    { date: '2024, Reasoning' },
    { date: 'Sep 20th, 2024', company: 'OpenAI', content: 'OpenAI released o1-preview, 1st reasoning model' },
    { date: 'Oct 2nd, 2024', company: 'OpenAI', content: 'OpenAI announced the completion of a $6.6 billion funding round, elevating valuation to $157 billion' },
    { date: '2025, Open source' },
    { date: 'Jan 20th, 2025', company: 'Deepseek', content: 'Deepseek released Deepseek-R1, 1st open-weights reasoning model' }
  ];

  // Robot Timeline data
  const robotTimeline = [
    { date: '2025, Reasoning / Mutli-robot Framework / Mass Production' },
    {
      date: 'Feb 20th, 2025',
      company: 'Figure',
      content: 'Figure released reasoning robot with collaboration; valuation jumped from $2.6B (Cursor $2.5B) to $39.5B (Anthropic $60B)',
      link: 'https://twitter.com/Figure_robot/status/1892577871366939087'
    },
  ];

  // 分组后的时间轴
  const llmGroups = groupTimeline(llmTimeline);
  const robotGroups = groupTimeline(robotTimeline);

  // 折叠/展开状态
  const [llmOpenStates, setLlmOpenStates] = useState(llmGroups.map(() => true));
  const [robotOpenStates, setRobotOpenStates] = useState(robotGroups.map(() => true));

  // 切换 LLM 分组
  function toggleLlmGroup(index) {
    setLlmOpenStates((prev) => {
      const copy = [...prev];
      copy[index] = !copy[index];
      return copy;
    });
  }

  // 切换 Robot 分组
  function toggleRobotGroup(index) {
    setRobotOpenStates((prev) => {
      const copy = [...prev];
      copy[index] = !copy[index];
      return copy;
    });
  }

  // 动态加载 Twitter script，使嵌入推文生效
  useEffect(() => {
    const script = document.createElement('script');
    script.async = true;
    script.src = 'https://platform.twitter.com/widgets.js';
    script.charset = 'utf-8';
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  // 根据 company 获取对应的图标
  function getIconByCompany(company) {
    switch (company) {
      case 'OpenAI':
        return '/images/OpenAI.png';
      case 'Deepseek':
        return '/images/Deepseek.png';
      case 'Figure':
        return '/images/Figure.png';
      default:
        return '/images/OpenAI.png'; // fallback
    }
  }

  return (
    <div style={{ position: 'relative', padding: '0', margin: '0' }}>
      <style>{`
        * {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
        }
        body {
          font-family: Arial, sans-serif;
          background: #fff;
        }

        /* ================= Introduction Section ================= */
        .section-intro {
          background-color: #E6F2FF; /* 第一块浅蓝色背景 */
          padding: 2rem 1rem;
          text-align: center;
        }
        .section-intro h3 {
          font-size: 2rem;
          margin-bottom: 0.5rem;
          color: #333;
        }
        .section-intro h4 {
          font-size: 1.3rem;
          font-weight: 400;
          margin-bottom: 1.5rem;
          color: #666;
        }

        .author-info {
          margin-bottom: 1rem;
        }
        /* 链接用黑色，鼠标悬停变灰并加下划线 */
        .author-link {
          color: #000;
          text-decoration: none;
          font-weight: bold;
          transition: color 0.2s;
        }
        .author-link:hover {
          color: #555;
          text-decoration: underline;
        }

        .intro-text {
          max-width: 980px;
          margin: 0 auto;
          text-align: left;
          line-height: 1.6;
          font-size: 1rem;
          color: #333;
          display: flex;
          align-items: flex-start;
          gap: 1em;
        }
        .intro-text img {
          width: 300px;
          height: 300px;
          object-fit: contain;
          padding-right: 2em;
        }
        .intro-text .text-block {
          padding-top: 1.5em;
        }

        /* ================= Timeline Section ================= */
        .timeline-section-container {
          background-color: #f8f9f9; /* 第二块浅黄色背景 */
          padding: 2rem 1rem;
        }
        .timeline-section-title {
          text-align: center;
          margin-bottom: 1.5rem;
          font-size: 2rem;
          color: #333;
        }
        .timeline-section {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          gap: 2rem;
          max-width: 1100px;
          margin: 0 auto;
        }

        .timeline-wrapper {
          flex: 1;
          position: relative;
          padding: 0 20px;
        }
        .timeline-wrapper h2 {
          text-align: center;
          margin-bottom: 1.5rem;
          font-size: 1.2rem;
          color: #333;
        }

        .timeline-wrapper::before {
          content: '';
          position: absolute;
          left: 30px;
          top: 0;
          width: 2px;
          height: 100%;
          background-color: #ccc;
        }

        .timeline-item {
          position: relative;
          margin: 0.8rem 0;
          padding-left: 70px;
          opacity: 0; /* for animation start */
          animation-duration: 0.8s;
          animation-fill-mode: forwards;
        }
        .timeline-icon {
          position: absolute;
          left: 10px;
          top: 10px;
          width: 40px;
          height: 40px;
          border-radius: 50%;
          overflow: hidden;
          display: flex;
          align-items: center;
          justify-content: center;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
          background-color: #fff;
        }
        .timeline-icon img {
          width: 100%;
          height: auto;
          object-fit: contain;
        }

        .timeline-details {
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
        }
        .timeline-date {
          font-weight: bold;
          color: #555;
        }
        .timeline-content {
          background-color: #f7f7f7;
          padding: 10px 15px;
          border-radius: 8px;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
          border-left: 3px solid #ff7f50;
        }

        .timeline-item::before {
          content: '';
          position: absolute;
          left: 27px;
          top: 20px;
          width: 10px;
          height: 10px;
          background-color: #ff7f50;
          border-radius: 50%;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
        }

        @keyframes fadeInLeft {
          from {
            transform: translateX(-30px);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }
        @keyframes fadeInRight {
          from {
            transform: translateX(30px);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }
        .fade-in-left {
          animation-name: fadeInLeft;
        }
        .fade-in-right {
          animation-name: fadeInRight;
        }

        .year-marker {
          padding-left: 40px;
          margin: 0.5rem 0;
          font-size: 1rem;
          font-weight: bold;
          color: #444;
          text-align: left;
          background: #f0f0f0;
          display: inline-block;
          border-radius: 4px;
          padding: 4px 8px;
          cursor: pointer; /* clickable for fold/unfold */
        }
        .year-marker::before {
          display: none; /* 去掉 dot */
        }
        .year-marker .timeline-icon {
          display: none; /* 不显示图标 */
        }

        /* ================= Event Section ================= */
        .event-section {
          background-color: #e5e7e9; /* 第三块浅青色背景 */
          padding: 3rem 1rem;
        }
        .event-section .section-title {
          text-align: center;
          font-size: 2rem;
          margin-bottom: 2rem;
          color: #333;
        }

        .event-container {
          max-width: 1100px;
          margin: 0 auto;
          display: flex;
          flex-direction: row;
          align-items: center;
          justify-content: center;
          gap: 2rem;
          /* 卡片化效果 */
          background: #ffffff;
          border-radius: 12px;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
          padding: 2rem;
        }
        .event-image {
          width: 40%;
          height: auto;
          object-fit: cover;
          border-radius: 8px;
        }
        .event-info {
          width: 60%;
          line-height: 1.8;
          font-size: 1.1rem;
          color: #333;
          display: flex;
          flex-direction: column;
          justify-content: center;
        }
        .event-info p {
          margin-bottom: 0.6rem;
        }
      `}</style>

      {/* ============== Introduction Section (去掉“Introduction”标题) ============== */}
      <div className="section-intro">
        <h3>Robot in 2025 as GenAI in 2022</h3>
        <h4>Riding the robot wave with lessons learned from GenAI boom</h4>

        <div className="author-info">
          <a
            className="author-link"
            href="https://jason-ding.com"
            target="_blank"
            rel="noopener noreferrer"
          >
            Zijian "Jason" Ding, University of Maryland College Park
          </a>
        </div>

        <div className="intro-text">
          {/* 左侧图片 */}
          <img src="/images/Robot.png" alt="Robot" />
          {/* 文字内容 */}
          <div className="text-block">
            <p style={{ paddingBottom: '1em' }}>
              Don't regret missing the GenAI boom—we are about to embrace an even bigger robotics wave soon.
            </p>
            <p style={{ paddingBottom: '1em' }}>
              Over the past year, we witnessed many AI advancements: VLM, open-source reasoning models,
              and multi-agent frameworks. All of these point towards robotics as a key application,
              as reflected in Figure's valuation surge from $2.6B (Feb 2024) to $39.5B (Feb 2025).
              With great waves come loud tides—too many exciting opportunities in the robot wave
              makes it difficult to focus on the right things.
            </p>
            <p>
              Yet, when we look back at the history of GenAI, things becomes much clearer.
              Let’s discuss together the trial and error in GenAI back in 2022 to better ride the robot wave in 2025.
            </p>
          </div>
        </div>
      </div>

      {/* ============== Timeline Comparison Section ============== */}
      <div className="timeline-section-container">
        <h2 className="timeline-section-title">Timeline Comparison</h2>
        <div className="timeline-section">
          {/* LLM Timeline */}
          <div className="timeline-wrapper">
            <h2>GenAI since 2022</h2>
            {llmGroups.map((group, groupIndex) => {
              const isMarkerPresent = !!group.marker;
              const isOpen = llmOpenStates[groupIndex];

              return (
                <div key={groupIndex}>
                  {/* Year marker (if any) */}
                  {isMarkerPresent && (
                    <div
                      className="timeline-item year-marker fade-in-left"
                      style={{ animationDelay: `${groupIndex * 0.2}s` }}
                      onClick={() => toggleLlmGroup(groupIndex)}
                    >
                      {group.marker}
                    </div>
                  )}

                  {/* Items in this group (render only if open) */}
                  {isOpen && group.items.map((item, itemIndex) => (
                    <div
                      className="timeline-item fade-in-left"
                      style={{ animationDelay: `${(groupIndex + itemIndex) * 0.2}s` }}
                      key={itemIndex}
                    >
                      <div className="timeline-icon">
                        <img src={getIconByCompany(item.company)} alt={item.company} />
                      </div>
                      <div className="timeline-details">
                        <div className="timeline-date">{item.date}</div>
                        <div className="timeline-content">
                          {item.content}
                          {item.link && (
                            <div style={{ marginTop: '10px' }}>
                              <blockquote className="twitter-tweet">
                                <a href={item.link}></a>
                              </blockquote>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              );
            })}
          </div>

          {/* Robot Timeline */}
          <div className="timeline-wrapper">
            <h2>Robot since 2025</h2>
            {robotGroups.map((group, groupIndex) => {
              const isMarkerPresent = !!group.marker;
              const isOpen = robotOpenStates[groupIndex];

              return (
                <div key={groupIndex}>
                  {/* Year marker (if any) */}
                  {isMarkerPresent && (
                    <div
                      className="timeline-item year-marker fade-in-right"
                      style={{ animationDelay: `${groupIndex * 0.2}s` }}
                      onClick={() => toggleRobotGroup(groupIndex)}
                    >
                      {group.marker}
                    </div>
                  )}

                  {/* Items in this group (render only if open) */}
                  {isOpen && group.items.map((item, itemIndex) => (
                    <div
                      className="timeline-item fade-in-right"
                      style={{ animationDelay: `${(groupIndex + itemIndex) * 0.2}s` }}
                      key={itemIndex}
                    >
                      <div className="timeline-icon">
                        <img src={getIconByCompany(item.company)} alt={item.company} />
                      </div>
                      <div className="timeline-details">
                        <div className="timeline-date">{item.date}</div>
                        <div className="timeline-content">
                          {item.content}
                          {item.link && (
                            <div style={{ marginTop: '10px' }}>
                              <blockquote className="twitter-tweet">
                                <a href={item.link}></a>
                              </blockquote>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* ============== Event Section ============== */}
      <div className="event-section">
        <h2 className="section-title">Event</h2>
        <div className="event-container">
          {/* 左侧图片：Atl.jpg */}
          <img
            src="/images/Atl.jpg"
            alt="Atlanta"
            className="event-image"
          />
          {/* 右侧文本信息 */}
          <div className="event-info">
            <p style={{ fontWeight: 'bold', fontSize: '1.2rem', marginBottom: '1rem' }}>
              May 18, 2025
            </p>
            <p>Georgia World Congress Center</p>
            <p>Atlanta, GA</p>
            <p>(co-located w/ ICRA’25)</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Showcase;
