import React, { useState } from 'react';

// 单条时间线组件
function Timeline({ data }) {
  // 记录当前展开的事件索引
  const [expandedIndex, setExpandedIndex] = useState(null);

  const toggle = (index) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div style={styles.timelineContainer}>
      {data.map((event, index) => (
        <div key={index} style={styles.eventContainer}>
          <div 
            style={styles.eventMarker} 
            onClick={() => toggle(index)}
          >
            <div style={styles.dot}></div>
            <div style={styles.date}>{event.date}</div>
          </div>
          <div style={styles.eventContent}>
            <h3 
              style={styles.eventTitle} 
              onClick={() => toggle(index)}
            >
              {event.title}
            </h3>
            {expandedIndex === index && (
              <p style={styles.eventDetail}>{event.detail}</p>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}

function Showcase() {
  // 每个浪潮包含详细到具体日期的事件列表
  const waves = [
    {
      wave: '个人电脑浪潮 (1979-1982)',
      events: [
        { date: '1979-05-15', title: '早期家用电脑兴起', detail: '早期家用电脑开始在实验室和部分家庭中试水，市场初见端倪。' },
        { date: '1980-07-10', title: 'IBM 投入个人电脑研发', detail: 'IBM 组建专门团队，开始布局个人电脑领域。' },
        { date: '1981-08-12', title: 'IBM PC 正式推出', detail: 'IBM PC 的发布标志着个人电脑进入商业化时代。' },
        { date: '1982-11-05', title: '克隆机与 MS-DOS 生态繁荣', detail: '克隆机市场崛起，MS-DOS 操作系统生态逐步建立。' },
      ],
    },
    {
      wave: '互联网浪潮 (1994-1997)',
      events: [
        { date: '1994-04-03', title: '网景浏览器发布', detail: 'Netscape 浏览器的发布大大降低了上网门槛。' },
        { date: '1995-09-14', title: '雅虎成立', detail: '雅虎的成立为互联网内容聚合和检索奠定基础。' },
        { date: '1996-06-30', title: '宽带普及初现', detail: '随着基础设施的升级，越来越多用户开始接入互联网。' },
        { date: '1997-11-21', title: '门户网站爆发', detail: '门户网站如新浪、搜狐等迅速涌现，互联网进入爆发期。' },
      ],
    },
    {
      wave: '智能手机浪潮 (2007-2010)',
      events: [
        { date: '2007-01-09', title: 'iPhone 发布', detail: '苹果发布第一代 iPhone，重新定义了手机的功能和体验。' },
        { date: '2008-09-23', title: 'Android 系统推出', detail: 'Android 系统面世，开启了多厂商竞争的智能手机时代。' },
        { date: '2009-05-15', title: '智能手机用户激增', detail: '硬件进步和 App 生态推动智能手机用户数量迅速攀升。' },
        { date: '2010-11-18', title: '移动互联网生态爆发', detail: '应用商店、社交平台等服务层出不穷，移动互联网全面爆发。' },
      ],
    },
    {
      wave: '生成式 AI 浪潮 (2019-2022)',
      events: [
        { date: '2019-06-20', title: 'Transformer 模型成熟', detail: 'Transformer 架构在多项任务上展现了强大能力。' },
        { date: '2020-07-15', title: 'GPT-3 发布', detail: 'GPT-3 的发布震撼了整个 AI 领域，推动大规模预训练模型发展。' },
        { date: '2021-08-01', title: '多模态 AI 研究活跃', detail: '各大企业和研究机构开始布局文本、图像、语音等多模态模型。' },
        { date: '2022-11-30', title: 'ChatGPT爆红', detail: 'ChatGPT 的广泛应用引发了公众对生成式 AI 的热议。' },
      ],
    },
    {
      wave: '电动车浪潮 (2015-2018)',
      events: [
        { date: '2015-03-01', title: '特斯拉 Model S 推出', detail: 'Model S 的推出标志着高性能电动车时代的来临。' },
        { date: '2016-05-20', title: '传统车企布局电动车', detail: '越来越多传统车企开始大规模投入电动车研发。' },
        { date: '2017-07-10', title: '电池成本显著下降', detail: '锂电池技术进步显著，成本下降推动了市场普及。' },
        { date: '2018-09-15', title: '充电基础设施完善', detail: '充电桩和快充网络建设加速，解决了里程焦虑问题。' },
      ],
    },
    // 可按需要继续添加其他浪潮
  ];

  return (
    <div style={{ padding: '20px' }}>
      <h1>科技浪潮「早期阶段」互动时间线</h1>
      {waves.map((waveData, index) => (
        <div key={index} style={{ marginBottom: '50px' }}>
          <h2>{waveData.wave}</h2>
          <Timeline data={waveData.events} />
        </div>
      ))}
    </div>
  );
}

const styles = {
  timelineContainer: {
    borderLeft: '2px solid #ddd',
    marginLeft: '20px',
    position: 'relative',
    paddingLeft: '20px'
  },
  eventContainer: {
    position: 'relative',
    marginBottom: '20px'
  },
  eventMarker: {
    position: 'absolute',
    left: '-30px',
    top: '0',
    cursor: 'pointer',
    textAlign: 'center'
  },
  dot: {
    width: '12px',
    height: '12px',
    borderRadius: '50%',
    background: '#007BFF',
    marginBottom: '5px'
  },
  date: {
    fontSize: '12px',
    color: '#555'
  },
  eventContent: {
    padding: '5px 10px',
    borderRadius: '4px',
    background: '#f9f9f9'
  },
  eventTitle: {
    margin: '0 0 5px',
    cursor: 'pointer'
  },
  eventDetail: {
    margin: 0,
    fontSize: '14px',
    color: '#333'
  }
};

export default Showcase;
