import React, { useRef, useEffect, useState } from 'react';

function Pinball() {
  const canvasRef = useRef(null);

  // 存储所有弹珠，格式：{ x, y, vx, vy, radius, lastCollisionTime }
  const ballsRef = useRef([]);

  // 发射点（固定在左下角），可根据需要调整
  const spawnX = 50;
  const spawnY = 300;

  // 方向（原角度），范围 0°～90°
  const [direction, setDirection] = useState(45);
  // 发射力度
  const [power, setPower] = useState(15);
  // 碰撞事件历史记录（显示在右侧）
  const [collisionHistory, setCollisionHistory] = useState([]);
  // 全局暂停标记（碰撞后暂停整个物理更新）
  const [isPaused, setIsPaused] = useState(false);

  // 定义更多的障碍物
  const bumpers = [
    { id: 1, x: 200, y: 150, radius: 10, elasticity: 1.2, color: 'blue' },
    { id: 2, x: 350, y: 250, radius: 8, elasticity: 1.2, color: 'green' },
    { id: 3, x: 150, y: 100, radius: 12, elasticity: 1.2, color: 'purple' },
    { id: 4, x: 300, y: 80, radius: 10, elasticity: 1.2, color: 'orange' },
    { id: 5, x: 100, y: 200, radius: 8, elasticity: 1.2, color: 'pink' },
    { id: 6, x: 400, y: 150, radius: 10, elasticity: 1.2, color: 'cyan' },
    { id: 7, x: 250, y: 300, radius: 10, elasticity: 1.2, color: 'red' },
    { id: 8, x: 450, y: 100, radius: 8, elasticity: 1.2, color: 'yellow' },
    { id: 9, x: 50,  y: 50,  radius: 10, elasticity: 1.2, color: 'brown' },
  ];

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    const gravity = 0.2;

    // 每帧更新
    const update = () => {
      if (isPaused) return; // 暂停时不更新物理

      // 更新所有弹珠
      ballsRef.current.forEach((ball) => {
        // 简单重力
        ball.vy += gravity;
        ball.x += ball.vx;
        ball.y += ball.vy;

        // 边界检测：上下左右
        if (ball.y + ball.radius > canvas.height) {
          ball.y = canvas.height - ball.radius;
          ball.vy = -ball.vy * 0.8;
          ball.vx *= 0.9;
        }
        if (ball.y - ball.radius < 0) {
          ball.y = ball.radius;
          ball.vy = -ball.vy * 0.8;
        }
        if (ball.x - ball.radius < 0) {
          ball.x = ball.radius;
          ball.vx = -ball.vx * 0.8;
        }
        if (ball.x + ball.radius > canvas.width) {
          ball.x = canvas.width - ball.radius;
          ball.vx = -ball.vx * 0.8;
        }

        // 与障碍物碰撞检测
        bumpers.forEach((bumper) => {
          const dx = ball.x - bumper.x;
          const dy = ball.y - bumper.y;
          const dist = Math.sqrt(dx * dx + dy * dy);

          if (dist < ball.radius + bumper.radius) {
            const now = Date.now();
            // 防止短时间重复触发碰撞事件
            if (now - ball.lastCollisionTime > 500) {
              ball.lastCollisionTime = now;
              // 记录碰撞信息
              setCollisionHistory((prev) => [
                ...prev,
                `${new Date().toLocaleTimeString()} 遇到事件 ${bumper.id}`,
              ]);
              // 碰撞时暂停整个模拟
              setIsPaused(true);
            }

            // 计算碰撞法线，并解决重叠
            const nx = dx / dist;
            const ny = dy / dist;
            const overlap = ball.radius + bumper.radius - dist;
            ball.x += nx * overlap;
            ball.y += ny * overlap;

            // 简单法线反射
            const dot = ball.vx * nx + ball.vy * ny;
            ball.vx = ball.vx - 2 * dot * nx;
            ball.vy = ball.vy - 2 * dot * ny;
            ball.vx *= bumper.elasticity;
            ball.vy *= bumper.elasticity;
          }
        });
      });
    };

    // 每帧绘制
    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // 绘制障碍物
      bumpers.forEach((bumper) => {
        ctx.beginPath();
        ctx.arc(bumper.x, bumper.y, bumper.radius, 0, Math.PI * 2);
        ctx.fillStyle = bumper.color;
        ctx.fill();
        ctx.closePath();
      });

      // 绘制所有弹珠
      ballsRef.current.forEach((ball) => {
        ctx.beginPath();
        ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
        ctx.fillStyle = 'red';
        ctx.fill();
        ctx.closePath();
      });

      // 绘制方向箭头（相对于发射点），稍微向下偏移
      const arrowStartX = spawnX;
      const arrowStartY = spawnY + 10; // 往下移动10px
      const arrowLength = 50;          // 箭头长度
      const rad = (direction * Math.PI) / 180;
      const arrowEndX = arrowStartX + arrowLength * Math.cos(rad);
      const arrowEndY = arrowStartY - arrowLength * Math.sin(rad);

      ctx.beginPath();
      ctx.moveTo(arrowStartX, arrowStartY);
      ctx.lineTo(arrowEndX, arrowEndY);
      ctx.strokeStyle = 'black';
      ctx.lineWidth = 2;
      ctx.stroke();

      // 箭头头部
      const headLength = 10;
      const angle1 = rad + Math.PI / 8;
      const angle2 = rad - Math.PI / 8;
      ctx.beginPath();
      ctx.moveTo(arrowEndX, arrowEndY);
      ctx.lineTo(
        arrowEndX - headLength * Math.cos(angle1),
        arrowEndY + headLength * Math.sin(angle1)
      );
      ctx.lineTo(
        arrowEndX - headLength * Math.cos(angle2),
        arrowEndY + headLength * Math.sin(angle2)
      );
      ctx.lineTo(arrowEndX, arrowEndY);
      ctx.fillStyle = 'black';
      ctx.fill();
    };

    const loop = () => {
      update();
      draw();
      animationFrameId = requestAnimationFrame(loop);
    };
    animationFrameId = requestAnimationFrame(loop);

    return () => cancelAnimationFrame(animationFrameId);
  }, [bumpers, isPaused, direction]);

  // 发射新弹珠（半径更小）
  const launchBall = () => {
    const rad = (direction * Math.PI) / 180;
    const newBall = {
      x: spawnX,
      y: spawnY,
      vx: power * Math.cos(rad),
      vy: -power * Math.sin(rad),
      radius: 5,
      lastCollisionTime: 0,
    };
    ballsRef.current.push(newBall);
    // 发射时如果处于暂停，恢复运动
    setIsPaused(false);
  };

  // 左移/右移，调整 direction
  const adjustDirectionLeft = () => setDirection((prev) => Math.max(0, prev - 5));
  const adjustDirectionRight = () => setDirection((prev) => Math.min(90, prev + 5));

  return (
    <div style={{ display: 'flex', flexDirection: 'row' }}>
      {/* 左侧画布及控制面板 */}
      <div>
        <div style={{ position: 'relative' }}>
          <canvas
            ref={canvasRef}
            width={500}
            height={400}
            style={{ border: '1px solid #000' }}
          />
          {/* 碰撞后弹出模态层 */}
          {isPaused && (
            <div
              style={{
                position: 'absolute',
                top: '40%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
                backgroundColor: 'rgba(255,255,255,0.9)',
                border: '1px solid #ccc',
                padding: '20px',
                zIndex: 10,
                textAlign: 'center',
              }}
            >
              <p>遇到事件</p>
              <button onClick={() => setIsPaused(false)}>确定</button>
            </div>
          )}
        </div>
        {/* 控制面板放在画布下方 */}
        <div style={{ marginTop: 20, textAlign: 'center' }}>
          <div style={{ marginBottom: 10 }}>
            <button onClick={adjustDirectionRight}>左移</button>
            <span style={{ margin: '0 10px' }}>方向</span>
            <button onClick={adjustDirectionLeft}>右移</button>
          </div>
          <div style={{ marginBottom: 10 }}>
            <span>力度: {power}</span>
            <input
              type="range"
              min="5"
              max="30"
              value={power}
              onChange={(e) => setPower(Number(e.target.value))}
              style={{ marginLeft: 10 }}
            />
          </div>
          <button onClick={launchBall} style={{ fontSize: '16px', padding: '10px 20px' }}>
            🚀 发射弹珠
          </button>
        </div>
      </div>

      {/* 右侧：碰撞事件历史 */}
      <div
        style={{
          marginLeft: 20,
          padding: 10,
          width: 250,
          minHeight: 400,
          border: '1px solid #aaa',
          backgroundColor: '#f9f9f9',
          overflowY: 'auto',
        }}
      >
        <h4>碰撞事件历史</h4>
        {collisionHistory.length > 0 ? (
          <ul style={{ paddingLeft: 20 }}>
            {collisionHistory.map((msg, index) => (
              <li key={index}>{msg}</li>
            ))}
          </ul>
        ) : (
          <div>暂无碰撞事件</div>
        )}
      </div>
    </div>
  );
}

export default Pinball;
