import React, { useRef, useEffect, useState } from 'react';

function Pinball() {
  const canvasRef = useRef(null);

  // Â≠òÂÇ®ÊâÄÊúâÂºπÁè†ÔºåÊ†ºÂºèÔºö{ x, y, vx, vy, radius, lastCollisionTime }
  const ballsRef = useRef([]);

  // ÂèëÂ∞ÑÁÇπÔºàÂõ∫ÂÆöÂú®Â∑¶‰∏ãËßíÔºâÔºåÂèØÊ†πÊçÆÈúÄË¶ÅË∞ÉÊï¥
  const spawnX = 50;
  const spawnY = 300;

  // ÊñπÂêëÔºàÂéüËßíÂ∫¶ÔºâÔºåËåÉÂõ¥ 0¬∞ÔΩû90¬∞
  const [direction, setDirection] = useState(45);
  // ÂèëÂ∞ÑÂäõÂ∫¶
  const [power, setPower] = useState(15);
  // Á¢∞Êíû‰∫ã‰ª∂ÂéÜÂè≤ËÆ∞ÂΩïÔºàÊòæÁ§∫Âú®Âè≥‰æßÔºâ
  const [collisionHistory, setCollisionHistory] = useState([]);
  // ÂÖ®Â±ÄÊöÇÂÅúÊ†áËÆ∞ÔºàÁ¢∞ÊíûÂêéÊöÇÂÅúÊï¥‰∏™Áâ©ÁêÜÊõ¥Êñ∞Ôºâ
  const [isPaused, setIsPaused] = useState(false);

  // ÂÆö‰πâÊõ¥Â§öÁöÑÈöúÁ¢çÁâ©
  const bumpers = [
    { id: 1, x: 200, y: 150, radius: 10, elasticity: 1.2, color: 'blue' },
    { id: 2, x: 350, y: 250, radius: 8, elasticity: 1.2, color: 'green' },
    { id: 3, x: 150, y: 100, radius: 12, elasticity: 1.2, color: 'purple' },
    { id: 4, x: 300, y: 80, radius: 10, elasticity: 1.2, color: 'orange' },
    { id: 5, x: 100, y: 200, radius: 8, elasticity: 1.2, color: 'pink' },
    { id: 6, x: 400, y: 150, radius: 10, elasticity: 1.2, color: 'cyan' },
    { id: 7, x: 250, y: 300, radius: 10, elasticity: 1.2, color: 'red' },
    { id: 8, x: 450, y: 100, radius: 8, elasticity: 1.2, color: 'yellow' },
    { id: 9, x: 50,  y: 50,  radius: 10, elasticity: 1.2, color: 'brown' },
  ];

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    const gravity = 0.2;

    // ÊØèÂ∏ßÊõ¥Êñ∞
    const update = () => {
      if (isPaused) return; // ÊöÇÂÅúÊó∂‰∏çÊõ¥Êñ∞Áâ©ÁêÜ

      // Êõ¥Êñ∞ÊâÄÊúâÂºπÁè†
      ballsRef.current.forEach((ball) => {
        // ÁÆÄÂçïÈáçÂäõ
        ball.vy += gravity;
        ball.x += ball.vx;
        ball.y += ball.vy;

        // ËæπÁïåÊ£ÄÊµãÔºö‰∏ä‰∏ãÂ∑¶Âè≥
        if (ball.y + ball.radius > canvas.height) {
          ball.y = canvas.height - ball.radius;
          ball.vy = -ball.vy * 0.8;
          ball.vx *= 0.9;
        }
        if (ball.y - ball.radius < 0) {
          ball.y = ball.radius;
          ball.vy = -ball.vy * 0.8;
        }
        if (ball.x - ball.radius < 0) {
          ball.x = ball.radius;
          ball.vx = -ball.vx * 0.8;
        }
        if (ball.x + ball.radius > canvas.width) {
          ball.x = canvas.width - ball.radius;
          ball.vx = -ball.vx * 0.8;
        }

        // ‰∏éÈöúÁ¢çÁâ©Á¢∞ÊíûÊ£ÄÊµã
        bumpers.forEach((bumper) => {
          const dx = ball.x - bumper.x;
          const dy = ball.y - bumper.y;
          const dist = Math.sqrt(dx * dx + dy * dy);

          if (dist < ball.radius + bumper.radius) {
            const now = Date.now();
            // Èò≤Ê≠¢Áü≠Êó∂Èó¥ÈáçÂ§çËß¶ÂèëÁ¢∞Êíû‰∫ã‰ª∂
            if (now - ball.lastCollisionTime > 500) {
              ball.lastCollisionTime = now;
              // ËÆ∞ÂΩïÁ¢∞Êíû‰ø°ÊÅØ
              setCollisionHistory((prev) => [
                ...prev,
                `${new Date().toLocaleTimeString()} ÈÅáÂà∞‰∫ã‰ª∂ ${bumper.id}`,
              ]);
              // Á¢∞ÊíûÊó∂ÊöÇÂÅúÊï¥‰∏™Ê®°Êãü
              setIsPaused(true);
            }

            // ËÆ°ÁÆóÁ¢∞ÊíûÊ≥ïÁ∫øÔºåÂπ∂Ëß£ÂÜ≥ÈáçÂè†
            const nx = dx / dist;
            const ny = dy / dist;
            const overlap = ball.radius + bumper.radius - dist;
            ball.x += nx * overlap;
            ball.y += ny * overlap;

            // ÁÆÄÂçïÊ≥ïÁ∫øÂèçÂ∞Ñ
            const dot = ball.vx * nx + ball.vy * ny;
            ball.vx = ball.vx - 2 * dot * nx;
            ball.vy = ball.vy - 2 * dot * ny;
            ball.vx *= bumper.elasticity;
            ball.vy *= bumper.elasticity;
          }
        });
      });
    };

    // ÊØèÂ∏ßÁªòÂà∂
    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // ÁªòÂà∂ÈöúÁ¢çÁâ©
      bumpers.forEach((bumper) => {
        ctx.beginPath();
        ctx.arc(bumper.x, bumper.y, bumper.radius, 0, Math.PI * 2);
        ctx.fillStyle = bumper.color;
        ctx.fill();
        ctx.closePath();
      });

      // ÁªòÂà∂ÊâÄÊúâÂºπÁè†
      ballsRef.current.forEach((ball) => {
        ctx.beginPath();
        ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
        ctx.fillStyle = 'red';
        ctx.fill();
        ctx.closePath();
      });

      // ÁªòÂà∂ÊñπÂêëÁÆ≠Â§¥ÔºàÁõ∏ÂØπ‰∫éÂèëÂ∞ÑÁÇπÔºâÔºåÁ®çÂæÆÂêë‰∏ãÂÅèÁßª
      const arrowStartX = spawnX;
      const arrowStartY = spawnY + 10; // ÂæÄ‰∏ãÁßªÂä®10px
      const arrowLength = 50;          // ÁÆ≠Â§¥ÈïøÂ∫¶
      const rad = (direction * Math.PI) / 180;
      const arrowEndX = arrowStartX + arrowLength * Math.cos(rad);
      const arrowEndY = arrowStartY - arrowLength * Math.sin(rad);

      ctx.beginPath();
      ctx.moveTo(arrowStartX, arrowStartY);
      ctx.lineTo(arrowEndX, arrowEndY);
      ctx.strokeStyle = 'black';
      ctx.lineWidth = 2;
      ctx.stroke();

      // ÁÆ≠Â§¥Â§¥ÈÉ®
      const headLength = 10;
      const angle1 = rad + Math.PI / 8;
      const angle2 = rad - Math.PI / 8;
      ctx.beginPath();
      ctx.moveTo(arrowEndX, arrowEndY);
      ctx.lineTo(
        arrowEndX - headLength * Math.cos(angle1),
        arrowEndY + headLength * Math.sin(angle1)
      );
      ctx.lineTo(
        arrowEndX - headLength * Math.cos(angle2),
        arrowEndY + headLength * Math.sin(angle2)
      );
      ctx.lineTo(arrowEndX, arrowEndY);
      ctx.fillStyle = 'black';
      ctx.fill();
    };

    const loop = () => {
      update();
      draw();
      animationFrameId = requestAnimationFrame(loop);
    };
    animationFrameId = requestAnimationFrame(loop);

    return () => cancelAnimationFrame(animationFrameId);
  }, [bumpers, isPaused, direction]);

  // ÂèëÂ∞ÑÊñ∞ÂºπÁè†ÔºàÂçäÂæÑÊõ¥Â∞èÔºâ
  const launchBall = () => {
    const rad = (direction * Math.PI) / 180;
    const newBall = {
      x: spawnX,
      y: spawnY,
