import React, { useState } from 'react';

/** 
 * 事件节点组件 
 */
function EventItem({ event }) {
  const [hover, setHover] = useState(false);

  // 悬浮提示（Hover Tooltip）的样式
  const tooltipStyle = {
    position: 'absolute',
    top: '-60px', // 控制浮层在垂直方向上的位置
    left: '50%',
    transform: 'translateX(-50%)',
    padding: '8px 12px',
    backgroundColor: '#fff',
    border: '1px solid #ccc',
    borderRadius: '8px',
    whiteSpace: 'nowrap',
    zIndex: 10,
    boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
    minWidth: '220px',
  };

  return (
    <div
      style={{
        position: 'relative',
        marginBottom: '0px', // 这里不使用固定间距，靠计算位置来决定
        cursor: 'pointer',
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      {/* 时间线左侧的蓝色圆点 */}
      <div
        style={{
          position: 'absolute',
          left: '-8px', // 让圆点中心与时间线更好贴合
          top: '0',
          width: '16px',
          height: '16px',
          borderRadius: '50%',
          backgroundColor: '#007BFF',
        }}
      />

      {/* 日期与标题部分（位于时间线右侧） */}
      <div style={{ marginLeft: '24px' }}>
        {/* 日期外框 */}
        <div
          style={{
            border: '2px solid #ddd',
            borderRadius: '20px',
            padding: '8px 14px',
            background: '#fff',
            fontSize: '15px',
            minWidth: '120px',
            textAlign: 'center',
            marginBottom: '4px',
          }}
        >
          {event.date}
        </div>
        {/* 事件标题（始终可见） */}
        <div style={{ fontSize: '14px', color: '#333', textAlign: 'center' }}>
          <strong>{event.title}</strong>
        </div>
      </div>

      {/* 悬浮时显示的详细信息浮层 */}
      {hover && (
        <div style={tooltipStyle}>
          <strong style={{ display: 'block', marginBottom: '6px' }}>
            {event.title}
          </strong>
          <div style={{ fontSize: '13px', color: '#333' }}>{event.detail}</div>
        </div>
      )}
    </div>
  );
}

/**
 * 单条浪潮的时间线组件
 * 通过 "computePositions" 函数，根据时间计算事件的垂直位置，并自动避免重叠。
 */
function Timeline({ wave }) {
  // 计算事件节点的垂直位置，以及所需的时间线高度
  const { positions, timelineHeight } = computePositions(wave.events);

  return (
    <div style={{ flex: '1', margin: '0 20px' }}>
      {/* 浪潮标题与时期 */}
      <h2 style={{ fontSize: '16px', marginBottom: '5px', textAlign: 'center' }}>
        {wave.title}
      </h2>
      <div style={{ fontSize: '14px', color: '#666', marginBottom: '20px', textAlign: 'center' }}>
        {wave.period}
      </div>

      {/* 垂直时间线，使用计算得到的 timelineHeight */}
      <div
        style={{
          position: 'relative',
          width: '2px',
          backgroundColor: '#bbb',
          height: timelineHeight + 'px',
          marginLeft: '0',
        }}
      >
        {/* 事件列表：根据计算的 positions 渲染 */}
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {wave.events.map((event, index) => {
            const pos = positions[index]; // 该事件在时间线上的垂直位置
            return (
              <div
                key={index}
                style={{
                  position: 'absolute',
                  top: pos + 'px',
                  width: '220px', // 控制每个事件在水平方向的可用空间
                }}
              >
                <EventItem event={event} />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

/**
 * 根据事件数组计算每个事件的垂直位置，自动避免重叠，并返回所需的时间线高度
 */
function computePositions(events) {
  if (!events || events.length === 0) {
    return { positions: [], timelineHeight: 0 };
  }

  // 将日期解析为毫秒，并按时间先后排序
  const sorted = [...events].sort((a, b) => new Date(a.date) - new Date(b.date));

  // 配置参数：每天对应多少像素（DAY_FACTOR），相邻事件最小间距（MIN_GAP）
  const DAY_FACTOR = 1.5;  // 每天对应的像素数，可根据实际需求调整
  const MIN_GAP = 30;      // 事件之间最小像素间距

  // 提取时间戳并计算最早和最晚
  const timestamps = sorted.map(e => new Date(e.date).getTime());
  const earliestTime = Math.min(...timestamps);
  const latestTime = Math.max(...timestamps);

  // 计算每个事件相对于最早时间的“天数偏移”
  const dayOffsets = timestamps.map(ts => {
    const diffMs = ts - earliestTime;
    const diffDays = diffMs / (1000 * 3600 * 24); // 毫秒 -> 天
    return diffDays;
  });

  // 初步计算每个事件的理想位置（不考虑最小间距）
  let positions = dayOffsets.map(offsetDays => offsetDays * DAY_FACTOR);

  // 按顺序遍历，保证相邻事件之间至少有 MIN_GAP 的间距
  for (let i = 1; i < positions.length; i++) {
    if (positions[i] - positions[i - 1] < MIN_GAP) {
      positions[i] = positions[i - 1] + MIN_GAP;
    }
  }

  // 计算最终所需的时间线高度（最后一个事件的位置 + 一些余量）
  const maxPos = positions[positions.length - 1];
  const timelineHeight = maxPos + 50; // 余量 50 px

  // 根据排序后的顺序输出 positions，但要映射回原顺序
  // 因为我们对 sorted 做了排序，需要还原到原 events 的顺序位置
  const finalPositions = new Array(events.length);
  sorted.forEach((item, idx) => {
    // 在原 events 中找到对应的 index
    const originalIndex = events.findIndex(e => e.date === item.date && e.title === item.title);
    finalPositions[originalIndex] = positions[idx];
  });

  return { positions: finalPositions, timelineHeight };
}

/**
 * 主容器组件：包含多个浪潮的时间线
 */
function Showcase() {
  // 示例数据：五大浪潮，每个浪潮包含若干确切日期的事件
  // 在某些浪潮中故意放了日期相近的事件来演示最小间距处理
  const waves = [
    {
      title: '个人电脑',
      period: '(1979-1982)',
      events: [
        {
          date: '1979-08-12',
          title: 'VisiCalc 发布',
          detail: '首款电子表格软件 VisiCalc 面向 Apple II 推出。',
        },
        {
          date: '1979-08-15',
          title: 'Apple II 升级',
          detail: '仅 3 天后，苹果推出 Apple II 小幅升级版本。',
        },
        {
          date: '1980-08-25',
          title: 'IBM PC 立项',
          detail: 'IBM 决定正式组建个人电脑部门，代号“Project Chess”。',
        },
        {
          date: '1981-08-12',
          title: 'IBM PC 发布',
          detail: 'IBM 5150 机型问世，搭载 Intel 8088 CPU 和 MS-DOS。',
        },
