import React, { useState, useEffect, useRef } from 'react';

/** 
 * Helper function to generate a curved path (cubic Bezier curve example)
 */
function generateCurvePath(x1, y1, x2, y2) {
  const cx = (x1 + x2) / 2; 
  return `M ${x1},${y1} C ${cx},${y1} ${cx},${y2} ${x2},${y2}`;
}

/**
 * Connection Line component: Uses <path> to draw a Bezier curve, and bolds the red line and text on hover
 */
function ConnectionLine({ fromPos, toPos, reason }) {
  const [hovered, setHovered] = useState(false);

  // Increase line width and saturation when hovered
  const strokeColor = hovered ? 'rgba(255,0,0,1)' : 'rgba(255,0,0,0.3)';
  const strokeWidth = hovered ? 3 : 2;
  const pathData = generateCurvePath(fromPos.x, fromPos.y, toPos.x, toPos.y);

  return (
    <g
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      style={{ 
        cursor: 'pointer',
        pointerEvents: 'visibleStroke', // Only respond to mouse events in the path stroke area
      }}
    >
      {/* Curve path */}
      <path
        d={pathData}
        stroke={strokeColor}
        strokeWidth={strokeWidth}
        fill="none"
      />
      {/* Text that can become bold and change color on hover */}
      <text
        x={(fromPos.x + toPos.x) / 2}
        y={(fromPos.y + toPos.y) / 2 - 5}
        fill={strokeColor}
        fontSize={hovered ? 14 : 12} // Larger font size on hover
        fontWeight={hovered ? 'bold' : 'normal'}
        textAnchor="middle"
      >
        {reason}
      </text>
    </g>
  );
}

/**
 * Single event node component (mostly unchanged)
 */
function EventItem({ event, eventId, registerPosition, onEventClick, scrollPos }) {
  const itemRef = useRef(null);
  const blueDotRef = useRef(null);
  const [hover, setHover] = useState(false);

  const tooltipStyle = {
    position: 'absolute',
    top: '-60px',
    left: '50%',
    transform: 'translateX(-50%)',
    padding: '8px 12px',
    backgroundColor: '#fff',
    border: '1px solid #ccc',
    borderRadius: '8px',
    whiteSpace: 'nowrap',
    zIndex: 10,
    boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
    minWidth: '220px',
  };

  useEffect(() => {
    if (blueDotRef.current) {
      const rect = blueDotRef.current.getBoundingClientRect();
      const pos = { x: rect.left + rect.width / 2, y: rect.top + rect.height / 2 };
      registerPosition(eventId, pos);
    }
  }, [blueDotRef, registerPosition, eventId, scrollPos]);

  return (
    <div
      ref={itemRef}
      style={{
        display: 'flex',
        alignItems: 'center',
        marginBottom: '20px',
        cursor: 'pointer',
        position: 'relative',
        padding: '4px 8px',
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
      onClick={() => onEventClick && onEventClick(eventId)}
    >
      {/* Blue node */}
      <div
        ref={blueDotRef}
        style={{
          width: '16px',
          height: '16px',
          borderRadius: '50%',
          backgroundColor: '#007BFF',
          marginRight: '8px',
          flexShrink: 0,
        }}
      />
      {/* Date and title section */}
      <div>
        <div
          style={{
            border: '2px solid #ddd',
            borderRadius: '20px',
            padding: '8px 14px',
            background: '#fff',
            fontSize: '15px',
            minWidth: '120px',
            textAlign: 'center',
            marginBottom: '4px',
