}

/**
 * Single wave timeline component (you can increase timelineHeight to show bottom events)
 */
function Timeline({ wave, waveIndex, registerPosition, onEventClick, scrollPos }) {
  // Change the height from 620 to a larger value, e.g., 800
  const timelineHeight = 800; 
  const timestamps = wave.events.map(e => new Date(e.date).getTime());
  const earliestTime = Math.min(...timestamps);
  const latestTime = Math.max(...timestamps);
  const totalTimeSpan = latestTime - earliestTime;
  const safeTimeSpan = totalTimeSpan === 0 ? 1 : totalTimeSpan;

  return (
    <div style={{ flex: '1', margin: '0 20px', position: 'relative' }}>
      <h2 style={{ fontSize: '16px', marginBottom: '5px', textAlign: 'center' }}>{wave.title}</h2>
      <div style={{ fontSize: '14px', color: '#666', marginBottom: '20px', textAlign: 'center' }}>
        {wave.period}
      </div>

      <div
        style={{
          position: 'relative',
          width: '2px',
          backgroundColor: '#bbb',
          height: timelineHeight + 'px',
          marginLeft: '0',
          borderRadius: '1px',
        }}
      >
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {wave.events.map((event, index) => {
            const currentTime = new Date(event.date).getTime();
            const offset = currentTime - earliestTime;
            const ratio = offset / safeTimeSpan;
            const pos = ratio * timelineHeight - 40;
            const eventId = `${waveIndex}-${index}`;
            return (
              <div
                key={index}
                style={{
                  position: 'absolute',
                  top: pos + 'px',
                  width: '220px',
                }}
              >
                <EventItem
                  event={event}
                  eventId={eventId}
                  registerPosition={registerPosition}
                  onEventClick={onEventClick}
                  scrollPos={scrollPos}
                />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

/**
 * Connection Layer component: Convert all connections to curves (ConnectionLine)
 */
function ConnectionsLayer({ connections, eventPositions, containerRef }) {
  const containerRect = containerRef.current ? containerRef.current.getBoundingClientRect() : { left: 0, top: 0 };

  return (
    <svg
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        // Remove pointerEvents: 'none' to allow curve mouse event capturing
        width: '100%',
        height: '100%',
      }}
    >
      {connections.map((conn, index) => {
        const fromPos = eventPositions[conn.from];
        const toPos = eventPositions[conn.to];
        if (!fromPos || !toPos) return null;

        // Offset relative to the main container
        const adjustedFrom = { x: fromPos.x - containerRect.left, y: fromPos.y - containerRect.top };
        const adjustedTo = { x: toPos.x - containerRect.left, y: toPos.y - containerRect.top };

        return (
          <ConnectionLine
            key={index}
            fromPos={adjustedFrom}
            toPos={adjustedTo}
            reason={conn.reason}
          />
        );
      })}
    </svg>
  );
}

