import React, { useState, useEffect } from 'react';
import Confetti from 'react-confetti';
import './Showcase.css'; // 可以在这里引入自定义CSS

function Showcase() {
  // 控制是否显示彩带
  const [showConfetti, setShowConfetti] = useState(false);

  // 监听窗口尺寸，以便 Confetti 能够自适应
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    function handleResize() {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // 示例：LLM 时间轴数据
  const llmTimeline = [
    { year: '2021', content: 'GPT-3 text-davinci-002 API' },
    { year: '2022', content: 'ChatGPT with GPT-3.5' },
    { year: '2023', content: 'GPT-4 发布' },
    { year: '2024', content: 'GPT-4.5？更多推理能力' },
  ];

  // 示例：Robot 时间轴数据
  const robotTimeline = [
    { year: 'Feb 2024', content: 'Figure 估值 $2.6B' },
    { year: 'Jan 2025', content: 'Cursor $2.5B -> Anthropic $60B' },
    { year: 'Feb 2025', content: 'Figure 估值从 $2.6B 飙升至 $39.5B' },
    { year: 'Feb 2025', content: '发布 Helix：与合作伙伴推出推理机器人' },
  ];

  return (
    <div className="showcase-container">
      {showConfetti && (
        <Confetti
          width={windowSize.width}
          height={windowSize.height}
        />
      )}

      <header className="showcase-header">
        <h1>Robot in 2025 as GenAI in 2022</h1>
        <h2>Riding the robot wave with lessons learned from GenAI boom</h2>
        <button onClick={() => setShowConfetti(!showConfetti)}>
          {showConfetti ? '停止彩带' : '庆祝一下'}
        </button>
      </header>

      <section className="showcase-text">
        <p>
          Don't regret missing the GenAI boom—we are about to embrace an even bigger robotics wave soon.
        </p>
        <p>
          Over the past year, we witnessed many AI advancements: VLM, open-source reasoning models, and multi-agent frameworks.
          All of these point towards robotics as a key application, as reflected in Figure’s valuation surge from $2.6B (Feb 2024)
          to $39.5B (Feb 2025). With great waves come loud tides—too many exciting opportunities in the robot wave makes it
          difficult to focus on the right things.
        </p>
        <p>
          Yet, when we look back at the history of GenAI, things becomes much clearer.
          Let’s discuss together the trial and error in GenAI back in 2022 to better ride the robot wave in 2025.
        </p>
      </section>

      <section className="timeline-section">
        <h2>LLM Timeline</h2>
        <div className="timeline-container">
          {llmTimeline.map((item, index) => (
            <div className="timeline-item" key={index}>
              <div className="timeline-year">{item.year}</div>
              <div className="timeline-content">{item.content}</div>
            </div>
          ))}
        </div>

        <h2>Robot Timeline</h2>
        <div className="timeline-container">
          {robotTimeline.map((item, index) => (
            <div className="timeline-item" key={index}>
              <div className="timeline-year">{item.year}</div>
              <div className="timeline-content">{item.content}</div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Showcase;
