import React, { useState, useEffect, useRef } from 'react';

/**
 * 单个事件节点组件
 * 添加了 ref 用于获取 DOM 节点的位置信息
 */
function EventItem({ event, eventId, registerPosition }) {
  const itemRef = useRef(null);
  const [hover, setHover] = useState(false);

  // 悬浮提示（Hover Tooltip）的样式
  const tooltipStyle = {
    position: 'absolute',
    top: '-60px',
    left: '50%',
    transform: 'translateX(-50%)',
    padding: '8px 12px',
    backgroundColor: '#fff',
    border: '1px solid #ccc',
    borderRadius: '8px',
    whiteSpace: 'nowrap',
    zIndex: 10,
    boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
    minWidth: '220px',
  };

  useEffect(() => {
    if (itemRef.current) {
      // 获取 DOM 节点相对于页面的坐标
      const rect = itemRef.current.getBoundingClientRect();
      // 此处假设圆点位于节点左侧16px的中心位置，实际可根据需要调整
      const pos = { x: rect.left, y: rect.top + rect.height / 2 };
      registerPosition(eventId, pos);
    }
  }, [itemRef.current]);

  return (
    <div
      ref={itemRef}
      style={{
        position: 'relative',
        marginBottom: '20px',
        cursor: 'pointer',
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      {/* 时间线左侧的蓝色圆点 */}
      <div
        style={{
          position: 'absolute',
          top: '0',
          width: '16px',
          height: '16px',
          borderRadius: '50%',
          backgroundColor: '#007BFF',
        }}
      />

      {/* 日期与标题部分 */}
      <div style={{ marginLeft: '24px' }}>
        <div
          style={{
            border: '2px solid #ddd',
            borderRadius: '20px',
            padding: '8px 14px',
            background: '#fff',
            fontSize: '15px',
            minWidth: '120px',
            textAlign: 'center',
            marginBottom: '4px',
          }}
        >
          {event.date}
        </div>
        <div style={{ fontSize: '14px', color: '#333', textAlign: 'center' }}>
          <strong>{event.title}</strong>
        </div>
      </div>

      {hover && (
        <div style={tooltipStyle}>
          <strong style={{ display: 'block', marginBottom: '6px' }}>
            {event.title}
          </strong>
          <div style={{ fontSize: '13px', color: '#333' }}>{event.detail}</div>
        </div>
      )}
    </div>
  );
}

/**
 * 单条浪潮的时间线组件
 * 添加 eventId 用于区分各个事件
 */
function Timeline({ wave, waveIndex, registerPosition }) {
  const timelineHeight = 400;
  const timestamps = wave.events.map(e => new Date(e.date).getTime());
  const earliestTime = Math.min(...timestamps);
  const latestTime = Math.max(...timestamps);
  const totalTimeSpan = latestTime - earliestTime;
  const safeTimeSpan = totalTimeSpan === 0 ? 1 : totalTimeSpan;

  return (
    <div style={{ flex: '1', margin: '0 20px', position: 'relative' }}>
      <h2 style={{ fontSize: '16px', marginBottom: '5px', textAlign: 'center' }}>
        {wave.title}
      </h2>
      <div style={{ fontSize: '14px', color: '#666', marginBottom: '20px', textAlign: 'center' }}>
        {wave.period}
      </div>

      <div
        style={{
          position: 'relative',
          width: '2px',
          backgroundColor: '#bbb',
          height: timelineHeight + 'px',
          marginLeft: '0',
        }}
      >
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {wave.events.map((event, index) => {
            const currentTime = new Date(event.date).getTime();
            const offset = currentTime - earliestTime;
            const ratio = offset / safeTimeSpan;
            const pos = ratio * timelineHeight;
            // 定义全局唯一的事件 id 格式：波浪索引-事件索引
            const eventId = `${waveIndex}-${index}`;
            return (
              <div
                key={index}
                style={{
                  position: 'absolute',
                  top: pos + 'px',
                  width: '220px',
                }}
              >
                <EventItem event={event} eventId={eventId} registerPosition={registerPosition} />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

/**
 * 用于绘制连接线的组件，使用 SVG 实现
 */
function ConnectionsLayer({ connections, eventPositions }) {
  // 确保有位置信息后再绘制
  return (
    <svg style={{ position: 'absolute', top: 0, left: 0, pointerEvents: 'none', width: '100%', height: '100%' }}>
      {connections.map((conn, index) => {
        const fromPos = eventPositions[conn.from];
        const toPos = eventPositions[conn.to];
        if (!fromPos || !toPos) return null;
        // 计算连线中点用于放置原因文本
        const midX = (fromPos.x + toPos.x) / 2;
        const midY = (fromPos.y + toPos.y) / 2;
        return (
          <g key={index}>
            <line
              x1={fromPos.x}
              y1={fromPos.y}
              x2={toPos.x}
              y2={toPos.y}
              stroke="#f00"
              strokeWidth="2"
            />
            <text x={midX} y={midY - 5} fill="#f00" fontSize="12" textAnchor="middle">
              {conn.reason}
            </text>
          </g>
        );
      })}
    </svg>
  );
}

/**
 * 主容器组件：包含多个浪潮的时间线和连接线
 */
function Showcase() {
  // 示例数据：五大浪潮
  const waves = [
    {
      title: '个人电脑',
      period: '(1979-1982)',
      events: [
        {
          date: '1979-08-12',
          title: 'VisiCalc 发布',
          detail: '首款电子表格软件 VisiCalc 面向 Apple II 推出。',
        },
        {
          date: '1980-08-25',
          title: 'IBM PC 立项',
          detail: 'IBM 决定正式组建个人电脑部门，代号“Project Chess”。',
        },
        {
          date: '1981-08-12',
          title: 'IBM PC 发布',
          detail: 'IBM 5150 机型问世，搭载 Intel 8088 CPU 和 MS-DOS。',
        },
        {
          date: '1982-02-16',
          title: 'Compaq 创立',
          detail: '康柏公司成立，专注于生产 IBM PC 兼容机。',
        },
      ],
    },
    {
      title: '互联网',
      period: '(1994-1997)',
      events: [
        {
          date: '1994-04-04',
          title: 'Netscape 创立',
          detail: 'Mosaic 团队成员创立 Netscape，专注浏览器研发。',
        },
        {
          date: '1995-07-05',
          title: 'Amazon 上线',
          detail: '杰夫·贝索斯创立的电商网站 Amazon.com 开始运营。',
        },
        {
          date: '1996-07-04',
          title: 'Hotmail 推出',
          detail: '免费网络邮件服务 Hotmail 上线，迅速风靡全球。',
        },
