import React, { useRef, useEffect, useState } from 'react';

function Pinball() {
  const canvasRef = useRef(null);
  // Â≠òÂÇ®ÊâÄÊúâÂºπÁè†ÔºåÂºπÁè†ÂØπË±°Ôºö{ x, y, vx, vy, radius, lastCollisionTime }
  const ballsRef = useRef([]);
  // ÂèëÂ∞ÑÁÇπÔºàÂõ∫ÂÆöÔºâ
  const spawnX = 50;
  const spawnY = 300;

  // ÂèëÂ∞ÑÂèÇÊï∞
  const [angle, setAngle] = useState(45); // Âçï‰ΩçÔºö¬∞ÔºåËåÉÂõ¥0~90
  const [power, setPower] = useState(15); // ÂèëÂ∞ÑÂäõÂ∫¶
  // Á¢∞Êíû‰∫ã‰ª∂ÂéÜÂè≤ËÆ∞ÂΩïÔºàÊòæÁ§∫Âú®Âè≥‰æßÈù¢ÊùøÔºâ
  const [collisionHistory, setCollisionHistory] = useState([]);
  // ÂÖ®Â±ÄÊöÇÂÅúÔºàÁ¢∞ÊíûÂêéÊöÇÂÅúÊï¥‰∏™Áâ©ÁêÜÊõ¥Êñ∞ÔºåÁ≠âÂæÖÁî®Êà∑ÁÇπÂáªÁ°ÆÂÆöÁªßÁª≠Ôºâ
  const [isPaused, setIsPaused] = useState(false);

  // ÂÆö‰πâÂ§ö‰∏™ÈöúÁ¢çÁâ©ÔºàÈ¢úËâ≤‰∏çÂêå„ÄÅ‰ΩìÁßØËæÉÂ∞èÔºâ
  const bumpers = [
    { id: 1, x: 200, y: 150, radius: 10, elasticity: 1.2, color: 'blue' },
    { id: 2, x: 350, y: 250, radius: 8, elasticity: 1.2, color: 'green' },
    { id: 3, x: 150, y: 100, radius: 12, elasticity: 1.2, color: 'purple' },
    { id: 4, x: 300, y: 80, radius: 10, elasticity: 1.2, color: 'orange' },
    { id: 5, x: 100, y: 200, radius: 8, elasticity: 1.2, color: 'pink' },
    { id: 6, x: 400, y: 150, radius: 10, elasticity: 1.2, color: 'cyan' },
  ];

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    const gravity = 0.2;

    const update = () => {
      if (isPaused) return; // ÊöÇÂÅúÊó∂‰∏çÊõ¥Êñ∞Áâ©ÁêÜÁä∂ÊÄÅ

      // Êõ¥Êñ∞ÊØè‰∏™ÂºπÁè†ÁöÑ‰ΩçÁΩÆ
      ballsRef.current.forEach(ball => {
        ball.vy += gravity;
        ball.x += ball.vx;
        ball.y += ball.vy;

        // ËæπÁïåÁ¢∞ÊíûÔºö‰∏ä‰∏ãÂ∑¶Âè≥
        if (ball.y + ball.radius > canvas.height) {
          ball.y = canvas.height - ball.radius;
          ball.vy = -ball.vy * 0.8;
          ball.vx *= 0.9;
        }
        if (ball.y - ball.radius < 0) {
          ball.y = ball.radius;
          ball.vy = -ball.vy * 0.8;
        }
        if (ball.x - ball.radius < 0) {
          ball.x = ball.radius;
          ball.vx = -ball.vx * 0.8;
        }
        if (ball.x + ball.radius > canvas.width) {
          ball.x = canvas.width - ball.radius;
          ball.vx = -ball.vx * 0.8;
        }

        // ‰∏éÊØè‰∏™ÈöúÁ¢çÁâ©ÁöÑÁ¢∞ÊíûÊ£ÄÊµãÔºàÂü∫‰∫éÂúÜ‰∏éÂúÜÁöÑÁ¢∞ÊíûÔºâ
        bumpers.forEach(bumper => {
          const dx = ball.x - bumper.x;
          const dy = ball.y - bumper.y;
          const dist = Math.sqrt(dx * dx + dy * dy);
          if (dist < ball.radius + bumper.radius) {
            const now = Date.now();
            // Èò≤Ê≠¢Áü≠Êó∂Èó¥ÂÜÖÈáçÂ§çËß¶ÂèëÁ¢∞Êíû‰∫ã‰ª∂
            if (now - ball.lastCollisionTime > 500) {
              ball.lastCollisionTime = now;
              setCollisionHistory(prev => [
                ...prev,
                `${new Date().toLocaleTimeString()} ÊíûÂà∞‰∫ÜÈöúÁ¢çÁâ© ${bumper.id}`,
              ]);
              // Á¢∞ÊíûÊó∂ÊöÇÂÅúÊï¥‰∏™Ê®°Êãü
              setIsPaused(true);
            }
            // ËÆ°ÁÆóÁ¢∞ÊíûÊ≥ïÁ∫øÔºåÂπ∂Ëß£ÂÜ≥ÈáçÂè†
            const nx = dx / dist;
            const ny = dy / dist;
            const overlap = ball.radius + bumper.radius - dist;
            ball.x += nx * overlap;
            ball.y += ny * overlap;
            // ÁÆÄÂçïÁöÑÊ≥ïÁ∫øÂèçÂ∞Ñ
            const dot = ball.vx * nx + ball.vy * ny;
            ball.vx = ball.vx - 2 * dot * nx;
            ball.vy = ball.vy - 2 * dot * ny;
            ball.vx *= bumper.elasticity;
            ball.vy *= bumper.elasticity;
          }
        });
      });
    };

    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // ÁªòÂà∂ÈöúÁ¢çÁâ©
      bumpers.forEach(bumper => {
        ctx.beginPath();
        ctx.arc(bumper.x, bumper.y, bumper.radius, 0, Math.PI * 2);
        ctx.fillStyle = bumper.color;
        ctx.fill();
        ctx.closePath();
      });

      // ÁªòÂà∂ÊâÄÊúâÂºπÁè†
      ballsRef.current.forEach(ball => {
        ctx.beginPath();
        ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
        ctx.fillStyle = 'red';
        ctx.fill();
        ctx.closePath();
      });

      // ÁªòÂà∂Ë°®Á§∫ÂèëÂ∞ÑËßíÂ∫¶ÁöÑÁÆ≠Â§¥ÔºàÂßãÁªàÊòæÁ§∫Âú®ÂèëÂ∞ÑÁÇπÂ§ÑÔºâ
      const arrowLength = 50; // Âõ∫ÂÆöÁÆ≠Â§¥ÈïøÂ∫¶
      const rad = (angle * Math.PI) / 180;
      const arrowEndX = spawnX + arrowLength * Math.cos(rad);
      const arrowEndY = spawnY - arrowLength * Math.sin(rad);
      ctx.beginPath();
      ctx.moveTo(spawnX, spawnY);
      ctx.lineTo(arrowEndX, arrowEndY);
      ctx.strokeStyle = 'black';
      ctx.lineWidth = 2;
      ctx.stroke();

      // ÁªòÂà∂ÁÆ≠Â§¥Â§¥ÈÉ®
      const headLength = 10;
      const angle1 = rad + Math.PI / 8;
      const angle2 = rad - Math.PI / 8;
      ctx.beginPath();
      ctx.moveTo(arrowEndX, arrowEndY);
      ctx.lineTo(arrowEndX - headLength * Math.cos(angle1), arrowEndY + headLength * Math.sin(angle1));
      ctx.lineTo(arrowEndX - headLength * Math.cos(angle2), arrowEndY + headLength * Math.sin(angle2));
      ctx.lineTo(arrowEndX, arrowEndY);
      ctx.fillStyle = 'black';
      ctx.fill();
    };

    const loop = () => {
      update();
      draw();
      animationFrameId = requestAnimationFrame(loop);
    };
    animationFrameId = requestAnimationFrame(loop);
    return () => cancelAnimationFrame(animationFrameId);
  }, [bumpers, isPaused, angle]);

  // ÂèëÂ∞ÑÊñ∞ÂºπÁè†Ôºö‰ªéÂèëÂ∞ÑÁÇπÁîüÊàê‰∏Ä‰∏™Êñ∞ÁöÑÂºπÁè†ÔºàÂ§ßÂ∞èÂáèÂ∞èÔºâ
  const launchBall = () => {
    const rad = (angle * Math.PI) / 180;
    const newBall = {
      x: spawnX,
      y: spawnY,
      vx: power * Math.cos(rad),
      vy: -power * Math.sin(rad),
      radius: 5, // ÂºπÁè†Â∞∫ÂØ∏ÂáèÂ∞è
      lastCollisionTime: 0,
