import React, { useRef, useEffect, useState } from 'react';

function Pinball() {
  const canvasRef = useRef(null);
  // 存储所有弹珠，弹珠对象：{ x, y, vx, vy, radius, lastCollisionTime }
  const ballsRef = useRef([]);
  // 发射点（固定）
  const spawnX = 50;
  const spawnY = 300;

  // 发射参数
  const [angle, setAngle] = useState(45); // 单位：°，范围0~90
  const [power, setPower] = useState(15); // 发射力度
  // 碰撞事件历史记录（显示在右侧面板）
  const [collisionHistory, setCollisionHistory] = useState([]);
  // 全局暂停（碰撞后暂停整个物理更新，等待用户点击确定继续）
  const [isPaused, setIsPaused] = useState(false);

  // 定义多个障碍物（颜色不同、体积较小）
  const bumpers = [
    { id: 1, x: 200, y: 150, radius: 10, elasticity: 1.2, color: 'blue' },
    { id: 2, x: 350, y: 250, radius: 8, elasticity: 1.2, color: 'green' },
    { id: 3, x: 150, y: 100, radius: 12, elasticity: 1.2, color: 'purple' },
    { id: 4, x: 300, y: 80, radius: 10, elasticity: 1.2, color: 'orange' },
    { id: 5, x: 100, y: 200, radius: 8, elasticity: 1.2, color: 'pink' },
    { id: 6, x: 400, y: 150, radius: 10, elasticity: 1.2, color: 'cyan' },
  ];

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    const gravity = 0.2;

    const update = () => {
      if (isPaused) return; // 暂停时不更新物理状态

      // 更新每个弹珠的位置
      ballsRef.current.forEach(ball => {
        ball.vy += gravity;
        ball.x += ball.vx;
        ball.y += ball.vy;

        // 边界碰撞：上下左右
        if (ball.y + ball.radius > canvas.height) {
          ball.y = canvas.height - ball.radius;
          ball.vy = -ball.vy * 0.8;
          ball.vx *= 0.9;
        }
        if (ball.y - ball.radius < 0) {
          ball.y = ball.radius;
          ball.vy = -ball.vy * 0.8;
        }
        if (ball.x - ball.radius < 0) {
          ball.x = ball.radius;
          ball.vx = -ball.vx * 0.8;
        }
        if (ball.x + ball.radius > canvas.width) {
          ball.x = canvas.width - ball.radius;
          ball.vx = -ball.vx * 0.8;
        }

        // 与每个障碍物的碰撞检测（基于圆与圆的碰撞）
        bumpers.forEach(bumper => {
          const dx = ball.x - bumper.x;
          const dy = ball.y - bumper.y;
          const dist = Math.sqrt(dx * dx + dy * dy);
          if (dist < ball.radius + bumper.radius) {
            const now = Date.now();
            // 防止短时间内重复触发碰撞事件
            if (now - ball.lastCollisionTime > 500) {
              ball.lastCollisionTime = now;
              setCollisionHistory(prev => [
                ...prev,
                `${new Date().toLocaleTimeString()} 撞到了障碍物 ${bumper.id}`,
              ]);
              // 碰撞时暂停整个模拟
              setIsPaused(true);
            }
            // 计算碰撞法线，并解决重叠
            const nx = dx / dist;
            const ny = dy / dist;
            const overlap = ball.radius + bumper.radius - dist;
            ball.x += nx * overlap;
            ball.y += ny * overlap;
            // 简单的法线反射
            const dot = ball.vx * nx + ball.vy * ny;
            ball.vx = ball.vx - 2 * dot * nx;
            ball.vy = ball.vy - 2 * dot * ny;
            ball.vx *= bumper.elasticity;
            ball.vy *= bumper.elasticity;
          }
        });
      });
    };

    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // 绘制障碍物
      bumpers.forEach(bumper => {
        ctx.beginPath();
        ctx.arc(bumper.x, bumper.y, bumper.radius, 0, Math.PI * 2);
        ctx.fillStyle = bumper.color;
        ctx.fill();
        ctx.closePath();
      });

      // 绘制所有弹珠
      ballsRef.current.forEach(ball => {
        ctx.beginPath();
        ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
        ctx.fillStyle = 'red';
        ctx.fill();
        ctx.closePath();
      });

      // 绘制表示发射角度的箭头（始终显示在发射点处）
      const arrowLength = 50; // 固定箭头长度
      const rad = (angle * Math.PI) / 180;
      const arrowEndX = spawnX + arrowLength * Math.cos(rad);
      const arrowEndY = spawnY - arrowLength * Math.sin(rad);
      ctx.beginPath();
      ctx.moveTo(spawnX, spawnY);
      ctx.lineTo(arrowEndX, arrowEndY);
      ctx.strokeStyle = 'black';
      ctx.lineWidth = 2;
      ctx.stroke();

      // 绘制箭头头部
      const headLength = 10;
      const angle1 = rad + Math.PI / 8;
      const angle2 = rad - Math.PI / 8;
      ctx.beginPath();
      ctx.moveTo(arrowEndX, arrowEndY);
      ctx.lineTo(arrowEndX - headLength * Math.cos(angle1), arrowEndY + headLength * Math.sin(angle1));
      ctx.lineTo(arrowEndX - headLength * Math.cos(angle2), arrowEndY + headLength * Math.sin(angle2));
      ctx.lineTo(arrowEndX, arrowEndY);
      ctx.fillStyle = 'black';
      ctx.fill();
    };

    const loop = () => {
      update();
      draw();
      animationFrameId = requestAnimationFrame(loop);
    };
    animationFrameId = requestAnimationFrame(loop);
    return () => cancelAnimationFrame(animationFrameId);
  }, [bumpers, isPaused, angle]);

  // 发射新弹珠：从发射点生成一个新的弹珠（大小减小）
  const launchBall = () => {
    const rad = (angle * Math.PI) / 180;
    const newBall = {
      x: spawnX,
      y: spawnY,
      vx: power * Math.cos(rad),
      vy: -power * Math.sin(rad),
      radius: 5, // 弹珠尺寸减小
      lastCollisionTime: 0,
    };
    ballsRef.current.push(newBall);
    // 发射时确保模拟处于运行状态
    setIsPaused(false);
  };

  // 左右按钮调整发射角度
  const adjustAngleLeft = () => setAngle(prev => Math.max(0, prev - 5));
  const adjustAngleRight = () => setAngle(prev => Math.min(90, prev + 5));

  return (
    <div style={{ display: 'flex', flexDirection: 'row' }}>
      <div>
        <div style={{ position: 'relative' }}>
          <canvas
            ref={canvasRef}
            width={500}
            height={400}
            style={{ border: '1px solid #000' }}
          />
          {/* 模态对话框：碰撞时暂停显示，点击“确定”后继续 */}
          {isPaused && (
            <div
              style={{
                position: 'absolute',
                top: '40%',
                left: '50%',
                transform: 'translate(-50%, -50%)',
                backgroundColor: 'rgba(255,255,255,0.9)',
                border: '1px solid #ccc',
                padding: '20px',
                zIndex: 10,
                textAlign: 'center'
              }}
            >
              <p>碰撞暂停，点击确定继续</p>
              <button onClick={() => setIsPaused(false)}>确定</button>
            </div>
          )}
        </div>
        {/* 控制面板放在画布下方 */}
        <div style={{ marginTop: 20, textAlign: 'center' }}>
          <div style={{ marginBottom: 10 }}>
            <button onClick={adjustAngleLeft}>←</button>
            <span style={{ margin: '0 10px' }}>角度: {angle}°</span>
            <button onClick={adjustAngleRight}>→</button>
          </div>
          <div style={{ marginBottom: 10 }}>
            <span>力度: {power}</span>
            <input
              type="range"
              min="5"
              max="30"
              value={power}
              onChange={e => setPower(Number(e.target.value))}
              style={{ marginLeft: 10 }}
            />
          </div>
          <button onClick={launchBall} style={{ fontSize: '16px', padding: '10px 20px' }}>
            🚀 发射弹珠
          </button>
        </div>
      </div>
      {/* 右侧显示碰撞事件历史记录 */}
      <div
        style={{
          marginLeft: 20,
          padding: 10,
          width: 250,
          minHeight: 400,
          border: '1px solid #aaa',
          backgroundColor: '#f9f9f9',
          overflowY: 'auto'
        }}
      >
        <h4>碰撞事件历史</h4>
        {collisionHistory.length > 0 ? (
          <ul style={{ paddingLeft: 20 }}>
            {collisionHistory.map((msg, index) => (
              <li key={index}>{msg}</li>
            ))}
          </ul>
        ) : (
          <div>暂无碰撞事件</div>
        )}
      </div>
    </div>
  );
}

export default Pinball;
