import React, { useState } from 'react';

function EventItem({ event }) {
  const [hover, setHover] = useState(false);

  // Hover浮层的样式
  const tooltipStyle = {
    position: 'absolute',
    top: '-120px',
    left: '50%',
    transform: 'translateX(-50%)',
    padding: '10px 14px',
    backgroundColor: '#fff',
    border: '1px solid #ccc',
    borderRadius: '8px',
    whiteSpace: 'nowrap',
    zIndex: 10,
    boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
    minWidth: '200px',
  };

  return (
    <div
      style={{
        position: 'relative',
        marginBottom: '60px', // 拉大时间点之间的间距
        cursor: 'pointer',
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      {/* 圆点（固定在左侧时间线处） */}
      <div
        style={{
          position: 'absolute',
          left: '-8px', // 让圆点中心与时间线对齐
          top: '0',
          width: '16px',
          height: '16px',
          borderRadius: '50%',
          backgroundColor: '#007BFF',
        }}
      />

      {/* 日期外框（在时间线右侧） */}
      <div
        style={{
          marginLeft: '24px', // 留出给圆点的空间
          border: '2px solid #ddd',
          borderRadius: '20px',
          padding: '10px 16px',
          background: '#fff',
          fontSize: '15px',
          minWidth: '130px', // 让日期区域更宽一些
          textAlign: 'center',
        }}
      >
        {event.date}
      </div>

      {/* 悬浮显示详细信息 */}
      {hover && (
        <div style={tooltipStyle}>
          <strong style={{ display: 'block', marginBottom: '6px' }}>
            {event.title}
          </strong>
          <div style={{ fontSize: '13px', color: '#333' }}>{event.detail}</div>
        </div>
      )}
    </div>
  );
}

function Timeline({ wave }) {
  // 时间线总高度（像素），可根据数据量和排版需求进行调整
  const timelineHeight = 600; 
  const totalEvents = wave.events.length;

  return (
    <div style={{ flex: '1', margin: '0 20px' }}>
      {/* 浪潮标题与时期 */}
      <h2 style={{ fontSize: '16px', marginBottom: '5px', textAlign: 'center' }}>
        {wave.title}
      </h2>
      <div style={{ fontSize: '14px', color: '#666', marginBottom: '20px', textAlign: 'center' }}>
        {wave.period}
      </div>

      {/* 时间线本体 */}
      <div
        style={{
          position: 'relative',
          marginLeft: '0',
          width: '2px',
          backgroundColor: '#bbb',
          height: timelineHeight + 'px',
        }}
      >
        {/* 事件列表 */}
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {wave.events.map((event, index) => {
            // 计算事件节点在时间线上的垂直位置
            const pos =
              totalEvents === 1
                ? timelineHeight / 2
                : (index / (totalEvents - 1)) * timelineHeight;
            return (
              <div
                key={index}
                style={{
                  position: 'absolute',
                  top: pos + 'px',
                  width: '200px', // 控制每个事件在水平方向的可用空间
                }}
              >
                <EventItem event={event} />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

