// ============== 段落1：导入依赖 ==============
import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import data_summary from './data_summary.json'; // 确保有 data_summary.json

// ============== 段落2：定义 TreePlotVisualization 组件 ==============
const TreePlotVisualization = () => {
  // ============== 段落2.1：定义所有 state 变量 ==============
  const [showTree, setShowTree] = useState(true); // true: Exploration View, false: Evaluation View
  const [nodes, setNodes] = useState([]);
  const [links, setLinks] = useState([]);
  const [hoveredNode, setHoveredNode] = useState(null);
  const [selectedNode, setSelectedNode] = useState(null);
  const [userInput, setUserInput] = useState('');
  const [analysisIntent, setAnalysisIntent] = useState('');
  const [isAnalysisSubmitted, setIsAnalysisSubmitted] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);
  const [isEvaluating, setIsEvaluating] = useState(false);
  const [error, setError] = useState(null);
  const svgRef = useRef(null);
  const [hypothesesList, setHypothesesList] = useState([]);
  // **新增**: 用于存放“拖拽后生成的 ghostNode + 悬浮确认按钮信息”
  const [pendingChange, setPendingChange] = useState(null);

  // X/Y 轴指标（仅在 Evaluation View 用）
  const [xAxisMetric, setXAxisMetric] = useState('feasibilityScore');
  const [yAxisMetric, setYAxisMetric] = useState('noveltyScore');

  // ID 计数器
  const idCounterRef = useRef(0);
  const generateUniqueId = () => {
    idCounterRef.current += 1;
    return `node-${idCounterRef.current}`;
  };

  // 配色映射
  const colorMap = {
    root: '#4C84FF',
    simple: '#45B649',
    complex: '#FF6B6B',
  };

  // ============== 段落3：评估假设（evaluateHypotheses） ==============
  const evaluateHypotheses = async (hypotheses) => {
    setIsEvaluating(true);
    setError(null);

    try {
      const prompt = `For the following hypotheses, rank **each one individually** based on novelty, feasibility, and impact.
      Novelty is judged by whether the hypotheses challenges traditional assumptions, propose innovative approaches, or explore under-researched areas will score higher in novelty. Less novel hypotheses include those that replicate well-known findings or extend existing studies with minimal new insights.
      Feasibility is judged by whether the hypothesis can be represented by the dataset described in the data summary.
      Impact assesses how the hypotheses addresses critical or emerging issues, influence policy or decision-making, or provide insights into major societal, economic, or scientific challenges. Hypotheses with lower impact are narrower in scope or contribute incremental knowledge without broader implications.
      For each hypothesis, provide its rank (1 being the highest) in terms of novelty, feasibility, and impact.
      Also, provide the ranking reasons for each hypothesis.
      **Ensure that you include all hypotheses in your evaluation and do not combine or omit any.**
      **No two hypotheses should have the same rank in any category. Assign unique ranks to each hypothesis in each category to avoid overlaps.**
      Return the results in JSON format as an array of objects with the following fields:
      - id
      - novelty_rank
      - novelty_rank_reason
      - feasibility_rank
      - feasibility_rank_reason
      - impact_rank
      - impact_rank_reason

Here are the hypotheses:
${JSON.stringify(hypotheses)}

Data summary: ${JSON.stringify(data_summary)}

Please provide only the JSON array in your response.`;

      const apiKey = process.env.REACT_APP_OPENAI_API_KEY;
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          model: 'gpt-4o',
          messages: [
            { role: 'system', content: 'You are a data scientist.' },
            { role: 'user', content: prompt },
          ],
          max_tokens: 2000,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error.message);
      }

      const data = await response.json();
      const assistantReply = data.choices[0].message.content.trim();

      let evaluatedHypotheses;
      try {
        const jsonMatch = assistantReply.match(/\[.*\]/s);
        if (jsonMatch) {
          evaluatedHypotheses = JSON.parse(jsonMatch[0]);
        } else {
          evaluatedHypotheses = JSON.parse(assistantReply);
        }
      } catch (parseError) {
        throw new Error("Failed to parse evaluated hypotheses JSON from the assistant's reply.");
      }

      if (evaluatedHypotheses.length !== hypotheses.length) {
        throw new Error(
          `Mismatch in number of evaluated hypotheses. Expected ${hypotheses.length}, but got ${evaluatedHypotheses.length}.`
        );
      }

      // rank -> score
      const rankToScore = (rank, total) => {
        const maxScore = 90;
        const minScore = 10;
        const scoreRange = maxScore - minScore;
        // 分数从高到低
        const score = maxScore - ((rank - 1) * scoreRange) / (total - 1);
        return Math.round(score);
      };

      setNodes((prevNodes) =>
        prevNodes.map((node) => {
          const evalHypo = evaluatedHypotheses.find((h) => h.id === node.id);
          if (evalHypo) {
            const totalHypotheses = hypotheses.length;
            const noveltyScore = rankToScore(evalHypo.novelty_rank, totalHypotheses);
            const feasibilityScore = rankToScore(evalHypo.feasibility_rank, totalHypotheses);
            const impactScore = rankToScore(evalHypo.impact_rank, totalHypotheses);

            return {
              ...node,
              noveltyScore,
              feasibilityScore,
              impactScore,
              noveltyReason: evalHypo.novelty_rank_reason || '(No reason provided)',
              feasibilityReason: evalHypo.feasibility_rank_reason || '(No reason provided)',
              impactReason: evalHypo.impact_rank_reason || '(No reason provided)',
            };
          }
          return node;
        })
      );
    } catch (err) {
      console.error('Error evaluating hypotheses:', err);
      setError(err.message);
    } finally {
      setIsEvaluating(false);
    }
  };

  // ============== 段落4：分析意图提交处理 (handleAnalysisIntentSubmit) ==============
  const handleAnalysisIntentSubmit = async (e) => {
    e.preventDefault();
    if (!analysisIntent.trim()) return;

    setIsGenerating(true);
    setError(null);

    try {
      const prompt = `Based on the following analysis intent and data, generate exactly three initial hypotheses. For each hypothesis, provide a two-word title and a detailed content. Return the hypotheses in JSON format as an array of objects with "title" and "content" fields.

Analysis Intent: ${analysisIntent}

Data: ${JSON.stringify(data_summary)}

Please provide only the JSON array in your response.`;

      const apiKey = process.env.REACT_APP_OPENAI_API_KEY;
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          model: 'gpt-4o',
          messages: [
            { role: 'system', content: 'You are a data scientist.' },
            { role: 'user', content: prompt },
          ],
          max_tokens: 500,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error.message);
      }

      const data = await response.json();
      const assistantReply = data.choices[0].message.content.trim();

      let hypothesesArray;
      try {
        const jsonMatch = assistantReply.match(/\[.*\]/s);
        if (jsonMatch) {
          hypothesesArray = JSON.parse(jsonMatch[0]);
        } else {
          hypothesesArray = JSON.parse(assistantReply);
        }
      } catch (parseError) {
        throw new Error("Failed to parse hypotheses JSON from the assistant's reply.");
      }

      const hypotheses = hypothesesArray.slice(0, 3);
      const hypothesesWithId = hypotheses.map((hyp) => {
        const id = generateUniqueId();
        return { id, ...hyp };
      });

      const updatedHypothesesList = [...hypothesesList, ...hypothesesWithId];
      setHypothesesList(updatedHypothesesList);

      // Root node
      const rootNode = {
        id: 'root',
        level: 0,
        title: analysisIntent,
        content: analysisIntent,
        type: 'root',
        x: 0,
        y: 0,
      };

      // Child nodes
      const childSpacing = 200;
      const totalWidth = (hypotheses.length - 1) * childSpacing;
      const startX = rootNode.x - totalWidth / 2;

      const childNodes = hypothesesWithId.map((hyp, i) => ({
        id: hyp.id,
        level: 1,
        title: hyp.title.trim(),
        content: hyp.content.trim(),
        type: 'complex',
        x: startX + i * childSpacing,
        y: rootNode.y + 150,
      }));

      const newNodes = [rootNode, ...childNodes];
      const newLinks = childNodes.map((nd) => ({ source: rootNode.id, target: nd.id }));
      setNodes(newNodes);
      setLinks(newLinks);

      setAnalysisIntent('');
      setIsAnalysisSubmitted(true);

      // 评估
      await evaluateHypotheses(updatedHypothesesList);
      setIsGenerating(false);
    } catch (err) {
      console.error('Error generating initial hypotheses:', err);
      setError(err.message);
      setIsGenerating(false);
    }
  };

  // ============== 段落5：生成子节点 (generateChildNodes) ==============
  const generateChildNodes = async () => {
    if (!selectedNode) return;
    setIsGenerating(true);
    setError(null);

    try {
      const prompt = `Based on the following node content and data, generate exactly three new hypotheses. For each hypothesis, provide a two-word title and a detailed content. Return the hypotheses in JSON format as an array of objects with "title" and "content" fields.

Node Content: ${selectedNode.content}

Data: ${JSON.stringify(data_summary)}

Additional Context: ${userInput}

Please provide only the JSON array in your response.`;

      const apiKey = process.env.REACT_APP_OPENAI_API_KEY;
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          model: 'gpt-4o',
          messages: [
            { role: 'system', content: 'You are a data scientist.' },
            { role: 'user', content: prompt },
          ],
          max_tokens: 500,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error.message);
      }

      const data = await response.json();
      const assistantReply = data.choices[0].message.content.trim();

      let hypothesesArray;
      try {
        const jsonMatch = assistantReply.match(/\[.*\]/s);
        if (jsonMatch) {
          hypothesesArray = JSON.parse(jsonMatch[0]);
        } else {
          hypothesesArray = JSON.parse(assistantReply);
        }
      } catch (parseError) {
        throw new Error("Failed to parse hypotheses JSON from the assistant's reply.");
      }

      const hypotheses = hypothesesArray.slice(0, 3);
      const newHypothesesWithId = hypotheses.map((hyp) => {
        const id = generateUniqueId();
        return { id, ...hyp };
      });

      const updatedHypothesesList = [...hypothesesList, ...newHypothesesWithId];
      setHypothesesList(updatedHypothesesList);

      // 布局
      const childSpacing = 200;
      const totalWidth = (hypotheses.length - 1) * childSpacing;
      const startX = selectedNode.x - totalWidth / 2;

      const newNodes = newHypothesesWithId.map((hyp, i) => ({
        id: hyp.id,
        level: selectedNode.level + 1,
        title: hyp.title.trim(),
        content: hyp.content.trim(),
        type: 'complex',
        x: startX + i * childSpacing + Math.random() * 20 - 10,
        y: selectedNode.y + 150 + Math.random() * 20 - 10,
      }));

      const newLinks = newNodes.map((nd) => ({ source: selectedNode.id, target: nd.id }));
      setNodes((prev) => [...prev, ...newNodes]);
      setLinks((prev) => [...prev, ...newLinks]);

      // 评估
      await evaluateHypotheses(updatedHypothesesList);

      setIsGenerating(false);
      setUserInput('');
    } catch (err) {
      console.error('Error generating hypotheses:', err);
      setError(err.message);
      setIsGenerating(false);
    }
  };

  // ============== 段落6：根据拖拽修改假设 (modifyHypothesisBasedOnModifications) ==============
  // ============== 段落6：修改开始 ==============
  const modifyHypothesisBasedOnModifications = async (
    originalNode,
    ghostNode,
    modifications,
    behindNode
  ) => {
    setError(null);

    try {
      setIsGenerating(true);

      // 根据每个指标和对应的 increase/decrease，拼接更灵活的描述
      let instructionLines = [];
      const behindNodeContent = behindNode ? behindNode.content : '(No behind node found)';

      modifications.forEach((mod) => {
        const metricName =
          mod.metric === 'noveltyScore'
            ? 'Novelty'
            : mod.metric === 'feasibilityScore'
            ? 'Feasibility'
            : 'Impact';
        const direction = mod.direction === 'increase' ? 'increasing' : 'decreasing';
        const line = `by ${direction} its ${metricName} with ${
          direction === 'increasing' ? 'more' : 'less'
        } ${metricName.toLowerCase()} than this hypothesis:\n${behindNodeContent}`;
        instructionLines.push(line);
      });

      const instructionsText = instructionLines
        .map((line, idx) => {
          if (idx === 0) return `Modify this hypothesis ${line}`;
          return `And ${line}`;
        })
        .join('\n\n');

      const prompt = `
  ${instructionsText}

  Keep its main idea intact, but focus on adjusting the degree of novelty, feasibility, and/or impact as requested.

  Original hypothesis:
  ${originalNode.content}

  Dataset summary: ${JSON.stringify(data_summary)}

  Please provide only the modified hypothesis content in your response.
      `.trim();


      console.log('modify prompt:', prompt);

      const apiKey = process.env.REACT_APP_OPENAI_API_KEY;
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          model: 'gpt-4o',
          messages: [
            { role: 'system', content: 'You are a data scientist.' },
            { role: 'user', content: prompt },
          ],
          max_tokens: 500,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error.message);
      }

      const data = await response.json();
      const assistantReply = data.choices[0].message.content.trim();

      ghostNode.content = assistantReply;
      ghostNode.isModified = true;
      ghostNode.previousState = originalNode;
      ghostNode.isGhost = false;

      const newHypothesis = {
        id: ghostNode.id,
        title: ghostNode.title,
        content: assistantReply,
      };
      setHypothesesList((prevList) => [...prevList, newHypothesis]);

      setNodes((prevNodes) => prevNodes.map((n) => (n.id === ghostNode.id ? ghostNode : n)));
      setIsGenerating(false);
      setIsEvaluating(true);

      await evaluateHypotheses([...hypothesesList, newHypothesis]);
    } catch (err) {
      console.error('Error modifying hypothesis:', err);
      setError(err.message);
      setIsGenerating(false);
    } finally {
      setIsEvaluating(false);
    }
  };
  // ============== 段落6：修改结束 ==============

  // ============== 段落7：节点点击/悬浮/离开事件处理 ==============
  const handleNodeClick = (node) => {
