      evaluationLinks.forEach((link) => {
        const source = nodes.find((n) => n.id === link.source);
        const target = nodes.find((n) => n.id === link.target);
        if (source && target) {
          g.append('line')
            .attr('x1', xScale(source[xAxisMetric]))
            .attr('y1', yScale(source[yAxisMetric]))
