          if (selectedNode?.id === d.id) return 4;
          else if (hoveredNode?.id === d.id) return 3;
          else return 2;
        });
    }
  }, [
    showTree,
    nodes,
    links,
    selectedNode,
    hoveredNode,
    xAxisMetric,
    yAxisMetric,
    isGenerating,
    isEvaluating,
  ]);

