        </div>
      </div>
    );
  };

// ============== 段落9.1：HypothesisFactorsAndScoresCard（完整函数） ==============
const HypothesisFactorsAndScoresCard = ({ node, isEvaluating, showAfter }) => {
  // 新 color scheme
  const noveltyColor = '#618685';
  const feasibilityColor = '#50394c';
  const impactColor = '#4040a1';

  const hasBefore = !!node.previousState;

  if (isEvaluating) {
    return (
      <div style={{ marginBottom: '16px' }}>
        <p style={{ fontSize: '0.875rem', color: '#6b7280' }}>
          Scores will be displayed once evaluation is complete.
        </p>
      </div>
    );
  }

  // 根据 showAfter 判断当前显示的 score / reason
  const noveltyScore = showAfter || !hasBefore ? node.noveltyScore : node.previousState.noveltyScore;
  const feasibilityScore = showAfter || !hasBefore ? node.feasibilityScore : node.previousState.feasibilityScore;
  const impactScore = showAfter || !hasBefore ? node.impactScore : node.previousState.impactScore;

  const noveltyReason = showAfter || !hasBefore ? node.noveltyReason : node.previousState.noveltyReason;
  const feasibilityReason = showAfter || !hasBefore ? node.feasibilityReason : node.previousState.feasibilityReason;
  const impactReason = showAfter || !hasBefore ? node.impactReason : node.previousState.impactReason;

  // 去掉外层卡片，直接渲染三个要素
  return (
    <>
      {/* Novelty */}
      <FactorBlock
        label="Novelty"
        color={noveltyColor}
        score={noveltyScore}
        reason={noveltyReason}
        beforeVal={hasBefore ? node.previousState.noveltyScore : null}
        afterVal={node.noveltyScore}
        showAfter={showAfter}
      />

      {/* Feasibility */}
      <FactorBlock
        label="Feasibility"
        color={feasibilityColor}
        score={feasibilityScore}
        reason={feasibilityReason}
        beforeVal={hasBefore ? node.previousState.feasibilityScore : null}
        afterVal={node.feasibilityScore}
        showAfter={showAfter}
      />

      {/* Impact */}
      <FactorBlock
        label="Impact"
        color={impactColor}
        score={impactScore}
        reason={impactReason}
        beforeVal={hasBefore ? node.previousState.impactScore : null}
        afterVal={node.impactScore}
        showAfter={showAfter}
      />
    </>
  );
};

const FactorBlock = ({ label, color, score, reason, beforeVal, afterVal, showAfter }) => {
  const getArrowDiv = (beforeVal, afterVal) => {
    const b = parseFloat(beforeVal);
    const a = parseFloat(afterVal);
    if (isNaN(b) || isNaN(a)) return null;

    if (a > b) {
      // green arrow
      return (
        <svg
          width="17"
          height="17"
          viewBox="0 0 17 17"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          style={{ margin: '0 6px' }}
        >
          <g>
            <path
              d="M7.01221 4.32837L13.0122 4.32837L13.0122 10.3284"
              stroke="#37A219"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
            <path
              d="M4.52682 12.8137L13.0121 4.32845"
              stroke="#37A219"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </g>
        </svg>
      );
    } else if (a < b) {
      // red arrow
      return (
        <svg
          width="17"
          height="17"
          viewBox="0 0 17 17"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          style={{ margin: '0 6px' }}
        >
          <g>
            <path
              d="M12.9168 7.19702V13.197H6.91684"
              stroke="#A21919"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
            <path
              d="M4.43146 4.71167L12.9167 13.1969"
              stroke="#A21919"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </g>
        </svg>
      );
    } else {
      // 相同 -> 横线
      return (
        <svg
          width="16"
          height="17"
          viewBox="0 0 16 17"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          style={{ margin: '0 6px' }}
        >
          <g id="Frame">
            <path
              id="Vector"
              d="M2.97964 8.17383L13.0204 8.17383"
              stroke="#A2A2A2"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </g>
        </svg>
      );
    }
  };

  const hasBefore = beforeVal !== null;
  let scoreDisplay;
  if (hasBefore) {
    scoreDisplay = (
      <div style={{ display: 'flex', alignItems: 'center', fontSize: '1rem', color: '#374151' }}>
        <span>{beforeVal}</span>
        {getArrowDiv(beforeVal, afterVal)}
        <span>{afterVal}</span>
      </div>
    );
  } else {
    scoreDisplay = (
      <div style={{ fontSize: '1rem', color: '#374151' }}>
        Score: {score}
      </div>
    );
  }

  return (
    <div
      style={{
        backgroundColor: '#F9FAFB',
        border: '1px solid #E5E7EB',
        borderRadius: '8px',
        padding: '16px',
        marginBottom: '16px',
      }}
    >
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
        <div
          style={{
            width: '12px',
            height: '12px',
            borderRadius: '50%',
            backgroundColor: color,
            marginRight: '8px',
          }}
        />
        <div style={{ fontSize: '1rem', fontWeight: '600', color }}>{label}</div>{scoreDisplay}
      </div>
      <div
        style={{
          fontSize: '0.875rem',
          color: '#111827',
          lineHeight: '1.4',
          marginBottom: '12px',
          whiteSpace: 'pre-wrap',
        }}
      >
        {reason || '(No reason provided)'}
      </div>
    </div>
  );
};

