import React, { useState } from 'react';

function WikiSearchDemo() {
  const [question, setQuestion] = useState('');
  const [responseData, setResponseData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const callApi = async () => {
    if (!question.trim()) {
      alert('Please enter a question.');
      return;
    }

    setLoading(true);
    setError(null);
    setResponseData(null);

    try {
      const response = await fetch('/api/wikipedia', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          query: [question],
          rerank: true,
          num_blocks_to_rerank: 10,
          num_blocks: 1,
          search_filters: [
            {
              field_name: 'language',
              filter_type: 'eq',
              field_value: 'en'
            }
          ]
        })
      });

      if (!response.ok) {
        throw new Error(`Error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      setResponseData(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Wikipedia Search API Demo</h1>
      <div style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Enter your question..."
          style={{ padding: '0.5rem', width: '300px' }}
        />
        <button onClick={callApi} disabled={loading} style={{ marginLeft: '0.5rem' }}>
          {loading ? 'Loading...' : 'Call API'}
        </button>
      </div>
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}
      {responseData && (
        <div>
          <h2>Response:</h2>
          <pre style={{ background: '#f4f4f4', padding: '1rem' }}>
            {JSON.stringify(responseData, null, 2)}
          </pre>
          {responseData[0] &&
            responseData[0].results &&
            responseData[0].results.length > 0 && (
              <div style={{ marginTop: '1rem' }}>
                <h3>Reply Content:</h3>
                <p>{responseData[0].results[0].content}</p>
              </div>
            )}
        </div>
      )}
    </div>
  );
}

export default WikiSearchDemo;
