import React, { useState } from 'react';

function EventItem({ event }) {
  const [hover, setHover] = useState(false);

  // 悬浮提示的样式，调整了 top 和 transform 以使其更靠近日期
  const tooltipStyle = {
    position: 'absolute',
    top: '-70px', // 调整此值让浮层更靠近/更远日期
    left: '50%',
    transform: 'translateX(-50%)',
    padding: '8px 12px',
    backgroundColor: '#fff',
    border: '1px solid #ccc',
    borderRadius: '8px',
    whiteSpace: 'nowrap',
    zIndex: 10,
    boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
    minWidth: '220px',
  };

  return (
    <div
      style={{
        position: 'relative',
        marginBottom: '40px', // 缩小事件间的垂直间距，使节点更紧凑
        cursor: 'pointer',
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      {/* 蓝色圆点，left 值微调以使其中心更好贴合时间线 */}
      <div
        style={{
          position: 'absolute',
          left: '-7px', // 如果仍有偏移，可改为 -8 或 -6 等
          top: '0',
          width: '16px',
          height: '16px',
          borderRadius: '50%',
          backgroundColor: '#007BFF',
        }}
      />

      {/* 日期与标题部分（位于时间线右侧） */}
      <div style={{ marginLeft: '24px' }}>
        {/* 日期外框 */}
        <div
          style={{
            border: '2px solid #ddd',
            borderRadius: '20px',
            padding: '10px 16px',
            background: '#fff',
            fontSize: '15px',
            minWidth: '130px',
            textAlign: 'center',
            marginBottom: '6px',
          }}
        >
          {event.date}
        </div>
        {/* 事件标题 */}
        <div style={{ fontSize: '14px', color: '#333', textAlign: 'center' }}>
          <strong>{event.title}</strong>
        </div>
      </div>

      {/* Hover 时显示的详细信息浮层 */}
      {hover && (
        <div style={tooltipStyle}>
          <strong style={{ display: 'block', marginBottom: '6px' }}>
            {event.title}
          </strong>
          <div style={{ fontSize: '13px', color: '#333' }}>{event.detail}</div>
        </div>
      )}
    </div>
  );
}

function Timeline({ wave }) {
  // 时间线高度，可根据事件数量或UI需求进行调节
  const timelineHeight = 600;
  const totalEvents = wave.events.length;

  return (
    <div style={{ flex: '1', margin: '0 20px' }}>
      {/* 浪潮标题与时期 */}
      <h2 style={{ fontSize: '16px', marginBottom: '5px', textAlign: 'center' }}>
        {wave.title}
      </h2>
      <div style={{ fontSize: '14px', color: '#666', marginBottom: '20px', textAlign: 'center' }}>
        {wave.period}
      </div>

      {/* 垂直时间线（略微加宽或缩小，都可微调） */}
      <div
        style={{
          position: 'relative',
          width: '2px',
          backgroundColor: '#bbb',
          height: timelineHeight + 'px',
          marginLeft: '0',
        }}
      >
        {/* 事件列表 */}
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {wave.events.map((event, index) => {
            // 计算事件节点在时间线上的垂直位置
            const pos =
              totalEvents === 1
                ? timelineHeight / 2
                : (index / (totalEvents - 1)) * timelineHeight;

            return (
              <div
                key={index}
                style={{
                  position: 'absolute',
                  top: pos + 'px',
                  width: '220px', // 控制每个事件在水平方向的可用空间
                }}
              >
                <EventItem event={event} />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

function Showcase() {
  // 仅保留一组示例数据以示范；你可根据需要替换为更多浪潮与事件
  const waves = [
    {
      title: '个人电脑',
      period: '(1979-1982)',
      events: [
        {
          date: '1979-08-12',
          title: 'VisiCalc 发布',
          detail: '首款电子表格软件 VisiCalc 面向 Apple II 推出。',
        },
        {
          date: '1980-08-25',
          title: 'IBM PC 立项',
          detail: 'IBM 决定正式组建个人电脑部门，代号“Project Chess”。',
        },
        {
          date: '1981-08-12',
          title: 'IBM PC 发布',
          detail: 'IBM 5150 机型问世，搭载 Intel 8088 CPU 和 MS-DOS。',
        },
        {
          date: '1982-02-16',
          title: 'Compaq 创立',
          detail: '康柏公司成立，专注于生产 IBM PC 兼容机。',
        },
      ],
    },
  ];

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ textAlign: 'center' }}>科技浪潮早期阶段互动时间线</h1>

      {/* 多列并排布局 */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-around',
          flexWrap: 'wrap',
          marginTop: '40px',
        }}
      >
        {waves.map((wave, index) => (
          <Timeline key={index} wave={wave} />
        ))}
      </div>
    </div>
  );
}

export default Showcase;
