import React, { useState } from 'react';

/**
 * 单个事件节点组件
 */
function EventItem({ event }) {
  const [hover, setHover] = useState(false);

  // 悬浮提示（Hover Tooltip）的样式
  const tooltipStyle = {
    position: 'absolute',
    top: '-60px',  // 控制浮层在垂直方向上的位置
    left: '50%',
    transform: 'translateX(-50%)',
    padding: '8px 12px',
    backgroundColor: '#fff',
    border: '1px solid #ccc',
    borderRadius: '8px',
    whiteSpace: 'nowrap',
    zIndex: 10,
    boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
    minWidth: '220px',
  };

  return (
    <div
      style={{
        position: 'relative',
        marginBottom: '20px',
        cursor: 'pointer',
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      {/* 时间线左侧的蓝色圆点 */}
      <div
        style={{
          position: 'absolute',
          top: '0',
          width: '16px',
          height: '16px',
          borderRadius: '50%',
          backgroundColor: '#007BFF',
        }}
      />

      {/* 日期与标题部分（位于时间线右侧） */}
      <div style={{ marginLeft: '24px' }}>
        {/* 日期外框 */}
        <div
          style={{
            border: '2px solid #ddd',
            borderRadius: '20px',
            padding: '8px 14px',
            background: '#fff',
            fontSize: '15px',
            minWidth: '120px',
            textAlign: 'center',
            marginBottom: '4px',
          }}
        >
          {event.date}
        </div>
        {/* 事件标题（始终可见） */}
        <div style={{ fontSize: '14px', color: '#333', textAlign: 'center' }}>
          <strong>{event.title}</strong>
        </div>
      </div>

      {/* 悬浮时显示的详细信息浮层 */}
      {hover && (
        <div style={tooltipStyle}>
          <strong style={{ display: 'block', marginBottom: '6px' }}>
            {event.title}
          </strong>
          <div style={{ fontSize: '13px', color: '#333' }}>{event.detail}</div>
        </div>
      )}
    </div>
  );
}

/**
 * 单条浪潮的时间线组件
 * 在此组件中根据事件的时间差来确定每个事件的垂直位置
 */
function Timeline({ wave }) {
  // 时间线总高度（像素）
  const timelineHeight = 400;

  // 将浪潮中的所有事件日期解析为时间戳，方便后续计算
  const timestamps = wave.events.map(e => new Date(e.date).getTime());

  // 找出最早和最晚时间点
  const earliestTime = Math.min(...timestamps);
  const latestTime = Math.max(...timestamps);

  // 总的时间跨度（毫秒）
  const totalTimeSpan = latestTime - earliestTime;

  // 如果所有日期都相同，则避免除以 0
  const safeTimeSpan = totalTimeSpan === 0 ? 1 : totalTimeSpan;

  return (
    <div style={{ flex: '1', margin: '0 20px' }}>
      {/* 浪潮标题与时期 */}
      <h2 style={{ fontSize: '16px', marginBottom: '5px', textAlign: 'center' }}>
        {wave.title}
      </h2>
      <div style={{ fontSize: '14px', color: '#666', marginBottom: '20px', textAlign: 'center' }}>
        {wave.period}
      </div>

      {/* 垂直时间线 */}
      <div
        style={{
          position: 'relative',
          width: '2px',
          backgroundColor: '#bbb',
          height: timelineHeight + 'px',
          marginLeft: '0',
        }}
      >
        {/* 事件列表：根据时间先后按比例分布在时间线中 */}
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {wave.events.map((event, index) => {
            const currentTime = new Date(event.date).getTime();
            const offset = currentTime - earliestTime;
            const ratio = offset / safeTimeSpan; // 0 ~ 1
            const pos = ratio * timelineHeight;  // 在时间线中的像素位置

            return (
              <div
                key={index}
                style={{
                  position: 'absolute',
                  top: pos + 'px',
                  width: '220px', // 控制每个事件在水平方向的可用空间
                }}
              >
                <EventItem event={event} />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

/**
 * 主容器组件：包含多个浪潮的时间线
 */
function Showcase() {
  // 示例数据：五大浪潮，每个浪潮包含若干确切日期的事件
  // 这里选取了部分示例事件以演示"时间差"分布
  const waves = [
    {
      title: '个人电脑',
      period: '(1979-1982)',
      events: [
        {
          date: '1979-08-12',
          title: 'VisiCalc 发布',
          detail: '首款电子表格软件 VisiCalc 面向 Apple II 推出。',
        },
        {
          date: '1980-08-25',
          title: 'IBM PC 立项',
          detail: 'IBM 决定正式组建个人电脑部门，代号“Project Chess”。',
        },
        {
          date: '1981-08-12',
          title: 'IBM PC 发布',
          detail: 'IBM 5150 机型问世，搭载 Intel 8088 CPU 和 MS-DOS。',
        },
        {
          date: '1982-02-16',
          title: 'Compaq 创立',
          detail: '康柏公司成立，专注于生产 IBM PC 兼容机。',
        },
      ],
    },
    {
      title: '互联网',
      period: '(1994-1997)',
      events: [
        {
          date: '1994-04-04',
          title: 'Netscape 创立',
          detail: 'Mosaic 团队成员创立 Netscape，专注浏览器研发。',
        },
        {
          date: '1995-07-05',
          title: 'Amazon 上线',
          detail: '杰夫·贝索斯创立的电商网站 Amazon.com 开始运营。',
        },
        {
          date: '1996-07-04',
          title: 'Hotmail 推出',
          detail: '免费网络邮件服务 Hotmail 上线，迅速风靡全球。',
        },
        {
          date: '1997-03-02',
          title: 'AOL 收购 CompuServe',
          detail: 'AOL 以 1.2 亿美元收购 CompuServe 网络业务。',
        },
      ],
    },
    {
      title: '智能手机',
      period: '(2007-2010)',
      events: [
        {
          date: '2007-01-09',
          title: 'iPhone 发布',
          detail: '苹果首款 iPhone 在 Macworld 大会上正式发布。',
        },
        {
          date: '2007-06-29',
          title: 'iPhone 开售',
          detail: '初代 iPhone 在美国上市，引发消费者排队抢购。',
        },
        {
          date: '2008-09-23',
          title: 'Android 1.0 发布',
          detail: '搭载 Android 系统的首款手机 HTC Dream 上市。',
        },
        {
          date: '2009-03-17',
          title: 'iPhone OS 3.0 公布',
          detail: '苹果发布 iPhone OS 3.0，新增推送通知、复制粘贴等功能。',
        },
      ],
    },
    {
      title: '电动车',
      period: '(2015-2018)',
      events: [
        {
          date: '2015-01-12',
          title: 'Chevrolet Bolt 概念车',
          detail: '通用在北美车展发布 Bolt EV 概念车，主打平价长续航。',
        },
        {
          date: '2016-03-31',
          title: 'Tesla Model 3 亮相',
          detail: '特斯拉举行 Model 3 发布会，引发大量预订。',
        },
        {
          date: '2017-07-28',
          title: 'Model 3 首批交付',
          detail: '特斯拉向首批 30 位用户交付 Model 3 量产车。',
        },
      ],
    },
    {
      title: '生成式 AI',
      period: '(2019-2022)',
      events: [
        {
          date: '2019-02-14',
          title: 'OpenAI GPT-2 公布',
          detail: 'OpenAI 宣布 GPT-2 模型，但仅限“受限发布”。',
        },
        {
          date: '2020-05-28',
          title: 'GPT-3 论文发表',
          detail: '“Language Models are Few-Shot Learners” 论文上载至 arXiv。',
        },
        {
          date: '2022-03-15',
          title: 'GPT-3.5 发布',
          detail: 'EleutherAI 发布开源大模型 GPT-Neo，推进社区合作。',
        },
        {
          date: '2022-11-30',
          title: 'ChatGPT 上线',
          detail: 'OpenAI 推出对话式 AI ChatGPT，短时间内用户量激增。',
        },
      ],
    },
  ];

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ textAlign: 'center' }}>科技浪潮早期时间线</h1>

      {/* 多列并排布局 */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-around',
          flexWrap: 'wrap',
          marginTop: '40px',
        }}
      >
        {waves.map((wave, index) => (
          <Timeline key={index} wave={wave} />
        ))}
      </div>
    </div>
  );
}

export default Showcase;
