import React, { useState, useEffect, useRef } from 'react';

/** 生成随机 ID 的小工具函数，用于给新添加的事件分配稳定的 event.id */
function generateUniqueId() {
  return Math.random().toString(36).substr(2, 9);
}

/** 
 * Helper function to generate a curved path (cubic Bezier curve example)
 */
function generateCurvePath(x1, y1, x2, y2) {
  const cx = (x1 + x2) / 2;
  return `M ${x1},${y1} C ${cx},${y1} ${cx},${y2} ${x2},${y2}`;
}

/**
 * Modal Component for adding new events
 */
function AddEventModal({ isOpen, onClose, onSubmit, waveIndex }) {
  const [date, setDate] = useState('');
  const [title, setTitle] = useState('');
  const [detail, setDetail] = useState('');

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (date && title) {
      onSubmit(waveIndex, { date, title, detail });
      onClose();
    }
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
    }}>
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '8px',
        width: '400px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
      }}>
        <h3 style={{ marginTop: 0 }}>Add New Event</h3>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Date (YYYY-MM-DD):</label>
            <input 
              type="date" 
              value={date} 
              onChange={(e) => setDate(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd'
              }}
              required
            />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Title:</label>
            <input 
              type="text" 
              value={title} 
              onChange={(e) => setTitle(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd'
              }}
              required
            />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Details:</label>
            <textarea 
              value={detail} 
              onChange={(e) => setDetail(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd',
                minHeight: '100px'
              }}
            />
          </div>
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
            <button 
              type="button" 
              onClick={onClose}
              style={{
                padding: '8px 16px',
                border: '1px solid #ddd',
                borderRadius: '4px',
                backgroundColor: '#f1f1f1',
                cursor: 'pointer'
              }}
            >
              Cancel
            </button>
            <button 
              type="submit"
              style={{
                padding: '8px 16px',
                border: 'none',
                borderRadius: '4px',
                backgroundColor: '#007BFF',
                color: 'white',
                cursor: 'pointer'
              }}
            >
              Add Event
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

/**
 * ConnectionLine 组件：绘制正式的红色连接线，
 * hover 时加粗并增加 glow 效果，点击时弹出确认后删除该连接。
 */
function ConnectionLine({ fromPos, toPos, reason, onDelete }) {
  const [hovered, setHovered] = useState(false);
  const strokeColor = hovered ? 'rgba(220,20,60,0.9)' : 'rgba(255,0,0,0.4)';
  const strokeWidth = hovered ? 3 : 2;
  const pathData = generateCurvePath(fromPos.x, fromPos.y, toPos.x, toPos.y);

  return (
    <g
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      onClick={(e) => {
        e.stopPropagation();
        if (window.confirm('Delete this connection?')) {
          onDelete();
        }
      }}
      style={{ cursor: 'pointer' }}
    >
      <path 
        d={pathData} 
        stroke={strokeColor} 
        strokeWidth={strokeWidth} 
        fill="none" 
        style={{ 
          pointerEvents: 'all',
          filter: hovered ? 'drop-shadow(0 0 3px rgba(255,0,0,0.3))' : 'none',
        }} 
      />
      <text
        x={(fromPos.x + toPos.x) / 2}
        y={(fromPos.y + toPos.y) / 2 - 5}
        fill={strokeColor}
        fontSize={hovered ? 14 : 12}
        fontWeight={hovered ? 'bold' : 'normal'}
        textAnchor="middle"
        style={{ 
          pointerEvents: 'none',
          filter: hovered ? 'drop-shadow(0 1px 2px rgba(255,255,255,0.8))' : 'none',
        }}
      >
        {reason}
      </text>
    </g>
  );
}

/**
 * 生成一个上下交错的偏移数组，例如：
 *   - 如果 count=1，返回 [0]
 *   - 如果 count=2，返回 [-35, 35]
 *   - 如果 count=3，返回 [-70, 0, 70]
 *   - 如果 count=4，返回 [-105, -35, 35, 105]
 *   - ...
 */
function getOffsets(count, gap = 70) {
  const offsets = [];
  // 让最中间的那个为 0，上下对称
  const half = Math.floor(count / 2);
  for (let i = 0; i < count; i++) {
    const diff = i - half;
    // 如果是偶数个，总体会略偏上一点，但一般无伤大雅
    offsets.push(diff * gap);
  }
  // 如果是偶数个，可以稍微再调一下让它们对称
  // 例如 count=2 => [-35,35], count=4 => [-105,-35,35,105] ...
  if (count % 2 === 0) {
    // 把每个偏移都加上 gap/2
    offsets.forEach((val, idx) => {
      offsets[idx] = val + gap / 2;
    });
  }
  return offsets;
}

// 全局 state 来管理当前悬停的事件ID，确保只有一个事件的 tooltip 可以显示
const GlobalHoverState = {
  currentHoverId: null,
  setCurrentHover: (id) => {
    GlobalHoverState.currentHoverId = id;
  },
  clearCurrentHover: () => {
    GlobalHoverState.currentHoverId = null;
  },
  isHovered: (id) => {
    return GlobalHoverState.currentHoverId === id;
  }
};

/**
 * EventGroup 组件：
 *   - 不再做原先"展开/折叠"或"左右并排"等区分，而是所有事件都上下交错排布。
 *   - 组在时间线上只占一个纵向点（根据最早事件计算）。
 *   - 每个事件是一个卡片，标题蓝色、日期在其下方，hover 显示详情，z-index 足够大以免被遮挡。
 */
function EventGroup({ events, waveIndex, registerPosition, onEventClick, onDelete, scrollPos, groupTop }) {
  // 按日期先后排序
  const sortedEvents = [...events].sort((a, b) => new Date(a.date) - new Date(b.date));
  const count = sortedEvents.length;

  // 生成上下交错的垂直偏移
  const offsets = getOffsets(count, 70);

  // 需要为每个事件单独测量位置，所以用多个 ref
  const cardRefs = useRef([]);
  const [hoveredEvent, setHoveredEvent] = useState(null);

  // 确保只有一个悬停工具提示显示
  useEffect(() => {
    // 清除当前悬停状态
    return () => {
      if (hoveredEvent) {
        GlobalHoverState.clearCurrentHover();
      }
    };
  }, [hoveredEvent]);

  useEffect(() => {
    sortedEvents.forEach((ev, i) => {
      if (cardRefs.current[i]) {
        const rect = cardRefs.current[i].getBoundingClientRect();
        const centerPos = {
          x: rect.left + rect.width / 2,
          y: rect.top + rect.height / 2,
        };
        const eventId = `${waveIndex}-${ev.id}`;
        registerPosition(eventId, centerPos);
      }
    });
  }, [sortedEvents, waveIndex, registerPosition, scrollPos]);

  // 创建覆盖整个页面的全屏容器，用于在其上显示事件悬停提示
  // 这样就能确保工具提示始终显示在顶层
  const TooltipPortal = () => {
    if (!hoveredEvent) return null;

    const ev = sortedEvents.find(e => e.id === hoveredEvent);
    if (!ev || !ev.detail) return null;

    const hoverRect = cardRefs.current[sortedEvents.findIndex(e => e.id === hoveredEvent)]?.getBoundingClientRect();
    if (!hoverRect) return null;

    // 创建工具提示，确保它定位在正确的位置，始终在顶层
    return (
      <div id="tooltip-portal" style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 10000, // 非常高的z-index确保在所有内容之上
      }}>
        <div style={{
          position: 'absolute',
          top: hoverRect.bottom + 'px',
          left: (hoverRect.left + (hoverRect.width / 2) - 100) + 'px',
          width: '200px',
          backgroundColor: '#fff',
          border: '1px solid #ddd',
          borderRadius: '6px',
          boxShadow: '0 4px 8px rgba(0,0,0,0.15)',
          padding: '8px',
          fontSize: '12px',
        }}>
          <div style={{ fontWeight: 'bold', marginBottom: '4px' }}>{ev.title}</div>
          <div>{ev.detail}</div>
        </div>
      </div>
    );
  };

  return (
    <>
      <div
        style={{
          position: 'absolute',
          top: groupTop,
          left: 0,
          width: '280px',
          zIndex: 500, // 确保组容器不会被遮挡
        }}
      >
        {sortedEvents.map((ev, i) => {
          const evId = `${waveIndex}-${ev.id}`;
          const offsetTop = offsets[i];
          const isHovered = hoveredEvent === ev.id;

          return (
            <div
              key={ev.id}
              ref={(el) => (cardRefs.current[i] = el)}
              style={{
                position: 'absolute',
                left: '25px', // 靠近时间线
                top: offsetTop + 'px',
                width: '160px',
                padding: '10px',
                border: '2px solid #ddd',
                borderRadius: '8px',
                backgroundColor: '#fff',
                boxShadow: isHovered
                  ? '0 4px 8px rgba(0,0,0,0.15)'
                  : '0 2px 4px rgba(0,0,0,0.08)',
                cursor: 'pointer',
                transition: 'all 0.2s ease',
                zIndex: isHovered ? 502 : 501, // hover 时略微更高但仍在工具提示之下
              }}
              onMouseEnter={() => {
                setHoveredEvent(ev.id);
                GlobalHoverState.setCurrentHover(evId);
              }}
              onMouseLeave={() => {
                setHoveredEvent(null);
                GlobalHoverState.clearCurrentHover();
              }}
              onClick={() => onEventClick(evId)}
            >
              {/* 标题 */}
              <div style={{ 
                fontWeight: 'bold', 
                marginBottom: '4px',
                fontSize: '14px',
                color: '#007BFF',
              }}>
                {ev.title}
              </div>
              {/* 日期 */}
              <div style={{ 
                fontSize: '13px', 
                marginBottom: '6px', 
                color: '#555' 
              }}>
                {ev.date}
              </div>

              {/* 删除按钮 */}
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  onDelete(ev.id);
                }}
                style={{
                  position: 'absolute',
                  top: '6px',
                  right: '6px',
                  background: 'transparent',
                  border: 'none',
                  color: isHovered ? '#ff3333' : 'rgba(255,0,0,0.6)',
                  fontSize: '14px',
                  cursor: 'pointer',
                  transition: 'color 0.2s ease',
                }}
              >
                ×
              </button>
            </div>
          );
        })}
      </div>

      {/* 渲染工具提示到顶层 */}
      <TooltipPortal />
    </>
  );
}

/**
 * Timeline 组件：渲染单条时间线，包括顶部标题、"+" 按钮、时间段说明及垂直事件列表
 */
function Timeline({
  wave,
  waveIndex,
  registerPosition,
  onEventClick,
  onAddEvent,
  onDeleteEvent,
  scrollPos,
}) {
  const timelineHeight = 1600;

  // 获取时间轴的起始和结束时间
  const timestamps = wave.events.map((e) => new Date(e.date).getTime());
  const firstEventTime = timestamps.length > 0 ? Math.min(...timestamps) : Date.now();
  const firstEventDate = new Date(firstEventTime);
  const timelineStart = new Date(firstEventDate.getFullYear(), 0, 1).getTime();

  // 固定为六年半的时间跨度（毫秒）
  const fiveYearsInMs = 8 * 365.25 * 24 * 60 * 60 * 1000;
  const timelineEndTime = timelineStart + fiveYearsInMs;
  const safeTimeSpan = timelineEndTime - timelineStart;

  // 按日期排序所有事件
  const sortedEvents = [...wave.events].sort((a, b) => new Date(a.date) - new Date(b.date));

  // 将接近的事件分组（时间差 ≤ 3 个月）
  const THREE_MONTHS_MS = 3 * 30 * 24 * 60 * 60 * 1000;
  const eventGroups = [];
  let currentGroup = [];

  sortedEvents.forEach((event, index) => {
    if (index === 0) {
      currentGroup.push(event);
    } else {
      const prevTime = new Date(sortedEvents[index - 1].date).getTime();
      const currTime = new Date(event.date).getTime();

      if (currTime - prevTime <= THREE_MONTHS_MS) {
        currentGroup.push(event);
      } else {
        eventGroups.push([...currentGroup]);
        currentGroup = [event];
      }
    }
  });
  if (currentGroup.length > 0) {
    eventGroups.push(currentGroup);
  }

  return (
    <div style={{ 
      flex: '1', 
      margin: '0 20px', 
      position: 'relative',
      padding: '15px',
      backgroundColor: 'rgba(255,255,255,0.7)',
      borderRadius: '8px',
      boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
    }}>
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '2px',
        borderBottom: '1px solid #eee',
        paddingBottom: '2px',
      }}>
        <h2 style={{ 
          fontSize: '18px', 
          margin: 0,
          color: '#2c3e50',
          fontWeight: '600',
        }}>
          {wave.title}
        </h2>
        <button
          onClick={() => onAddEvent(waveIndex)}
          style={{
            width: '30px',
            height: '30px',
            borderRadius: '50%',
            backgroundColor: '#007BFF',
            border: 'none',
            color: '#fff',
            fontWeight: 'bold',
            cursor: 'pointer',
            zIndex: 10,
            boxShadow: '0 2px 5px rgba(0,123,255,0.4)',
            transition: 'all 0.2s ease',
          }}
          title="Add Event"
        >
          +
        </button>
      </div>
      <div style={{ 
        fontSize: '14px', 
        color: '#666', 
        marginBottom: '70px', 
        textAlign: 'center',
        fontStyle: 'italic',
      }}>
        {wave.period}
      </div>
      {/* 时间轴主线 */}
      <div style={{ 
        position: 'relative', 
        width: '2px', 
        backgroundColor: '#bbb', 
        height: timelineHeight + 'px', 
        marginLeft: '0', 
        marginTop: '-70px',
        borderRadius: '1px' 
      }}>
        {/* 分组容器：绝对定位在主轴内 */}
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {eventGroups.map((group, groupIndex) => {
            // 使用组内最早事件的时间来确定这个组的纵向位置
            const firstEvent = group[0];
            const currentTime = new Date(firstEvent.date).getTime();
            const offset = currentTime - timelineStart;
            const ratio = offset / safeTimeSpan;
            const pos = ratio * timelineHeight; // 这就是这个组在时间轴上的位置

            return (
              <EventGroup 
                key={groupIndex}
                events={group} 
                waveIndex={waveIndex} 
                registerPosition={registerPosition} 
                onEventClick={onEventClick} 
                onDelete={(eventId) => onDeleteEvent(waveIndex, eventId)} 
                scrollPos={scrollPos} 
                groupTop={pos}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

/**
 * ConnectionsLayer 组件：渲染所有正式连接（红色曲线）以及正在建立的 pending connection（红色箭头跟随鼠标）。
 */
function ConnectionsLayer({
  connections,
  eventPositions,
  containerRef,
  pendingConnection,
  pendingConnectionEnd,
  onDeleteConnection,
}) {
  const containerRect = containerRef.current
    ? containerRef.current.getBoundingClientRect()
    : { left: 0, top: 0 };

  return (
    <svg
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: 1,
        pointerEvents: 'none',
      }}
    >
      <defs>
        <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="10" refY="3.5" orient="auto">
          <polygon points="0 0, 10 3.5, 0 7" fill="red" />
        </marker>
      </defs>
      {connections.map((conn, index) => {
        const fromPos = eventPositions[conn.from];
        const toPos = eventPositions[conn.to];
        if (!fromPos || !toPos) return null;
        const adjustedFrom = { x: fromPos.x - containerRect.left, y: fromPos.y - containerRect.top };
        const adjustedTo = { x: toPos.x - containerRect.left, y: toPos.y - containerRect.top };
        return (
          <ConnectionLine
            key={index}
            fromPos={adjustedFrom}
            toPos={adjustedTo}
            reason={conn.reason}
            onDelete={() => onDeleteConnection(index)}
          />
        );
      })}
      {pendingConnection &&
        pendingConnectionEnd &&
        eventPositions[pendingConnection.startEventId] &&
        (() => {
          const startPosGlobal = eventPositions[pendingConnection.startEventId];
          const startPos = {
            x: startPosGlobal.x - containerRect.left,
            y: startPosGlobal.y - containerRect.top,
          };
          return (
            <line
              x1={startPos.x}
              y1={startPos.y}
              x2={pendingConnectionEnd.x}
              y2={pendingConnectionEnd.y}
              stroke="red"
              strokeWidth="3"
              markerEnd="url(#arrowhead)"
              style={{ pointerEvents: 'none' }}
            />
          );
        })()}
    </svg>
  );
}

/**
 * ConnectionReasonModal - Modal component for entering connection reason
 */
function ConnectionReasonModal({ isOpen, onClose, onSubmit }) {
  const [reason, setReason] = useState('Manual connection');

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(reason || 'Manual connection');
    onClose();
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
    }}>
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '8px',
        width: '400px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
      }}>
        <h3 style={{ marginTop: 0 }}>Connection Reason</h3>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Reason for connection:</label>
            <input 
              type="text" 
              value={reason} 
              onChange={(e) => setReason(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd'
              }}
              autoFocus
            />
          </div>
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
            <button 
              type="button" 
              onClick={onClose}
              style={{
                padding: '8px 16px',
                border: '1px solid #ddd',
                borderRadius: '4px',
                backgroundColor: '#f1f1f1',
                cursor: 'pointer'
              }}
            >
              Cancel
            </button>
            <button 
              type="submit"
              style={{
                padding: '8px 16px',
                border: 'none',
                borderRadius: '4px',
                backgroundColor: '#007BFF',
                color: 'white',
                cursor: 'pointer'
              }}
            >
              Create Connection
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

/**
 * Showcase 主容器组件：
 * - 加载 JSON 文件中的 waves 与 connections 数据
 * - 支持添加、删除事件，以及通过点击连接不同事件形成连接线（连接前会弹出输入连接原因的 Modal）
 */
function Showcase() {
  // waves 与 connections 初始为空，通过 fetch 加载 data.json
  const [waves, setWaves] = useState([]);
  const [connections, setConnections] = useState([]);

  // 注册事件节点的位置信息
  const [eventPositions, setEventPositions] = useState({});
  const registerPosition = (id, pos) => {
    setEventPositions((prev) => ({ ...prev, [id]: pos }));
  };

  // pendingConnection 保存正在建立连接的起点（eventId）
  const [pendingConnection, setPendingConnection] = useState(null);
  const [pendingConnectionEnd, setPendingConnectionEnd] = useState(null);

  // 模态框状态
  const [showAddModal, setShowAddModal] = useState(false);
  const [showConnectionModal, setShowConnectionModal] = useState(false);
  const [activeWaveIndex, setActiveWaveIndex] = useState(null);
  const [pendingTo, setPendingTo] = useState(null);

  // 加载 JSON 数据
  useEffect(() => {
    fetch('/data.json')
      .then((res) => res.json())
      .then((data) => {
        setWaves(data.waves);
        setConnections(data.connections);
      })
      .catch((error) => console.error("Error loading data:", error));
  }, []);

  const handleEventClick = (eventId) => {
    // 如果当前没有正在连接的事件，则把这个事件作为连接起点
    if (!pendingConnection) {
      setPendingConnection({ startEventId: eventId });
    } else {
      // 如果正在连接，且再次点到同一个事件，则取消连接
      if (pendingConnection.startEventId === eventId) {
        setPendingConnection(null);
        setPendingConnectionEnd(null);
        return;
      }
      // 否则，把这个事件当做终点，弹出"填写连接原因"的 Modal
      setPendingTo(eventId);
      setShowConnectionModal(true);
    }
  };

  const handleAddEvent = (waveIndex) => {
    setActiveWaveIndex(waveIndex);
    setShowAddModal(true);
  };

  const handleSubmitNewEvent = (waveIndex, eventData) => {
    setWaves((prev) => {
      const newWaves = [...prev];
      const newId = generateUniqueId();
      newWaves[waveIndex].events.push({ 
        id: newId, 
        date: eventData.date, 
        title: eventData.title, 
        detail: eventData.detail 
      });
      return newWaves;
    });
  };

  const handleSubmitConnection = (reason) => {
    setConnections((prev) => [
      ...prev,
      { from: pendingConnection.startEventId, to: pendingTo, reason },
    ]);
    setPendingConnection(null);
    setPendingConnectionEnd(null);
    setPendingTo(null);
  };

  const handleDeleteEvent = (waveIndex, eventId) => {
    // 删除该事件
    setWaves((prev) => {
      const newWaves = [...prev];
      newWaves[waveIndex].events = newWaves[waveIndex].events.filter(e => e.id !== eventId);
      return newWaves;
    });
    // 删除与之相关的所有连接
    const targetId = `${waveIndex}-${eventId}`;
    setConnections((prev) =>
      prev.filter((conn) => conn.from !== targetId && conn.to !== targetId)
    );
  };

  const handleDeleteConnection = (connIndex) => {
    setConnections((prev) => prev.filter((_, index) => index !== connIndex));
  };

  const containerRef = useRef(null);
  const [scrollPos, setScrollPos] = useState(0);

  useEffect(() => {
    const handleScroll = () => setScrollPos(window.scrollY);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleMouseMove = (e) => {
    if (pendingConnection) {
      const containerRect = containerRef.current.getBoundingClientRect();
      setPendingConnectionEnd({
        x: e.clientX - containerRect.left,
        y: e.clientY - containerRect.top,
      });
    }
  };

  return (
    <div 
      ref={containerRef} 
      onMouseMove={handleMouseMove} 
      style={{ 
        padding: '20px', 
        position: 'relative'
      }}
    >
      {/* 添加事件的 Modal */}
      <AddEventModal 
        isOpen={showAddModal} 
        onClose={() => setShowAddModal(false)} 
        onSubmit={handleSubmitNewEvent} 
        waveIndex={activeWaveIndex} 
      />

      {/* 连接原因的 Modal */}
      <ConnectionReasonModal 
        isOpen={showConnectionModal}
        onClose={() => {
          setShowConnectionModal(false);
          setPendingConnection(null);
          setPendingConnectionEnd(null);
        }}
        onSubmit={handleSubmitConnection}
      />

      <h1 style={{ textAlign: 'center', marginBottom: '40px' }}>
        Analogical Timeline of Tech Wave
      </h1>
      <div 
        style={{ 
          display: 'flex', 
          justifyContent: 'space-around', 
          flexWrap: 'wrap', 
          marginTop: '40px', 
          position: 'relative',
          zIndex: 0,
        }}
      >
        {waves.map((wave, index) => (
          <Timeline
            key={index}
            wave={wave}
            waveIndex={index}
            registerPosition={registerPosition}
            onEventClick={handleEventClick}
            onAddEvent={handleAddEvent}
            onDeleteEvent={handleDeleteEvent}
            scrollPos={scrollPos}
          />
        ))}
      </div>

      {/* 全局悬停提示容器 - 确保提示总是在最上层 */}
      <div id="global-tooltip-container" style={{ position: 'fixed', top: 0, left: 0, zIndex: 10000 }} />

      <ConnectionsLayer
        connections={connections}
        eventPositions={eventPositions}
        containerRef={containerRef}
        pendingConnection={pendingConnection}
        pendingConnectionEnd={pendingConnectionEnd}
        onDeleteConnection={handleDeleteConnection}
      />
    </div>
  );
}

export default Showcase;
