import React, {
  useState,
  useEffect,
  useRef,
  useCallback,
  useMemo,
  useLayoutEffect,
} from 'react';

/** ç”Ÿæˆéšæœº ID çš„å°å·¥å…·å‡½æ•°ï¼Œç”¨äºç»™æ–°æ·»åŠ çš„äº‹ä»¶åˆ†é…ç¨³å®šçš„ event.id */
function generateUniqueId() {
  return Math.random().toString(36).substr(2, 9);
}

/** 
 * Helper function to generateä¸€ä¸ªç®€å•çš„è´å¡å°”æ›²çº¿è·¯å¾„
 */
function generateCurvePath(x1, y1, x2, y2) {
  const cx = (x1 + x2) / 2;
  return `M ${x1},${y1} C ${cx},${y1} ${cx},${y2} ${x2},${y2}`;
}

/**
 * Modal Component for adding new events
 */
function AddEventModal({ isOpen, onClose, onSubmit, timelineOptions = [] }) {
  const [date, setDate] = useState('');
  const [title, setTitle] = useState('');
  const [detail, setDetail] = useState('');
  const [selectedTimeline, setSelectedTimeline] = useState(0);

  // Modal æ‰“å¼€æ—¶ï¼Œé»˜è®¤é€‰ä¸­ç¬¬ä¸€ä¸ª
  useEffect(() => {
    if (isOpen && timelineOptions.length > 0) {
      setSelectedTimeline(timelineOptions[0].index);
    }
  }, [isOpen, timelineOptions]);

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (date && title) {
      onSubmit({
        timelineIndex: selectedTimeline,
        date,
        title,
        detail,
      });
      onClose();
      // é‡ç½®
      setDate('');
      setTitle('');
      setDetail('');
    }
  };

  return (
    <div style={overlayStyle}>
      <div style={modalStyle}>
        <h3>Add New Event</h3>
        <form onSubmit={handleSubmit}>
          {/* ä¸‹æ‹‰èœå• */}
          {timelineOptions.length > 0 && (
            <div style={formItemStyle}>
              <label style={labelStyle}>Select Timeline:</label>
              <select
                value={selectedTimeline}
                onChange={(e) => setSelectedTimeline(parseInt(e.target.value))}
                style={selectStyle}
              >
                {timelineOptions.map((option, idx) => (
                  <option key={idx} value={option.index}>
                    {option.title}
                  </option>
                ))}
              </select>
            </div>
          )}

          <div style={formItemStyle}>
            <label style={labelStyle}>Date (YYYY-MM-DD):</label>
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              style={inputStyle}
              required
            />
          </div>
          <div style={formItemStyle}>
            <label style={labelStyle}>Title:</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              style={inputStyle}
              required
            />
          </div>
          <div style={formItemStyle}>
            <label style={labelStyle}>Details:</label>
            <textarea
              value={detail}
              onChange={(e) => setDetail(e.target.value)}
              style={{ ...inputStyle, minHeight: '100px' }}
            />
          </div>
          <div style={btnContainerStyle}>
            <button type="button" onClick={onClose} style={cancelBtnStyle}>
              Cancel
            </button>
            <button type="submit" style={submitBtnStyle}>
              Add Event
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

/**
 * ConnectionLineï¼šç»˜åˆ¶çº¢è‰²è¿æ¥çº¿
 */
function ConnectionLine({ fromPos, toPos, reason, onDelete }) {
  const [hovered, setHovered] = useState(false);

  const strokeColor = hovered ? 'rgba(220,20,60,0.9)' : 'rgba(255,0,0,0.4)';
  const strokeWidth = hovered ? 3 : 2;
  const pathData = generateCurvePath(fromPos.x, fromPos.y, toPos.x, toPos.y);

  const handleClick = (e) => {
    e.stopPropagation();
    if (window.confirm('Delete this connection?')) {
      onDelete();
    }
  };

  return (
    <g
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      onClick={handleClick}
      style={{ cursor: 'pointer' }}
    >
      <path
        d={pathData}
        stroke={strokeColor}
        strokeWidth={strokeWidth}
        fill="none"
        style={{
          pointerEvents: 'all',
          filter: hovered ? 'drop-shadow(0 0 3px rgba(255,0,0,0.3))' : 'none',
        }}
      />
      <text
        x={(fromPos.x + toPos.x) / 2}
        y={(fromPos.y + toPos.y) / 2 - 5}
        fill={strokeColor}
        fontSize={hovered ? 14 : 12}
        fontWeight={hovered ? 'bold' : 'normal'}
        textAnchor="middle"
        style={{
          pointerEvents: 'none',
          filter: hovered
            ? 'drop-shadow(0 1px 2px rgba(255,255,255,0.8))'
            : 'none',
        }}
      >
        {reason}
      </text>
    </g>
  );
}

/** 
 * ç”¨æ¥ç”Ÿæˆä¸Šä¸‹äº¤é”™åç§»
 */
function getOffsets(count, gap = 80) {
  const offsets = [];
  const half = Math.floor(count / 2);
  for (let i = 0; i < count; i++) {
    offsets.push((i - half) * gap);
  }
  // å¶æ•°é¡¹è®©å®ƒä»¬å¯¹ç§°
  if (count % 2 === 0) {
    offsets.forEach((val, idx) => {
      offsets[idx] = val + gap / 2;
    });
  }
  return offsets;
}

/**
 * å…¨å±€æ‚¬æµ®å¼¹æ¡†
 */
function GlobalTooltip({ hoveredEvent }) {
  if (!hoveredEvent) return null;
  const { rect, title, detail } = hoveredEvent;
  if (!rect) return null;

  // è®©tooltipå±…ä¸­
  const left = rect.left + rect.width / 2 - 100;
  const top = rect.bottom + 8;

  return (
    <div
      style={{
        position: 'fixed',
        top,
        left,
        width: '200px',
        backgroundColor: '#fff',
        border: '1px solid #ddd',
        borderRadius: '6px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.15)',
        padding: '8px',
        fontSize: '12px',
        zIndex: 9999,
        pointerEvents: 'none',
      }}
    >
      <div style={{ fontWeight: 'bold', marginBottom: '4px' }}>{title}</div>
      <div>{detail}</div>
    </div>
  );
}

/**
 * EventGroupï¼šä¸€ç»„ï¼ˆ<= 3ä¸ªæœˆå†…ï¼‰äº‹ä»¶åœ¨åŒä¸€æ°´å¹³ï¼Œå†…éƒ¨ä¸Šä¸‹äº¤é”™
 * æ³¨æ„ï¼šè¿™é‡Œ **ä¸å†** è°ƒç”¨ registerPositionã€‚æµ‹é‡æŒªåˆ°çˆ¶ç»„ä»¶åš
 */
function EventGroup({ events, onEventClick, onDelete, groupTop, setHoveredEvent, getEventRef }) {
  // æ’åºå¹¶è®¡ç®—åç§»
  const sortedEvents = useMemo(() => {
    return [...events].sort((a, b) => new Date(a.date) - new Date(b.date));
  }, [events]);

  const offsets = useMemo(() => {
    return getOffsets(sortedEvents.length, 80);
  }, [sortedEvents.length]);

  const handleMouseEnter = (ev, i) => {
    if (!ev.detail) return;
    const refEl = getEventRef(ev.id)?.current;
    if (refEl) {
      const rect = refEl.getBoundingClientRect();
      setHoveredEvent({ rect, title: ev.title, detail: ev.detail });
    }
  };

  return (
    <div
      style={{
        position: 'absolute',
        top: groupTop,
        left: 0,
        width: '280px',
        zIndex: 500,
      }}
    >
      {sortedEvents.map((ev, i) => {
        const offsetTop = offsets[i];
        const eventRef = getEventRef(ev.id); // çˆ¶ç»„ä»¶ä¼ ä¸‹æ¥çš„ref

        return (
          <div
            key={ev.id}
            ref={eventRef}
            style={{
              position: 'absolute',
              left: '10px',
              top: offsetTop + 'px',
              width: '180px',
              height: '60px',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '8px',
              backgroundColor: '#fff',
              boxShadow: '0 2px 4px rgba(0,0,0,0.08)',
              cursor: 'pointer',
              transition: 'all 0.2s ease',
            }}
            onClick={() => onEventClick(ev.id)}
            onMouseEnter={() => handleMouseEnter(ev, i)}
            onMouseLeave={() => setHoveredEvent(null)}
          >
            {/* æ ‡é¢˜ */}
            <div
              style={{
                fontWeight: 'bold',
                marginBottom: '4px',
                fontSize: '14px',
                color: '#007BFF',
              }}
            >
              {ev.title}
            </div>
            {/* æ—¥æœŸ */}
            <div style={{ fontSize: '13px', marginBottom: '6px', color: '#555' }}>
              {ev.date}
            </div>

            {/* åˆ é™¤æŒ‰é’®ï¼šé¢œè‰²è“è‰² */}
            <button
              onClick={(e) => {
                e.stopPropagation();
                onDelete(ev.id);
              }}
              style={{
                position: 'absolute',
                top: '6px',
                right: '6px',
                background: 'transparent',
                border: 'none',
                color: 'rgba(0,0,255,0.6)',
                fontSize: '14px',
                cursor: 'pointer',
                transition: 'color 0.2s ease',
              }}
            >
              Ã—
            </button>
          </div>
        );
      })}
    </div>
  );
}

/**
 * Timelineï¼šä¸€æ¡æ—¶é—´è½´ + æ ‡é¢˜ + å†…éƒ¨å¤šä¸ª EventGroup
 */
function Timeline({
  timelineIndex,
  wave,
  onEventClick,
  onDeleteEvent,
  setHoveredEvent,
  getEventRef,
}) {
  const timelineHeight = 1600;

  // è®¡ç®—æ—¶é—´çº¿èŒƒå›´
  const timestamps = wave.events.map((e) => new Date(e.date).getTime());
  const firstEventTime = timestamps.length > 0 ? Math.min(...timestamps) : Date.now();
  const firstEventDate = new Date(firstEventTime);
  const timelineStart = new Date(firstEventDate.getFullYear(), 0, 1).getTime();

  // ç¤ºä¾‹ï¼š8å¹´è·¨åº¦
  const eightYearsInMs = 7.2 * 365.25 * 24 * 60 * 60 * 1000;
  const timelineEndTime = timelineStart + eightYearsInMs;
  const safeTimeSpan = timelineEndTime - timelineStart;

  // æŒ‰æ—¥æœŸæ’åº
  const sortedEvents = useMemo(() => {
    return [...wave.events].sort((a, b) => new Date(a.date) - new Date(b.date));
  }, [wave.events]);

  // å°†äº‹ä»¶åˆ†ç»„ï¼ˆ<= 3ä¸ªæœˆå†…ï¼‰
  const THREE_MONTHS_MS = 3 * 30 * 24 * 60 * 60 * 1000;
  const eventGroups = [];
  let currentGroup = [];

  sortedEvents.forEach((ev, index) => {
    if (index === 0) {
      currentGroup.push(ev);
    } else {
      const prevTime = new Date(sortedEvents[index - 1].date).getTime();
      const currTime = new Date(ev.date).getTime();
      if (currTime - prevTime <= THREE_MONTHS_MS) {
        currentGroup.push(ev);
      } else {
        eventGroups.push([...currentGroup]);
        currentGroup = [ev];
      }
    }
  });
  if (currentGroup.length > 0) {
    eventGroups.push(currentGroup);
  }

  // è®¡ç®—å„ç»„çºµå‘ä½ç½®ï¼Œé˜²æ­¢é‡å 
  let lastUsedPos = 0;
  const groupSpacing = 20;

  function estimateGroupHeight(group) {
    const n = group.length;
    // n ä¸ªäº‹ä»¶ => (n-1)ä¸ª 80px é—´éš” + 60px å¡ç‰‡é«˜åº¦
    return 80 * (n - 1) + 60;
  }

  const groupPositions = eventGroups.map((group) => {
    const firstEv = group[0];
    const currentTime = new Date(firstEv.date).getTime();
    const ratio = (currentTime - timelineStart) / safeTimeSpan;
    const naivePos = ratio * timelineHeight;
    const groupHeight = estimateGroupHeight(group);
    const finalPos = Math.max(naivePos, lastUsedPos + groupSpacing);
    lastUsedPos = finalPos + groupHeight;
    return finalPos;
  });

  // icon
  const iconMap = {
    PC: 'ğŸ–¥',
    Internet: 'ğŸŒ',
    Smartphones: 'ğŸ“±',
    EVs: 'ğŸš—',
    GenAI: 'ğŸ’¡',
    Robot: 'ğŸ¤–',
  };
  const icon = iconMap[wave.title] || 'ğŸ’»';

  return (
    <div style={timelineWrapperStyle}>
      {/* æ ‡é¢˜ */}
      <div style={timelineHeaderStyle}>
        <h2 style={timelineTitleStyle}>
          <span style={{ marginRight: '8px' }}>{icon}</span>
          {wave.title}
        </h2>
      </div>
      <div style={timelinePeriodStyle}>{wave.period}</div>

      {/* ä¸»è½´ */}
      <div style={{ position: 'relative', width: '2px', backgroundColor: '#bbb',
                    height: timelineHeight + 'px', marginLeft: '0', marginTop: '-70px',
                    borderRadius: '1px' }}>
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {eventGroups.map((group, idx) => {
            return (
              <EventGroup
                key={idx}
                events={group}
                onEventClick={onEventClick}
                onDelete={onDeleteEvent}
                groupTop={groupPositions[idx]}
                setHoveredEvent={setHoveredEvent}
                getEventRef={getEventRef}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

/**
 * ConnectionsLayerï¼šæ¸²æŸ“æ‰€æœ‰è¿æ¥çº¿ + è¿çº¿ä¸­
 */
function ConnectionsLayer({
  connections,
  eventPositions,
  containerRef,
  pendingConnection,
  pendingConnectionEnd,
  onDeleteConnection,
}) {
  const containerRect = containerRef.current
    ? containerRef.current.getBoundingClientRect()
    : { left: 0, top: 0 };

  return (
    <svg
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: 1,
        pointerEvents: 'none',
      }}
    >
      <defs>
        <marker
          id="arrowhead"
          markerWidth="10"
          markerHeight="7"
          refX="10"
          refY="3.5"
          orient="auto"
        >
          <polygon points="0 0, 10 3.5, 0 7" fill="red" />
        </marker>
      </defs>

      {/* æ­£å¼è¿æ¥ */}
      {connections.map((conn, index) => {
        const fromPos = eventPositions[conn.from];
        const toPos = eventPositions[conn.to];
        if (!fromPos || !toPos) return null;

        // è°ƒæ•´åæ ‡ç›¸å¯¹ container
        const adjustedFrom = {
          x: fromPos.x - containerRect.left,
          y: fromPos.y - containerRect.top,
        };
        const adjustedTo = {
          x: toPos.x - containerRect.left,
          y: toPos.y - containerRect.top,
        };

        return (
          <ConnectionLine
            key={index}
            fromPos={adjustedFrom}
            toPos={adjustedTo}
            reason={conn.reason}
            onDelete={() => onDeleteConnection(index)}
          />
        );
      })}

      {/* å¦‚æœæ­£åœ¨è¿çº¿ï¼Œåˆ™ç”»ä¸´æ—¶çº¿ */}
      {pendingConnection &&
        pendingConnectionEnd &&
        eventPositions[pendingConnection.startEventId] && (() => {
          const startPosGlobal = eventPositions[pendingConnection.startEventId];
          const startPos = {
            x: startPosGlobal.x - containerRect.left,
            y: startPosGlobal.y - containerRect.top,
          };
          return (
            <line
              x1={startPos.x}
              y1={startPos.y}
              x2={pendingConnectionEnd.x}
              y2={pendingConnectionEnd.y}
              stroke="red"
              strokeWidth="3"
              markerEnd="url(#arrowhead)"
              style={{ pointerEvents: 'none' }}
            />
          );
        })()}
    </svg>
  );
}

/**
 * ConnectionReasonModalï¼šå¡«å†™è¿æ¥åŸå› 
 */
function ConnectionReasonModal({ isOpen, onClose, onSubmit }) {
  const [reason, setReason] = useState('Manual connection');

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(reason || 'Manual connection');
    onClose();
  };

  return (
    <div style={overlayStyle}>
      <div style={modalStyle}>
        <h3>Connection Reason</h3>
        <form onSubmit={handleSubmit}>
          <div style={formItemStyle}>
            <label style={labelStyle}>Reason for connection:</label>
            <input
              type="text"
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              style={inputStyle}
              autoFocus
            />
          </div>
          <div style={btnContainerStyle}>
            <button type="button" onClick={onClose} style={cancelBtnStyle}>
              Cancel
            </button>
            <button type="submit" style={submitBtnStyle}>
              Create Connection
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

/**
 * Showcase ä¸»å®¹å™¨
 *  - çˆ¶ç»„ä»¶ç»Ÿä¸€ç®¡ç†ï¼šäº‹ä»¶ & è¿æ¥
 *  - çˆ¶ç»„ä»¶ç»Ÿä¸€æµ‹é‡ï¼šæ¯ä¸ªäº‹ä»¶å¡ç‰‡çš„DOMä½ç½®
 */
function Showcase() {
  const [waves, setWaves] = useState([]);
  const [connections, setConnections] = useState([]);
  const [eventPositions, setEventPositions] = useState({});

  // å­˜æ”¾æ¯ä¸ªäº‹ä»¶å¡ç‰‡çš„ ref
  // eventRefs.current[eventId] = React ref object
  const eventRefs = useRef({});

  // ç»™å­ç»„ä»¶ç”¨çš„ getterï¼Œè¿”å›ä¸€ä¸ª refã€‚è‹¥æ²¡æœ‰åˆ™åˆ›å»º
  const getEventRef = useCallback((eventId) => {
    if (!eventRefs.current[eventId]) {
      eventRefs.current[eventId] = React.createRef();
    }
    return eventRefs.current[eventId];
  }, []);

  // æµ‹é‡æ‰€æœ‰äº‹ä»¶å¡ç‰‡ä½ç½®
  useLayoutEffect(() => {
    let changed = false;
    const newPositions = { ...eventPositions };

    Object.entries(eventRefs.current).forEach(([eventId, refObj]) => {
      if (refObj.current) {
        const rect = refObj.current.getBoundingClientRect();
        // å››èˆäº”å…¥ï¼Œé¿å…å°æ•°ç‚¹æŠ–åŠ¨
        const centerPos = {
          x: Math.round(rect.left + rect.width / 2),
          y: Math.round(rect.top + rect.height / 2),
        };
        const oldPos = newPositions[eventId];
        if (!oldPos || oldPos.x !== centerPos.x || oldPos.y !== centerPos.y) {
          changed = true;
          newPositions[eventId] = centerPos;
        }
      }
    });

    // åªæœ‰æ£€æµ‹åˆ°åæ ‡çœŸçš„æœ‰å˜åŒ–æ—¶å†æ›´æ–° state
    if (changed) {
      setEventPositions(newPositions);
    }
  });

  // è¿æ¥ä¸­çš„ä¸´æ—¶çŠ¶æ€
  const [pendingConnection, setPendingConnection] = useState(null); // {startEventId}
  const [pendingConnectionEnd, setPendingConnectionEnd] = useState(null);

  // ä¸¤ä¸ªModalï¼šæ·»åŠ äº‹ä»¶ã€æ·»åŠ è¿æ¥åŸå› 
  const [showGlobalAddModal, setShowGlobalAddModal] = useState(false);
  const [showConnectionModal, setShowConnectionModal] = useState(false);
  const [pendingTo, setPendingTo] = useState(null);

  // tooltip
  const [hoveredEvent, setHoveredEvent] = useState(null);

  // åŠ è½½æ•°æ®
  useEffect(() => {
    fetch('/data.json')
      .then((r) => r.json())
      .then((data) => {
        setWaves(data.waves);
        setConnections(data.connections);
      })
      .catch((err) => console.error('Error loading data:', err));
  }, []);

  // ç‚¹å‡»äº‹ä»¶å¡ç‰‡ï¼šè‹¥æ— pendingåˆ™è®¾ç½®startï¼Œå¦åˆ™è®¾ç½®end
  const handleEventClick = (eventId) => {
    if (!pendingConnection) {
      setPendingConnection({ startEventId: eventId });
    } else {
      // å¦‚æœç‚¹åˆ°åŒä¸€ä¸ªeventï¼Œå–æ¶ˆ
      if (pendingConnection.startEventId === eventId) {
        setPendingConnection(null);
        setPendingConnectionEnd(null);
      } else {
        setPendingTo(eventId);
        setShowConnectionModal(true);
      }
    }
  };

  // æäº¤æ–°äº‹ä»¶
  // Showcase ä¸»å®¹å™¨ç»„ä»¶å†…çš„ handleSubmitNewEvent
const handleSubmitNewEvent = (data) => {
  const newId = generateUniqueId();

  setWaves((prev) => {
    // åšä¸€ä»½æµ…æ‹·è´
    const newWaves = [...prev];
    const idx = data.timelineIndex;

    if (newWaves[idx]) {
      // å¯¹ wave è‡ªèº«ä¹Ÿåšä¸€æ¬¡æ‹·è´
      const waveCopy = {
        ...newWaves[idx],
        // å¯¹ events ä¹Ÿæ‹·è´
        events: [...newWaves[idx].events],
      };

      // åœ¨æ–°æ•°ç»„é‡Œ push æ–°äº‹ä»¶
      waveCopy.events.push({
        id: newId,
        date: data.date,
        title: data.title,
        detail: data.detail,
      });

      // æŠŠæ‹·è´çš„ wave æ›¿æ¢å›å»
      newWaves[idx] = waveCopy;
    }
    return newWaves;
  });
  };

  // æäº¤è¿æ¥åŸå› 
  const handleSubmitConnection = (reason) => {
    setConnections((prev) => [
      ...prev,
      { from: pendingConnection.startEventId, to: pendingTo, reason },
    ]);
    setPendingConnection(null);
    setPendingConnectionEnd(null);
    setPendingTo(null);
  };

  // åˆ é™¤äº‹ä»¶
  const handleDeleteEvent = (eventId) => {
    setWaves((prev) =>
      prev.map((w) => ({
        ...w,
        events: w.events.filter((e) => e.id !== eventId),
      }))
    );
    // åˆ æ‰ç›¸å…³è¿æ¥
    setConnections((prev) =>
      prev.filter((c) => c.from !== eventId && c.to !== eventId)
    );
  };

  // åˆ é™¤è¿æ¥
  const handleDeleteConnection = (connIndex) => {
    setConnections((prev) => prev.filter((_, i) => i !== connIndex));
  };

  // é¼ æ ‡ç§»åŠ¨æ—¶ï¼Œå¦‚æœæ­£åœ¨è¿çº¿ï¼Œå°±æ›´æ–°è¿çº¿ç»ˆç‚¹
  const containerRef = useRef(null);
  const handleMouseMove = (e) => {
    if (pendingConnection) {
      const containerRect = containerRef.current.getBoundingClientRect();
      setPendingConnectionEnd({
        x: e.clientX - containerRect.left,
        y: e.clientY - containerRect.top,
      });
    }
  };

  // ç”¨Memoæ„é€  timelineOptions
  const timelineOptions = useMemo(() => {
    return waves.map((wave, i) => ({ index: i, title: wave.title }));
  }, [waves]);

  return (
    <div
      ref={containerRef}
      onMouseMove={handleMouseMove}
      style={{ padding: '120px', position: 'relative' }}
    >
      {/* Add Event Modal */}
      <AddEventModal
        isOpen={showGlobalAddModal}
        onClose={() => setShowGlobalAddModal(false)}
        onSubmit={handleSubmitNewEvent}
        timelineOptions={timelineOptions}
      />

      {/* Connection Reason Modal */}
      <ConnectionReasonModal
        isOpen={showConnectionModal}
        onClose={() => {
          setShowConnectionModal(false);
          setPendingConnection(null);
          setPendingConnectionEnd(null);
        }}
        onSubmit={handleSubmitConnection}
      />

      {/* å…¨å±€ Hover å¼¹æ¡† */}
      <GlobalTooltip hoveredEvent={hoveredEvent} />

      <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>
        Analogical Timeline of Tech Wave
      </h1>
      <h2 style={{ textAlign: 'center' }}>
        History doesn't repeat itself, but it often rhymes.
      </h2>

      {/* å³ä¸Šè§’ æ·»åŠ æŒ‰é’® */}
      <button
        onClick={() => setShowGlobalAddModal(true)}
        style={{
          position: 'fixed',
          top: '20px',
          right: '20px',
          width: '40px',
          height: '40px',
          borderRadius: '50%',
          backgroundColor: '#007BFF',
          border: 'none',
          color: '#fff',
          fontSize: '24px',
          fontWeight: 'bold',
          cursor: 'pointer',
          zIndex: 1001,
          boxShadow: '0 2px 5px rgba(0,0,0,0.3)',
        }}
        title="Add Event"
      >
        +
      </button>

      {/* å¤šæ¡ Wave (Timeline) */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-around',
          flexWrap: 'wrap',
          marginTop: '40px',
          position: 'relative',
          zIndex: 0,
        }}
      >
        {waves.map((wave, idx) => (
          <Timeline
            key={idx}
            timelineIndex={idx}
            wave={wave}
            onEventClick={handleEventClick}
            onDeleteEvent={handleDeleteEvent}
            setHoveredEvent={setHoveredEvent}
            getEventRef={getEventRef}
          />
        ))}
      </div>

      {/* è¿çº¿å±‚ */}
      <ConnectionsLayer
        connections={connections}
        eventPositions={eventPositions}
        containerRef={containerRef}
        pendingConnection={pendingConnection}
        pendingConnectionEnd={pendingConnectionEnd}
        onDeleteConnection={handleDeleteConnection}
      />
    </div>
  );
}

export default Showcase;

