import React, { useState, useEffect } from 'react';

/**
 * Groups timeline items by "year marker"—an item with no `company` and no `content`.
 * Returns an array of objects: { marker: string, items: [] }
 */
function groupTimeline(timeline) {
  const groups = [];
  let currentGroup = null;

  timeline.forEach((item) => {
    const isYearMarker = !item.company && !item.content;
    if (isYearMarker) {
      if (currentGroup) {
        groups.push(currentGroup);
      }
      currentGroup = { marker: item.date, items: [] };
    } else {
      if (!currentGroup) {
        currentGroup = { marker: '', items: [] };
      }
      currentGroup.items.push(item);
    }
  });

  if (currentGroup) {
    groups.push(currentGroup);
  }

  return groups;
}

function Showcase() {
  // LLM Timeline data
  const llmTimeline = [
    { date: '2022: Pre-training / Scaling Law / Chatbot' },
    { date: 'Mar 15th, 2022', company: 'OpenAI', content: 'OpenAI released GPT-3.5 text-davinci-002 API' },
    { date: 'Nov 30th, 2022', company: 'OpenAI', content: 'OpenAI released ChatGPT w/ GPT-3.5' },
    { date: '2023: Post-training / Agent' },
    { date: 'Jan 23rd, 2023', company: 'OpenAI', content: 'Microsoft announced a new multiyear investment in OpenAI, amounting to $10 billion. This investment valued OpenAI at approximately $29 billion' },
    { date: 'Mar 14th, 2023', company: 'OpenAI', content: 'OpenAI released GPT-4 which continues to follow the scaling law' },
    { date: 'Oct 10th, 2023', company: 'Benchmark', content: <><a className="author-link" href="https://www.swebench.com/" target="_blank" rel="noopener noreferrer">SWE-bench</a>, a major benchmark for evaluating LLM code generation performance with real-world GitHub issues, was released. RAG combined with ChatGPT-3.5 achieved only 0.33% on SWE-bench Lite</> },
    { date: '2024: Reasoning / Virtual Productivity' },
    { date: 'May 13th, 2024', company: 'OpenAI', content: 'OpenAI released GPT-4o, the first mainstream multimodal model' },
    { date: 'Jun 20th, 2024', company: 'Anthropic', content: 'Anthropic released Claude 3.5 Sonnet, which leads in programming capabilities and becomes a major competitor to OpenAI' },
    { date: 'Sep 20th, 2024', company: 'OpenAI', content: 'OpenAI released o1-preview, 1st reasoning model' },
    { date: 'Oct 2nd, 2024', company: 'OpenAI', content: 'OpenAI announced the completion of a $6.6 billion funding round, elevating valuation to $157 billion' },
    { date: 'Oct 22th, 2024', company: 'Anthropic', content: 'Anthropic released computer use, the first mainstream agent capable of operating a computer interface' },
    { date: '2025: Open source / Social Impact / Next Wave' },
    { date: 'Jan 20th, 2025', company: 'Deepseek', content: 'Deepseek released Deepseek-R1, 1st open-weights reasoning model. It brings significant benefits to robotics, ushering in a new analogical timeline' }
  ];

  // Robot Timeline data
  const robotTimeline = [
    { date: '2025: RL / VLM / Reasoning' },
    {
      date: 'Feb 20th, 2025',
      company: 'Figure',
      content: (
        <>
        Figure released <a className="author-link" href="https://www.figure.ai/news/helix" target="_blank" rel="noopener noreferrer">reasoning robot with collaboration</a>, valuation jumped from $2.6B to $39.5B
        </>
        )
    },
    {
      date: 'Mar 12th, 2025',
      company: 'Google',
      content: (
      <>
      Google released <a className="author-link" href="https://deepmind.google/discover/blog/gemini-robotics-brings-ai-into-the-physical-world/" target="_blank" rel="noopener noreferrer">Gemini Robotics</a> model
      </>
      )},
      {
        date: 'Mar 18th, 2025',
        company: 'Nvidia',
        content: (
        <>
        Nvidia released <a className="author-link" href="https://nvidianews.nvidia.com/news/nvidia-isaac-gr00t-n1-open-humanoid-robot-foundation-model-simulation-frameworks" target="_blank" rel="noopener noreferrer">GR00T N1</a> model, the world's first open humanoid robot foundation model
        </>
        )},
    { date: '2026: Mass Production / Human-Robot Interaction' },
    {
      date: 'By early 2026',
      company: 'Predict',
      content: 'Many companies, individuals, and investors are pouring into robotics industry, boosting the valuations of leading robotics startups',
    },
    {
      date: 'By mid 2026',
      company: 'Predict',
      content: 'The robotics industry is maturing, allowing leading companies to produce >10k units annually',
    },
    {
      date: 'By late 2026',
      company: 'Predict',
      content: 'As robot prices decline, their prevalence increases, making robotics and HRI increasingly popular fields. This trend drives more real-world oriented benchmarks',
    },
    { date: '2027: Physical Productivity' },
    {
      date: 'By mid 2027',
      company: 'Predict',
      content: 'The US market landscape features a mix of ~3 unicorn startups coexisting with major tech companies',
    },
    {
      date: 'By late 2027',
      company: 'Predict',
      content: 'Mass-produced robots (>1m) are increasingly automating physical work in real world, especially in developed countries where labor cost is expensive',
    },
    { date: '2028: Open Source / Social Impact / Next Wave' },
    {
      date: 'By early 2028',
      company: 'Predict',
      content: 'More companies are entering the market and adopting open-source strategies, significantly reducing the cost of robots. This further expands their applications and creates a broader societal impact',
    },
    {
      date: 'By mid 2028',
      company: 'Predict',
      content: 'As robots take over a significant amount of physical labor, people have more free time, which is increasingly directed toward social interactions and entertainment. This shift has driven the development of AR/VR, ushering in a new analogical timeline',
    },
  ];

  const llmGroups = groupTimeline(llmTimeline);
  const robotGroups = groupTimeline(robotTimeline);

  const [llmOpenStates, setLlmOpenStates] = useState(llmGroups.map(() => true));
  const [robotOpenStates, setRobotOpenStates] = useState(robotGroups.map(() => true));

  function toggleLlmGroup(index) {
    setLlmOpenStates((prev) => {
      const copy = [...prev];
      copy[index] = !copy[index];
      return copy;
    });
  }

  function toggleRobotGroup(index) {
    setRobotOpenStates((prev) => {
      const copy = [...prev];
      copy[index] = !copy[index];
      return copy;
    });
  }

  useEffect(() => {
    const script = document.createElement('script');
    script.async = true;
    script.src = 'https://platform.twitter.com/widgets.js';
    script.charset = 'utf-8';
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  function getIconByCompany(company) {
    switch (company) {
      case 'OpenAI':
        return '/images/OpenAI.png';
      case 'Deepseek':
        return '/images/Deepseek.png';
      case 'Figure':
        return '/images/Figure.png';
      case 'Google':
        return '/images/Google.png';
      case 'Nvidia':
        return '/images/NVIDIA.png';
      case 'Anthropic':
        return '/images/Anthropic.png';
      case 'Benchmark':
        return '/images/Histogram.jpg';
      case 'Predict':
        return '/images/Q.png';
      default:
        return '/images/OpenAI.png';
    }
  }

  return (
    <div style={{ position: 'relative', padding: '0', margin: '0' }}>
      <style>{`
        * {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
        }
        body {
          font-family: Arial, sans-serif;
          background: #fff;
        }
        /* Visually hidden text for accessibility */
        .sr-only {
          position: absolute;
          width: 1px;
          height: 1px;
          padding: 0;
          margin: -1px;
          overflow: hidden;
          clip: rect(0, 0, 0, 0);
          white-space: nowrap;
          border: 0;
        }
        /* ================= Introduction Section ================= */
        .section-intro {
          background-color: #E6F2FF;
          padding: 2rem 1rem;
          text-align: center;
        }
        .section-intro h3 {
          font-size: 2rem;
          margin-bottom: 0.5rem;
          color: #333;
        }
        .section-intro h4 {
          font-size: 1.3rem;
          font-weight: 400;
          margin-bottom: 1.5rem;
          color: #666;
        }
        .author-info {
          margin-bottom: 1rem;
        }
        .author-link {
          color: #000;
          text-decoration: none;
          font-weight: bold;
          transition: color 0.2s;
        }
        .author-link:hover {
          color: #555;
          text-decoration: underline;
        }
        .intro-text {
          max-width: 980px;
          margin: 0 auto;
          text-align: left;
          line-height: 1.6;
          font-size: 1rem;
          color: #333;
          display: flex;
          align-items: flex-start;
          gap: 0.5em;
          /* Ensure text does not overflow */
          overflow-wrap: break-word;
          word-wrap: break-word;
          word-break: break-word;
        }
        .intro-text img {
          width: 300px;
          height: 300px;
          max-width: 100%;
          object-fit: contain;
          padding-right: 2em;
        }
        .intro-text .text-block {
          padding-top: 1em;
        }
        .intro-text p {
          overflow-wrap: break-word;
          word-wrap: break-word;
          word-break: break-word;
        }

        /* ================= Timeline Section ================= */
        .timeline-section-container {
          background-color: #f8f9f9;
          padding: 2rem 1rem;
        }
        .timeline-section-title {
          text-align: center;
          margin-bottom: 1.5rem;
          font-size: 1rem;
          color: #333;
        }
        .timeline-section {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          gap: 2rem;
          max-width: 1100px;
          margin: 0 auto;
        }
        .timeline-wrapper {
          flex: 1;
          position: relative;
          padding: 0 20px;
        }
        .timeline-wrapper h2 {
          text-align: center;
          margin-bottom: 1.5rem;
          font-size: 1.2rem;
          color: #333;
        }
        .timeline-wrapper::before {
          content: '';
