import React, { useState, useEffect } from 'react';
import Confetti from 'react-confetti';

/**
 * Group timeline items by "year marker."
 * A year marker is identified by an item that has no company/content.
 */
function groupTimeline(timeline) {
  const groups = [];
  let currentGroup = null;

  timeline.forEach((item) => {
    // If it's a year marker => start a new group
    if (!item.company && !item.content) {
      // if there's an existing group, push it first
      if (currentGroup) {
        groups.push(currentGroup);
      }
      currentGroup = { marker: item.date, items: [] };
    } else {
      // normal item
      if (!currentGroup) {
        // if no group started yet, create a default
        currentGroup = { marker: '', items: [] };
      }
      currentGroup.items.push(item);
    }
  });

  // push the final group
  if (currentGroup) {
    groups.push(currentGroup);
  }

  return groups;
}

function Showcase() {
  // Confetti control
  const [showConfetti, setShowConfetti] = useState(false);

  // Window size for Confetti
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  useEffect(() => {
    function handleResize() {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    }
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Original timeline data
  const llmTimeline = [
    { date: '2022, Pre-training / Scaling Law / Chatbot' },
    { date: 'Mar 15th, 2022', company: 'OpenAI', content: 'OpenAI released GPT-3.5 text-davinci-002 API' },
    { date: 'Nov 30th, 2022', company: 'OpenAI', content: 'OpenAI released ChatGPT with GPT-3.5' },
    { date: '2023, Post-training / Agent' },
    { date: 'Mar 14th, 2023', company: 'OpenAI', content: 'OpenAI released GPT-4' },
    { date: '2024, Reasoning' },
    { date: 'Sep 20th, 2024', company: 'OpenAI', content: 'OpenAI released o1-preview, 1st reasoning model' },
    { date: '2025, Open source' },
    { date: 'Jan 20th, 2025', company: 'Deepseek', content: 'Deepseek released Deepseek-R1, 1st open-weights reasoning model' }
  ];

  const robotTimeline = [
    { date: '2025, Mutli-robot Framework' },
    { date: 'Feb 20th, 2025', company: 'Figure', content: 'Figure released reasoning robot with collaboration; valuation jumped from $2.6B (Cursor $2.5B) to $39.5B (Anthropic $60B)' },
  ];

  // 1) Group the timeline data
  const llmGroups = groupTimeline(llmTimeline);
  const robotGroups = groupTimeline(robotTimeline);

  // 2) Track open/closed state for each group
  const [llmOpenStates, setLlmOpenStates] = useState(llmGroups.map(() => true));
  const [robotOpenStates, setRobotOpenStates] = useState(robotGroups.map(() => true));

  // Toggle LLM group
  function toggleLlmGroup(index) {
    setLlmOpenStates((prev) => {
      const copy = [...prev];
      copy[index] = !copy[index];
      return copy;
    });
  }

  // Toggle Robot group
  function toggleRobotGroup(index) {
    setRobotOpenStates((prev) => {
      const copy = [...prev];
      copy[index] = !copy[index];
      return copy;
    });
  }

  // Return image path by company
  function getIconByCompany(company) {
    switch (company) {
      case 'OpenAI':
        return '/images/OpenAI.png';
      case 'Deepseek':
        return '/images/Deepseek.png';
      case 'Figure':
        return '/images/Figure.png';
      default:
        return '/images/OpenAI.png'; // fallback
    }
  }

  return (
    <div style={{ position: 'relative', padding: '20px' }}>
      <style>{`
        * {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
        }
        body {
          font-family: Arial, sans-serif;
        }
        .showcase-header {
          text-align: center;
          margin-bottom: 2rem;
        }
        .showcase-header h1 {
          font-size: 2rem;
          margin-bottom: 0.5rem;
        }
        .showcase-header h2 {
          font-size: 1.3rem;
          font-weight: 400;
          margin-bottom: 1.5rem;
          color: #666;
        }
        .showcase-header button {
          background-color: #ff7f50;
          color: #fff;
          border: none;
          padding: 10px 20px;
          cursor: pointer;
          font-size: 1rem;
          border-radius: 8px;
          transition: background-color 0.3s;
        }
        .showcase-header button:hover {
          background-color: #ff9b75;
        }

        .showcase-text {
          max-width: 800px;
          margin: 0 auto 3rem auto;
          text-align: left;
          line-height: 1.6;
          font-size: 1rem;
          color: #333;
        }

        /* Timeline container: two columns side by side */
        .timeline-section {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          gap: 2rem;
          max-width: 1100px;
          margin: 0 auto;
        }

        /* Each timeline wrapper */
        .timeline-wrapper {
          flex: 1;
          position: relative;
          padding: 0 20px;
        }

        .timeline-wrapper h2 {
          text-align: center;
          margin-bottom: 1.5rem;
        }

        /* Vertical line for each timeline */
        .timeline-wrapper::before {
          content: '';
          position: absolute;
          left: 30px;
          top: 0;
          width: 2px;
          height: 100%;
          background-color: #ccc;
        }

        /* Common timeline item style */
        .timeline-item {
          position: relative;
          margin: 0.8rem 0;
          padding-left: 70px; /* space for icon */
          opacity: 0; /* for animation start */
          animation-duration: 0.8s;
          animation-fill-mode: forwards;
        }

        /* The icon container on the left */
        .timeline-icon {
          position: absolute;
          left: 10px;
          top: 10px;
          width: 40px;
          height: 40px;
          border-radius: 50%;
          overflow: hidden;
          display: flex;
          align-items: center;
          justify-content: center;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
          background-color: #fff;
        }

        .timeline-icon img {
          width: 100%;
          height: auto;
          object-fit: contain;
        }

        .timeline-details {
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
        }

        .timeline-date {
          font-weight: bold;
          color: #555;
        }

        /* Add a left border to highlight the content */
        .timeline-content {
          background-color: #f7f7f7;
          padding: 10px 15px;
          border-radius: 8px;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
          border-left: 3px solid #ff7f50;
        }

        /* Dot on the vertical line */
        .timeline-item::before {
          content: '';
          position: absolute;
          left: 27px;
          top: 20px;
          width: 10px;
          height: 10px;
          background-color: #ff7f50;
          border-radius: 50%;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
        }

        /* Keyframes for the animations */
        @keyframes fadeInLeft {
          from {
            transform: translateX(-30px);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }
        @keyframes fadeInRight {
          from {
            transform: translateX(30px);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }

        .fade-in-left {
          animation-name: fadeInLeft;
        }
        .fade-in-right {
          animation-name: fadeInRight;
        }

        /* YEAR MARKER style */
        .year-marker {
          padding-left: 40px;
          margin: 0.5rem 0;
          font-size: 1rem;
          font-weight: bold;
          color: #444;
          text-align: left;
          background: #f0f0f0;
          display: inline-block;
          border-radius: 4px;
          padding: 4px 8px;
          cursor: pointer; /* make it clickable */
        }
        /* Remove dot for year marker */
        .year-marker::before {
          display: none;
        }
        /* No icon for year marker */
        .year-marker .timeline-icon {
          display: none;
        }
      `}</style>

      {/* Confetti */}
      {showConfetti && (
        <Confetti
          width={windowSize.width}
          height={windowSize.height}
        />
      )}

      {/* Header */}
      <div className="showcase-header">
        <h1>Robot in 2025 as GenAI in 2022</h1>
        <h2>Riding the robot wave with lessons learned from GenAI boom</h2>
        <button onClick={() => setShowConfetti(!showConfetti)}>
          {showConfetti ? 'Stop Confetti' : 'Celebrate!'}
        </button>
      </div>

      {/* Text Section */}
      <div className="showcase-text">
        <p>
          Don't regret missing the GenAI boom—we are about to embrace an even bigger robotics wave soon.
        </p>
        <p>
          Over the past year, we witnessed many AI advancements: VLM, open-source reasoning models,
          and multi-agent frameworks. All of these point towards robotics as a key application,
          as reflected in Figure’s valuation surge from $2.6B (Feb 2024) to $39.5B (Feb 2025).
          With great waves come loud tides—too many exciting opportunities in the robot wave
          makes it difficult to focus on the right things.
        </p>
        <p>
          Yet, when we look back at the history of GenAI, things becomes much clearer.
          Let’s discuss together the trial and error in GenAI back in 2022 to better ride the robot wave in 2025.
        </p>
      </div>

      {/* Timeline Section */}
      <div className="timeline-section">
        {/* LLM Timeline */}
        <div className="timeline-wrapper">
          <h2>LLM</h2>
          {llmGroups.map((group, groupIndex) => {
            const isMarkerPresent = !!group.marker; // if group.marker is not empty
            const isOpen = llmOpenStates[groupIndex];

            return (
              <div key={groupIndex}>
                {/* Year marker (if any) */}
                {isMarkerPresent && (
                  <div
                    className="timeline-item year-marker fade-in-left"
                    style={{ animationDelay: `${groupIndex * 0.2}s` }}
                    onClick={() => toggleLlmGroup(groupIndex)}
                  >
                    {group.marker}
                  </div>
                )}

                {/* Render items only if group is open */}
                {isOpen && group.items.map((item, itemIndex) => (
                  <div
                    className="timeline-item fade-in-left"
                    style={{ animationDelay: `${(groupIndex + itemIndex) * 0.2}s` }}
                    key={itemIndex}
                  >
                    <div className="timeline-icon">
                      <img src={getIconByCompany(item.company)} alt={item.company} />
                    </div>
                    <div className="timeline-details">
                      <div className="timeline-date">{item.date}</div>
                      <div className="timeline-content">{item.content}</div>
                    </div>
                  </div>
                ))}
              </div>
            );
          })}
        </div>

        {/* Robot Timeline */}
        <div className="timeline-wrapper">
          <h2>Robot</h2>
          {robotGroups.map((group, groupIndex) => {
            const isMarkerPresent = !!group.marker;
            const isOpen = robotOpenStates[groupIndex];

            return (
              <div key={groupIndex}>
                {/* Year marker (if any) */}
                {isMarkerPresent && (
                  <div
                    className="timeline-item year-marker fade-in-right"
                    style={{ animationDelay: `${groupIndex * 0.2}s` }}
                    onClick={() => toggleRobotGroup(groupIndex)}
                  >
                    {group.marker}
                  </div>
                )}

                {/* Render items only if group is open */}
                {isOpen && group.items.map((item, itemIndex) => (
                  <div
                    className="timeline-item fade-in-right"
                    style={{ animationDelay: `${(groupIndex + itemIndex) * 0.2}s` }}
                    key={itemIndex}
                  >
                    <div className="timeline-icon">
                      <img src={getIconByCompany(item.company)} alt={item.company} />
                    </div>
                    <div className="timeline-details">
                      <div className="timeline-date">{item.date}</div>
                      <div className="timeline-content">{item.content}</div>
                    </div>
                  </div>
                ))}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Showcase;
