import React, { useState } from 'react';

function Simulator() {
  // 游戏状态
  const [playerCards, setPlayerCards] = useState([]);
  const [bankerCards, setBankerCards] = useState([]);
  const [result, setResult] = useState('');
  const [balance, setBalance] = useState(1000);
  const [betType, setBetType] = useState('');
  const [betAmount, setBetAmount] = useState(0);
  const [message, setMessage] = useState('');

  // 牌组：1～9 为对应数字，10/J/Q/K 均记作 0
  const cardDeck = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0, 0, 0, 0];

  // 随机抽一张牌
  function drawCard() {
    const randomIndex = Math.floor(Math.random() * cardDeck.length);
    return cardDeck[randomIndex];
  }

  // 计算牌点数（取个位数）
  function modSum(cards) {
    return cards.reduce((acc, card) => acc + card, 0) % 10;
  }

  // 模拟一局游戏（百家乐规则）
  function simulateGame() {
    let pCards = [drawCard(), drawCard()];
    let bCards = [drawCard(), drawCard()];
    let playerTotal = modSum(pCards);
    let bankerTotal = modSum(bCards);
    let playerThird = null;

    // 如果任一方拿到自然牌（8 或 9）则不补牌
    if (!(playerTotal === 8 || playerTotal === 9 || bankerTotal === 8 || bankerTotal === 9)) {
      // 玩家：0～5 补一张牌
      if (playerTotal <= 5) {
        playerThird = drawCard();
        pCards.push(playerThird);
        playerTotal = modSum(pCards);
      }
      // 庄家规则
      if (bankerTotal <= 2) {
        bCards.push(drawCard());
        bankerTotal = modSum(bCards);
      } else {
        if (playerThird === null) {
          if (bankerTotal <= 5) {
            bCards.push(drawCard());
            bankerTotal = modSum(bCards);
          }
        } else {
          const pt = playerThird;
          if (bankerTotal === 3) {
            if (pt !== 8) {
              bCards.push(drawCard());
              bankerTotal = modSum(bCards);
            }
          } else if (bankerTotal === 4) {
            if (pt >= 2 && pt <= 7) {
              bCards.push(drawCard());
              bankerTotal = modSum(bCards);
            }
          } else if (bankerTotal === 5) {
            if (pt >= 4 && pt <= 7) {
              bCards.push(drawCard());
              bankerTotal = modSum(bCards);
            }
          } else if (bankerTotal === 6) {
            if (pt === 6 || pt === 7) {
              bCards.push(drawCard());
              bankerTotal = modSum(bCards);
            }
          }
          // 庄家7点时不补牌
        }
      }
    }

    // 判定胜负
    let gameResult = '';
    if (playerTotal > bankerTotal) {
      gameResult = 'Player';
    } else if (bankerTotal > playerTotal) {
      gameResult = 'Banker';
    } else {
      gameResult = 'Tie';
    }

    setPlayerCards(pCards);
    setBankerCards(bCards);
    setResult(gameResult);
    resolveBet(gameResult);
  }

  // 根据下注和游戏结果更新余额
  function resolveBet(gameResult) {
    if (!betType || betAmount <= 0) {
      setMessage('请先下注！');
      return;
    }
    let newBalance = balance;
    if (gameResult === betType) {
      newBalance += betAmount;
      setMessage(`恭喜，你赢了 ${betAmount}！`);
    } else {
      newBalance -= betAmount;
      setMessage(`很遗憾，你输了 ${betAmount}！`);
    }
    setBalance(newBalance);
  }

  // 下注并开始游戏
  function handleBetSubmit(e) {
    e.preventDefault();
    if (betAmount > balance) {
      setMessage('下注金额不能超过余额！');
      return;
    }
    simulateGame();
  }

  // 重置桌面（不重置余额）
  function resetGame() {
    setPlayerCards([]);
    setBankerCards([]);
    setResult('');
    setMessage('');
  }

  // 外层容器样式（全屏暗色背景）
  const outerStyle = {
    minHeight: '100vh',
    background: '#2b2b2b',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  };

  // 牌桌样式（使用背景图片模拟真实桌面，可替换为真实图片 URL）
  const tableStyle = {
    width: '800px',
    height: '600px',
    background: 'url("https://via.placeholder.com/800x600/006400/ffffff?text=牌桌") no-repeat center/cover',
    borderRadius: '20px',
    position: 'relative',
    boxShadow: '0 0 20px rgba(0,0,0,0.5)'
  };

  // 庄家牌区（上方居中）
  const bankerAreaStyle = {
    position: 'absolute',
    top: '20px',
    left: '50%',
    transform: 'translateX(-50%)',
    textAlign: 'center',
    color: 'white'
  };

  // 玩家牌区（下方居中）
  const playerAreaStyle = {
    position: 'absolute',
    bottom: '20px',
    left: '50%',
    transform: 'translateX(-50%)',
    textAlign: 'center',
    color: 'white'
  };

  // 下注区（牌桌中间，半透明背景）
  const bettingAreaStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    textAlign: 'center',
    color: 'white',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    padding: '20px',
    borderRadius: '10px'
  };

  // 牌的样式
  const cardStyle = {
    width: '60px',
    height: '90px',
    border: '2px solid #fff',
    borderRadius: '8px',
    backgroundColor: '#fff',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '24px',
    color: '#000',
    margin: '0 5px'
  };

  return (
    <div style={outerStyle}>
      <div style={tableStyle}>
        {/* 庄家牌区 */}
        <div style={bankerAreaStyle}>
          <h2>庄家</h2>
          <div style={{ display: 'flex', justifyContent: 'center' }}>
            {bankerCards.map((card, index) => (
              <div key={index} style={cardStyle}>
                {card}
              </div>
            ))}
          </div>
          <p>总点数: {bankerCards.length ? modSum(bankerCards) : 0}</p>
        </div>

        {/* 下注及控制区 */}
        <div style={bettingAreaStyle}>
          <p>当前余额: {balance}</p>
          <form onSubmit={handleBetSubmit}>
            <div>
              <label>
                下注金额:
                <input
                  type="number"
                  value={betAmount}
                  onChange={(e) => setBetAmount(Number(e.target.value))}
                  min="1"
                  style={{ marginLeft: '10px', width: '80px' }}
                />
              </label>
            </div>
            <div style={{ marginTop: '10px' }}>
              <label style={{ marginRight: '10px' }}>
                <input
                  type="radio"
                  name="betType"
                  value="Player"
                  onChange={(e) => setBetType(e.target.value)}
                  checked={betType === 'Player'}
                />
                玩家
              </label>
              <label style={{ marginRight: '10px' }}>
                <input
                  type="radio"
                  name="betType"
                  value="Banker"
                  onChange={(e) => setBetType(e.target.value)}
                  checked={betType === 'Banker'}
                />
                庄家
              </label>
              <label>
                <input
                  type="radio"
                  name="betType"
                  value="Tie"
                  onChange={(e) => setBetType(e.target.value)}
                  checked={betType === 'Tie'}
                />
                和局
              </label>
            </div>
            <button type="submit" style={{ marginTop: '10px' }}>
              下注并发牌
            </button>
          </form>
          {message && <p style={{ marginTop: '10px' }}>{message}</p>}
          <button onClick={resetGame} style={{ marginTop: '10px' }}>
            重置桌面
          </button>
        </div>

        {/* 玩家牌区 */}
        <div style={playerAreaStyle}>
          <h2>玩家</h2>
          <div style={{ display: 'flex', justifyContent: 'center' }}>
            {playerCards.map((card, index) => (
              <div key={index} style={cardStyle}>
                {card}
              </div>
            ))}
          </div>
          <p>总点数: {playerCards.length ? modSum(playerCards) : 0}</p>
        </div>
      </div>
    </div>
  );
}

export default Simulator;
