import React, { useRef, useEffect, useState } from 'react';

function Pinball() {
  const canvasRef = useRef(null);
  // 弹珠物理状态及上次碰撞时间
  const ballRef = useRef({
    x: 50,
    y: 300,
    vx: 0,
    vy: 0,
    radius: 10,
    lastCollisionTime: 0,
  });

  // 发射控制（角度和力度），初始值可自行调整
  const [angle, setAngle] = useState(45);
  const [power, setPower] = useState(15);
  // 当前碰撞提示消息（在暂停时显示）
  const [collisionMsg, setCollisionMsg] = useState('');
  // 是否暂停（碰撞后暂停，等待用户点击确定继续）
  const [isPaused, setIsPaused] = useState(false);

  // 定义多个障碍物（颜色不同、体积较小）
  const bumpers = [
    { id: 1, x: 200, y: 150, radius: 10, elasticity: 1.2, color: 'blue' },
    { id: 2, x: 350, y: 250, radius: 8, elasticity: 1.2, color: 'green' },
    { id: 3, x: 150, y: 100, radius: 12, elasticity: 1.2, color: 'purple' },
    { id: 4, x: 300, y: 80, radius: 10, elasticity: 1.2, color: 'orange' },
    { id: 5, x: 100, y: 200, radius: 8, elasticity: 1.2, color: 'pink' },
    { id: 6, x: 400, y: 150, radius: 10, elasticity: 1.2, color: 'cyan' },
  ];

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;
    const gravity = 0.2; // 重力加速度

    const update = () => {
      if (isPaused) return;

      const ball = ballRef.current;
      ball.vy += gravity;
      ball.x += ball.vx;
      ball.y += ball.vy;

      // 底部边界检测
      if (ball.y + ball.radius > canvas.height) {
        ball.y = canvas.height - ball.radius;
        ball.vy = -ball.vy * 0.8;
        ball.vx *= 0.9;
      }
      // 顶部边界检测
      if (ball.y - ball.radius < 0) {
        ball.y = ball.radius;
        ball.vy = -ball.vy * 0.8;
      }
      // 左右墙壁检测
      if (ball.x - ball.radius < 0) {
        ball.x = ball.radius;
        ball.vx = -ball.vx * 0.8;
      } else if (ball.x + ball.radius > canvas.width) {
        ball.x = canvas.width - ball.radius;
        ball.vx = -ball.vx * 0.8;
      }

      // 遍历每个障碍物，检测碰撞
      bumpers.forEach(bumper => {
        const dx = ball.x - bumper.x;
        const dy = ball.y - bumper.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        if (distance < ball.radius + bumper.radius) {
          // 如果已处于暂停状态则不重复触发
          if (isPaused) return;
          // 计算碰撞法线
          const nx = dx / distance;
          const ny = dy / distance;
          const overlap = ball.radius + bumper.radius - distance;
          ball.x += nx * overlap;
          ball.y += ny * overlap;
          // 简单反射计算
          const dot = ball.vx * nx + ball.vy * ny;
          ball.vx = ball.vx - 2 * dot * nx;
          ball.vy = ball.vy - 2 * dot * ny;
          ball.vx *= bumper.elasticity;
          ball.vy *= bumper.elasticity;

          // 更新碰撞信息，并暂停游戏，等待用户点击确定后继续
          const now = Date.now();
          if (now - ball.lastCollisionTime > 500) {
            ball.lastCollisionTime = now;
            setCollisionMsg(`撞到了障碍物 ${bumper.id}`);
            setIsPaused(true);
          }
        }
      });
    };

    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      // 绘制障碍物
      bumpers.forEach(bumper => {
        ctx.beginPath();
        ctx.arc(bumper.x, bumper.y, bumper.radius, 0, Math.PI * 2);
        ctx.fillStyle = bumper.color;
        ctx.fill();
        ctx.closePath();
      });
      // 绘制弹珠
      const ball = ballRef.current;
      ctx.beginPath();
      ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
      ctx.fillStyle = 'red';
      ctx.fill();
      ctx.closePath();
    };

    const loop = () => {
      update();
      draw();
      animationFrameId = requestAnimationFrame(loop);
    };
    animationFrameId = requestAnimationFrame(loop);
    return () => cancelAnimationFrame(animationFrameId);
  }, [bumpers, isPaused]);

  // 发射弹珠：根据角度和力度设置初始速度
  const launchBall = () => {
    const rad = (angle * Math.PI) / 180;
    ballRef.current = {
      x: 50,
      y: 300,
      vx: power * Math.cos(rad),
      vy: -power * Math.sin(rad),
      radius: 10,
      lastCollisionTime: 0,
    };
    setIsPaused(false);
    setCollisionMsg('');
  };

  // 点击对话框“确定”恢复运动
  const resumeGame = () => {
    setIsPaused(false);
    setCollisionMsg('');
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ position: 'relative' }}>
        <canvas
          ref={canvasRef}
          width={500}
          height={400}
          style={{ border: '1px solid #000' }}
        ></canvas>
        {isPaused && collisionMsg && (
          <div
            style={{
              position: 'absolute',
              top: '40%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              backgroundColor: 'rgba(255,255,255,0.9)',
              border: '1px solid #ccc',
              padding: '20px',
              zIndex: 10,
              textAlign: 'center',
            }}
          >
            <p>{collisionMsg}</p>
            <button onClick={resumeGame}>确定</button>
          </div>
        )}
      </div>
      {/* 控制面板放在画布下方，使用滑动条代替输入框 */}
      <div style={{ marginTop: 20, display: 'flex', alignItems: 'center' }}>
        <div style={{ marginRight: 20 }}>
          <span>发射角度 (°): {angle}</span>
          <input
            type="range"
            min="0"
            max="90"
            value={angle}
            onChange={(e) => setAngle(Number(e.target.value))}
            style={{ width: 150, marginLeft: 10 }}
          />
        </div>
        <div style={{ marginRight: 20 }}>
          <span>发射力度: {power}</span>
          <input
            type="range"
            min="5"
            max="30"
            value={power}
            onChange={(e) => setPower(Number(e.target.value))}
            style={{ width: 150, marginLeft: 10 }}
          />
        </div>
        <button onClick={launchBall} style={{ fontSize: '16px', padding: '10px 20px' }}>
          🚀 发射弹珠
        </button>
      </div>
    </div>
  );
}

export default Pinball;
