import React, { useState } from 'react';

function EventItem({ event }) {
  const [hover, setHover] = useState(false);

  return (
    <div
      style={{ position: 'relative', marginBottom: '20px', cursor: 'pointer' }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      <div
        style={{
          width: '12px',
          height: '12px',
          borderRadius: '50%',
          backgroundColor: '#007BFF',
          margin: '0 auto',
        }}
      ></div>
      <div style={{ textAlign: 'center', fontSize: '12px', marginTop: '5px' }}>
        {event.date}
      </div>
      {hover && (
        <div
          style={{
            position: 'absolute',
            top: '-70px',
            left: '50%',
            transform: 'translateX(-50%)',
            padding: '8px',
            backgroundColor: '#fff',
            border: '1px solid #ccc',
            borderRadius: '4px',
            whiteSpace: 'nowrap',
            zIndex: 10,
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
          }}
        >
          <strong>{event.title}</strong>
          <div style={{ fontSize: '12px', marginTop: '4px' }}>{event.detail}</div>
        </div>
      )}
    </div>
  );
}

function Timeline({ wave }) {
  // 设定时间线总高度（单位：像素）
  const timelineHeight = 300;
  const totalEvents = wave.events.length;

  return (
    <div style={{ margin: '10px', flex: '1', textAlign: 'center' }}>
      <h2 style={{ fontSize: '16px', marginBottom: '20px' }}>{wave.wave}</h2>
      <div
        style={{
          position: 'relative',
          margin: '0 auto',
          width: '2px',
          backgroundColor: '#ddd',
          height: timelineHeight + 'px',
        }}
      >
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {wave.events.map((event, index) => {
            const pos = totalEvents === 1 ? timelineHeight / 2 : (index / (totalEvents - 1)) * timelineHeight;
            return (
              <div key={index} style={{ position: 'absolute', top: pos + 'px', width: '100%' }}>
                <EventItem event={event} />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

function Showcase() {
  const waves = [
    {
      wave: '个人电脑浪潮 (1979-1982)',
      events: [
        { date: '1979-05-15', title: '早期家用电脑兴起', detail: '家用电脑开始在实验室和部分家庭中试水。' },
        { date: '1980-07-10', title: 'IBM 投入研发', detail: 'IBM 组建团队，进入个人电脑领域。' },
        { date: '1981-08-12', title: 'IBM PC 推出', detail: 'IBM PC 正式面市，开启商业化时代。' },
        { date: '1982-11-05', title: 'MS-DOS 生态繁荣', detail: '克隆机市场兴起，MS-DOS 生态逐步成熟。' },
      ],
    },
    {
      wave: '互联网浪潮 (1994-1997)',
      events: [
        { date: '1994-04-03', title: '网景浏览器发布', detail: 'Netscape 浏览器大幅降低上网门槛。' },
        { date: '1995-09-14', title: '雅虎成立', detail: '雅虎成立，推动内容聚合与搜索。' },
        { date: '1996-06-30', title: '宽带普及', detail: '基础设施升级，互联网用户显著增加。' },
        { date: '1997-11-21', title: '门户网站爆发', detail: '新浪、搜狐等门户网站开始崛起。' },
      ],
    },
    {
      wave: '智能手机浪潮 (2007-2010)',
      events: [
        { date: '2007-01-09', title: 'iPhone 发布', detail: '苹果发布首款 iPhone，重新定义手机体验。' },
        { date: '2008-09-23', title: 'Android 推出', detail: 'Android 系统面世，开启多厂商竞争。' },
        { date: '2009-05-15', title: '用户激增', detail: '智能手机用户迅速攀升，市场规模爆发。' },
        { date: '2010-11-18', title: '生态系统爆发', detail: '移动应用生态逐步完善，推动产业升级。' },
      ],
    },
    {
      wave: '生成式 AI 浪潮 (2019-2022)',
      events: [
        { date: '2019-06-20', title: 'Transformer 成熟', detail: 'Transformer 模型在多个任务上展现出强大性能。' },
        { date: '2020-07-15', title: 'GPT-3 发布', detail: 'GPT-3 推动大规模预训练模型的应用。' },
        { date: '2021-08-01', title: '多模态研究', detail: '文本、图像、语音等多模态 AI 研究活跃。' },
        { date: '2022-11-30', title: 'ChatGPT 爆红', detail: 'ChatGPT 的广泛应用引发公众热议。' },
      ],
    },
    {
      wave: '电动车浪潮 (2015-2018)',
      events: [
        { date: '2015-03-01', title: 'Model S 推出', detail: '特斯拉推出 Model S，标志高性能电动车时代来临。' },
        { date: '2016-05-20', title: '传统车企布局', detail: '越来越多的传统车企开始研发电动车。' },
        { date: '2017-07-10', title: '电池成本下降', detail: '锂电池技术进步，成本大幅下降。' },
        { date: '2018-09-15', title: '基础设施完善', detail: '充电设施加速建设，缓解里程焦虑。' },
      ],
    },
  ];

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ textAlign: 'center' }}>科技浪潮早期阶段互动时间线</h1>
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-around',
          flexWrap: 'wrap',
          marginTop: '40px',
        }}
      >
        {waves.map((wave, index) => (
          <Timeline key={index} wave={wave} />
        ))}
      </div>
    </div>
  );
}

export default Showcase;
