import React, {
  useState,
  useEffect,
  useRef,
  useCallback,
  useMemo,
  useLayoutEffect,
} from 'react';

/** ç”Ÿæˆéšæœº ID çš„å°å·¥å…·å‡½æ•°ï¼Œç”¨äºç»™æ–°æ·»åŠ çš„äº‹ä»¶åˆ†é…ç¨³å®šçš„ event.id */
function generateUniqueId() {
  return Math.random().toString(36).substr(2, 9);
}

/** 
 * Helper functionï¼šç”Ÿæˆä¸€ä¸ªç®€å•çš„è´å¡å°”æ›²çº¿è·¯å¾„
 */
function generateCurvePath(x1, y1, x2, y2) {
  const cx = (x1 + x2) / 2;
  return `M ${x1},${y1} C ${cx},${y1} ${cx},${y2} ${x2},${y2}`;
}

/**
 * æ ¹æ® wave.title è¿”å›å¯¹åº”çš„icon
 * - PC => ğŸ’», Internet => ğŸŒ, Smartphones => ğŸ“±, EVs => ğŸš—
 * - å«æœ‰GenAI => ğŸ’¡, å«æœ‰Robot => ğŸ¤–
 * - å…¶ä½™é»˜è®¤ä¸º ğŸ’»
 */
function getWaveIcon(title) {
  const lower = title.toLowerCase();
  if (lower.includes('genai')) return 'ğŸ’¡';
  if (lower.includes('robot')) return 'ğŸ¤–';
  if (lower.includes('pc')) return 'ğŸ’»';
  if (lower.includes('internet')) return 'ğŸŒ';
  if (lower.includes('smartphones')) return 'ğŸ“±';
  if (lower.includes('evs')) return 'ğŸš—';
  return 'ğŸ’»';
}

/**
 * Modal Component for adding new events
 */
function AddEventModal({ isOpen, onClose, onSubmit, timelineOptions = [] }) {
  const [date, setDate] = useState('');
  const [title, setTitle] = useState('');
  const [detail, setDetail] = useState('');
  const [selectedTimeline, setSelectedTimeline] = useState(0);

  // Modal æ‰“å¼€æ—¶ï¼Œé»˜è®¤é€‰ä¸­ç¬¬ä¸€ä¸ª
  useEffect(() => {
    if (isOpen && timelineOptions.length > 0) {
      setSelectedTimeline(timelineOptions[0].index);
    }
  }, [isOpen, timelineOptions]);

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (date && title) {
      onSubmit({
        timelineIndex: selectedTimeline,
        date,
        title,
        detail,
      });
      onClose();
      // é‡ç½®
      setDate('');
      setTitle('');
      setDetail('');
    }
  };

  return (
    <div style={overlayStyle}>
      <div style={modalStyle}>
        <h3>Add New Event</h3>
        <form onSubmit={handleSubmit}>
          {/* ä¸‹æ‹‰èœå• */}
          {timelineOptions.length > 0 && (
            <div style={formItemStyle}>
              <label style={labelStyle}>Select Timeline:</label>
              <select
                value={selectedTimeline}
                onChange={(e) => setSelectedTimeline(parseInt(e.target.value))}
                style={selectStyle}
              >
                {timelineOptions.map((option, idx) => (
                  <option key={idx} value={option.index}>
                    {option.title}
                  </option>
                ))}
              </select>
            </div>
          )}

          <div style={formItemStyle}>
            <label style={labelStyle}>Date (YYYY-MM-DD):</label>
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              style={inputStyle}
              required
            />
          </div>
          <div style={formItemStyle}>
            <label style={labelStyle}>Title:</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              style={inputStyle}
              required
            />
          </div>
          <div style={formItemStyle}>
            <label style={labelStyle}>Details:</label>
            <textarea
              value={detail}
              onChange={(e) => setDetail(e.target.value)}
              style={{ ...inputStyle, minHeight: '100px' }}
            />
          </div>
          <div style={btnContainerStyle}>
            <button type="button" onClick={onClose} style={cancelBtnStyle}>
              Cancel
            </button>
            <button type="submit" style={submitBtnStyle}>
              Add Event
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

/**
 * ConnectionLineï¼šç»˜åˆ¶ç°è‰²è¿æ¥çº¿
 * - hoveræ—¶åŠ ç²—ã€æ–‡å­—å˜å¤§ã€é¢œè‰²æ›´æ·±
 */
function ConnectionLine({ fromPos, toPos, reason, onDelete }) {
  const [hovered, setHovered] = useState(false);

  // åœ¨æ‚¬åœæ—¶ï¼Œå°†çº¿æ¡å˜ç²—ã€æ–‡å­—å˜å¤§ã€é¢œè‰²åŠ æ·±
  const strokeColor = hovered ? '#444' : 'rgba(128,128,128,0.4)';
  const strokeWidth = hovered ? 4 : 2;
  const textColor = hovered ? '#333' : 'rgba(128,128,128,0.4)';
  const fontSize = hovered ? 16 : 12;
  const fontWeight = hovered ? 'bold' : 'normal';
  const pathData = generateCurvePath(fromPos.x, fromPos.y, toPos.x, toPos.y);

  const handleClick = (e) => {
    e.stopPropagation();
    if (window.confirm('Delete this connection?')) {
      onDelete();
    }
  };

  return (
    <g
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      onClick={handleClick}
      style={{ cursor: 'pointer' }}
    >
      <path
        d={pathData}
        stroke={strokeColor}
        strokeWidth={strokeWidth}
        fill="none"
        style={{
          pointerEvents: 'all',
          filter: hovered ? 'drop-shadow(0 0 4px rgba(0,0,0,0.2))' : 'none',
        }}
      />
      <text
        x={(fromPos.x + toPos.x) / 2}
        y={(fromPos.y + toPos.y) / 2 - 5}
        fill={textColor}
        fontSize={fontSize}
        fontWeight={fontWeight}
        textAnchor="middle"
        style={{
          pointerEvents: 'none',
          userSelect: 'none',
        }}
      >
        {reason}
      </text>
    </g>
  );
}

/** 
 * ç”¨æ¥ç”Ÿæˆä¸Šä¸‹äº¤é”™åç§»
 */
function getOffsets(count, gap = 80) {
  const offsets = [];
  const half = Math.floor(count / 2);
  for (let i = 0; i < count; i++) {
    offsets.push((i - half) * gap);
  }
  // å¶æ•°é¡¹è®©å®ƒä»¬å¯¹ç§°
  if (count % 2 === 0) {
    offsets.forEach((val, idx) => {
      offsets[idx] = val + gap / 2;
    });
  }
  return offsets;
}

/**
 * å…¨å±€æ‚¬æµ®å¼¹æ¡†
 */
function GlobalTooltip({ hoveredEvent }) {
  if (!hoveredEvent) return null;
  const { rect, title, detail } = hoveredEvent;
  if (!rect) return null;

  // è®© tooltip å±…ä¸­
  const left = rect.left + rect.width / 2 - 100;
  const top = rect.bottom + 8;

  return (
    <div
      style={{
        position: 'fixed',
        top,
        left,
        width: '200px',
        backgroundColor: '#fff',
        border: '1px solid #ddd',
        borderRadius: '6px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.15)',
        padding: '8px',
        fontSize: '12px',
        zIndex: 9999,
        pointerEvents: 'none',
      }}
    >
      <div style={{ fontWeight: 'bold', marginBottom: '4px' }}>{title}</div>
      <div>{detail}</div>
    </div>
  );
}

/**
 * åˆ¤æ–­æ˜¯å¦ä¸ºâ€œå¤±è´¥â€äº‹ä»¶ï¼šæ”¹ä¸ºä¾èµ– JSON ä¸­çš„ isFailure å­—æ®µ
 */
function isFailureEvent(ev) {
  return ev.isFailure === true;
}

/**
 * åˆ¤æ–­æ˜¯å¦ä¸ºâ€œé¢„æµ‹äº‹ä»¶(å°šæœªå‘ç”Ÿçš„)â€
 * è¿™é‡Œç®€å•é€šè¿‡æ—¥æœŸæ˜¯å¦å¤§äºâ€œä»Šå¤©â€æ¥åˆ¤æ–­
 */
function isFutureEvent(ev) {
  const eventTime = new Date(ev.date).getTime();
  const now = new Date().getTime();
  return eventTime > now;
}

/**
 * EventGroupï¼šä¸€ç»„ï¼ˆ<= 3ä¸ªæœˆå†…ï¼‰äº‹ä»¶åœ¨åŒä¸€æ°´å¹³ï¼Œå†…éƒ¨ä¸Šä¸‹äº¤é”™
 */
function EventGroup({
  events,
  onEventDoubleClick,
  onDelete,
  groupTop,
  setHoveredEvent,
  getEventRef,
}) {
  // æ’åºå¹¶è®¡ç®—åç§»
  const sortedEvents = useMemo(() => {
    return [...events].sort((a, b) => new Date(a.date) - new Date(b.date));
  }, [events]);

  const offsets = useMemo(() => {
    return getOffsets(sortedEvents.length, 80);
  }, [sortedEvents.length]);

  const handleMouseEnter = (ev) => {
    if (!ev.detail) return;
    const refEl = getEventRef(ev.id)?.current;
    if (refEl) {
      const rect = refEl.getBoundingClientRect();
      setHoveredEvent({ rect, title: ev.title, detail: ev.detail });
    }
  };

  return (
    <div
      style={{
        position: 'absolute',
        top: groupTop,
        left: 0,
        width: '280px',
        zIndex: 500,
      }}
    >
      {sortedEvents.map((ev, i) => {
        const offsetTop = offsets[i];
        const eventRef = getEventRef(ev.id);

        // æ ¹æ® JSON å­—æ®µåˆ¤æ–­æ˜¯å¦å¤±è´¥äº‹ä»¶
        const failure = isFailureEvent(ev);
        // åˆ¤æ–­æ˜¯å¦æœªæ¥äº‹ä»¶ï¼ˆé¢„æµ‹äº‹ä»¶ï¼‰
        const future = isFutureEvent(ev);

        // èƒŒæ™¯è‰²ç»Ÿä¸€ç™½è‰²ã€è¾¹æ¡†æ­£å¸¸
        const backgroundColor = '#fff';
        const borderColor = '#ddd';

        // æ ‡é¢˜æ–‡å­—é¢œè‰²é€»è¾‘ï¼š
        //  - æœªæ¥äº‹ä»¶(é¢„æµ‹äº‹ä»¶) => æ·±ç°è‰²(#333)
        //  - å¤±è´¥äº‹ä»¶ => æ·±çº¢è‰²(darkred)
        //  - é»˜è®¤ => è“è‰²(#007BFF)
        let titleColor = '#007BFF';
        if (failure) {
          titleColor = 'darkred';
        } else if (future) {
          titleColor = '#333';
        }

        return (
          <div
            key={ev.id}
            ref={eventRef}
            style={{
              position: 'absolute',
              left: '10px',
              top: offsetTop + 'px',
              width: '180px',
              height: '60px',
              padding: '10px',
              border: `2px solid ${borderColor}`,
              borderRadius: '8px',
              backgroundColor,
              color: '#000',
              // å¡ç‰‡æ·»åŠ äº›é˜´å½±
              boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
              cursor: 'pointer',
              transition: 'all 0.2s ease',
            }}
            // åŒå‡»äº‹ä»¶ï¼šå¼€å§‹æˆ–ç»“æŸè¿æ¥
            // å°†äº‹ä»¶idå’Œé¼ æ ‡äº‹ä»¶éƒ½ä¼ ä¸Šå»
            onDoubleClick={(e) => onEventDoubleClick(ev.id, e)}
            onMouseEnter={() => handleMouseEnter(ev)}
            onMouseLeave={() => setHoveredEvent(null)}
            // æ‚¬åœæ—¶æé«˜ zIndex ä»¥å…ç›¸äº’é®æŒ¡
            onMouseOver={(e) => (e.currentTarget.style.zIndex = 999)}
            onMouseOut={(e) => (e.currentTarget.style.zIndex = 1)}
          >
            {/* æ ‡é¢˜ */}
            <div
              style={{
                fontWeight: 'bold',
                marginBottom: '4px',
                fontSize: '14px',
                color: titleColor,
              }}
            >
              {ev.title}
            </div>
            {/* æ—¥æœŸ */}
            <div
              style={{
                fontSize: '13px',
                marginBottom: '6px',
                color: failure ? 'darkred' : '#555',
              }}
            >
              {ev.date}
            </div>

            {/* åˆ é™¤æŒ‰é’®ï¼ˆé¢œè‰²è“è‰²ï¼‰ */}
            <button
              onClick={(e) => {
                e.stopPropagation();
                onDelete(ev.id);
              }}
              style={{
                position: 'absolute',
                top: '6px',
                right: '6px',
                background: 'transparent',
                border: 'none',
                color: 'rgba(0,0,255,0.6)',
                fontSize: '14px',
                cursor: 'pointer',
                transition: 'color 0.2s ease',
              }}
            >
              Ã—
            </button>
          </div>
        );
      })}
    </div>
  );
}

/**
 * Timelineï¼šä¸€æ¡æ—¶é—´è½´ + æ ‡é¢˜ + å†…éƒ¨å¤šä¸ª EventGroup
 */
function Timeline({
  timelineIndex,
  wave,
  onEventDoubleClick,
  onDeleteEvent,
  setHoveredEvent,
  getEventRef,
}) {
  const timelineHeight = 1600;

  // è®¡ç®—æ—¶é—´çº¿èŒƒå›´
  const timestamps = wave.events.map((e) => new Date(e.date).getTime());
  const firstEventTime = timestamps.length > 0 ? Math.min(...timestamps) : Date.now();
  const firstEventDate = new Date(firstEventTime);
  const timelineStart = new Date(firstEventDate.getFullYear(), 0, 1).getTime();

  // 8å¹´è·¨åº¦ï¼Œä»…ä½œç¤ºèŒƒ
  const eightYearsInMs = 7.8 * 365.25 * 24 * 60 * 60 * 1000;
  const timelineEndTime = timelineStart + eightYearsInMs;
  const safeTimeSpan = timelineEndTime - timelineStart;

  // æŒ‰æ—¥æœŸæ’åº
  const sortedEvents = useMemo(() => {
    return [...wave.events].sort((a, b) => new Date(a.date) - new Date(b.date));
  }, [wave.events]);

  // å°†äº‹ä»¶åˆ†ç»„ï¼ˆ<= 3ä¸ªæœˆå†…ï¼‰
  const THREE_MONTHS_MS = 3 * 30 * 24 * 60 * 60 * 1000;
  const eventGroups = [];
  let currentGroup = [];

  sortedEvents.forEach((ev, index) => {
    if (index === 0) {
      currentGroup.push(ev);
    } else {
      const prevTime = new Date(sortedEvents[index - 1].date).getTime();
      const currTime = new Date(ev.date).getTime();
      if (currTime - prevTime <= THREE_MONTHS_MS) {
        currentGroup.push(ev);
      } else {
        eventGroups.push([...currentGroup]);
        currentGroup = [ev];
      }
    }
  });
  if (currentGroup.length > 0) {
    eventGroups.push(currentGroup);
  }

  // è®¡ç®—å„ç»„çºµå‘ä½ç½®ï¼Œé˜²æ­¢é‡å 
  let lastUsedPos = 0;
  const groupSpacing = 20;

  function estimateGroupHeight(group) {
    const n = group.length;
    // n ä¸ªäº‹ä»¶ => (n-1)ä¸ª 80px é—´éš” + 60px å¡ç‰‡é«˜åº¦
    return 80 * (n - 1) + 60;
  }

  const groupPositions = eventGroups.map((group) => {
    const firstEv = group[0];
    const currentTime = new Date(firstEv.date).getTime();
    const ratio = (currentTime - timelineStart) / safeTimeSpan;
    const naivePos = ratio * timelineHeight;
    const groupHeight = estimateGroupHeight(group);
    const finalPos = Math.max(naivePos, lastUsedPos + groupSpacing);
    lastUsedPos = finalPos + groupHeight;
    return finalPos;
  });

  // ç¡®å®šå›¾æ ‡
  const waveIcon = getWaveIcon(wave.title);

  // é¢„æµ‹æ—¶é—´çº¿æ ‡è¯†
  const predictedTag = wave.predictionTime
    ? ` (${wave.predictionTime})`
    : '';

  return (
    <div style={timelineWrapperStyle}>
      {/* æ ‡é¢˜é¢„ç•™é«˜åº¦ */}
      <div style={{ height: '80px' }}>
        <div style={timelineHeaderStyle}>
          <h2 style={timelineTitleStyle}>
            <span style={{ marginRight: '8px' }}>{waveIcon}</span>
            {wave.title}
            {predictedTag && (
              <span style={{ fontSize: '0.9rem', color: '#999', marginLeft: '8px' }}>
                {predictedTag}
              </span>
            )}
          </h2>
        </div>
      </div>

      <div style={timelinePeriodStyle}>{wave.period}</div>

      {/* ä¸»è½´ */}
      <div
        style={{
          position: 'relative',
          width: '2px',
          backgroundColor: '#bbb',
          height: timelineHeight + 'px',
          marginLeft: '0',
          marginTop: '-70px',
          borderRadius: '1px',
        }}
      >
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {eventGroups.map((group, idx) => (
            <EventGroup
              key={idx}
              events={group}
              onEventDoubleClick={onEventDoubleClick}
              onDelete={onDeleteEvent}
              groupTop={groupPositions[idx]}
              setHoveredEvent={setHoveredEvent}
              getEventRef={getEventRef}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

/**
 * ConnectionsLayerï¼šæ¸²æŸ“æ‰€æœ‰è¿æ¥çº¿ + è¿çº¿ä¸­
 */
function ConnectionsLayer({
  connections,
  eventPositions,
  containerRef,
  pendingConnection,
  pendingConnectionEnd,
  onDeleteConnection,
}) {
  const containerRect = containerRef.current
    ? containerRef.current.getBoundingClientRect()
    : { left: 0, top: 0 };

  return (
    <svg
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: 1,
        pointerEvents: 'none',
      }}
    >
      <defs>
        <marker
          id="arrowhead"
          markerWidth="10"
          markerHeight="7"
          refX="10"
          refY="3.5"
          orient="auto"
        >
          {/* ç®­å¤´ä¹Ÿæ”¹æˆç°è‰² */}
          <polygon points="0 0, 10 3.5, 0 7" fill="gray" />
        </marker>
      </defs>

      {/* å·²ç»ç¡®è®¤çš„è¿æ¥ */}
      {connections.map((conn, index) => {
        const fromPos = eventPositions[conn.from];
        const toPos = eventPositions[conn.to];
        if (!fromPos || !toPos) return null;

        // è°ƒæ•´åæ ‡ç›¸å¯¹ container
        const adjustedFrom = {
          x: fromPos.x - containerRect.left,
          y: fromPos.y - containerRect.top,
        };
        const adjustedTo = {
          x: toPos.x - containerRect.left,
          y: toPos.y - containerRect.top,
        };

        return (
          <ConnectionLine
            key={index}
            fromPos={adjustedFrom}
            toPos={adjustedTo}
            reason={conn.reason}
            onDelete={() => onDeleteConnection(index)}
          />
        );
      })}

      {/* å¦‚æœæ­£åœ¨è¿çº¿ï¼Œåˆ™ç”»ä¸´æ—¶çº¿ */}
      {pendingConnection &&
        pendingConnectionEnd &&
        eventPositions[pendingConnection.startEventId] && (() => {
          const startPosGlobal = eventPositions[pendingConnection.startEventId];
          const startPos = {
            x: startPosGlobal.x - containerRect.left,
            y: startPosGlobal.y - containerRect.top,
          };
          return (
            <line
              x1={startPos.x}
              y1={startPos.y}
              x2={pendingConnectionEnd.x}
              y2={pendingConnectionEnd.y}
              stroke="gray"
              strokeWidth="3"
              markerEnd="url(#arrowhead)"
              style={{ pointerEvents: 'none' }}
            />
          );
        })()}
    </svg>
  );
}

/**
 * ConnectionReasonModalï¼šå¡«å†™è¿æ¥åŸå› 
 */
function ConnectionReasonModal({ isOpen, onClose, onSubmit }) {
  const [reason, setReason] = useState('Manual connection');

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(reason || 'Manual connection');
    onClose();
  };

  return (
    <div style={overlayStyle}>
      <div style={modalStyle}>
        <h3>Connection Reason</h3>
        <form onSubmit={handleSubmit}>
          <div style={formItemStyle}>
            <label style={labelStyle}>Reason for connection:</label>
            <input
              type="text"
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              style={inputStyle}
              autoFocus
            />
          </div>
          <div style={btnContainerStyle}>
            <button type="button" onClick={onClose} style={cancelBtnStyle}>
              Cancel
            </button>
            <button type="submit" style={submitBtnStyle}>
              Create Connection
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

/**
 * Showcase ä¸»å®¹å™¨
 *  - çˆ¶ç»„ä»¶ç»Ÿä¸€ç®¡ç†ï¼šäº‹ä»¶ & è¿æ¥
 *  - çˆ¶ç»„ä»¶ç»Ÿä¸€æµ‹é‡ï¼šæ¯ä¸ªäº‹ä»¶å¡ç‰‡çš„DOMä½ç½®
 */
function Showcase() {
  const [waves, setWaves] = useState([]);
  const [connections, setConnections] = useState([]);
  const [eventPositions, setEventPositions] = useState({});

  // å“ªäº›æ—¶é—´çº¿ï¼ˆwaveï¼‰è¢«é€‰ä¸­æ˜¾ç¤º
  const [selectedWaveIndices, setSelectedWaveIndices] = useState([]);

  // å­˜æ”¾æ¯ä¸ªäº‹ä»¶å¡ç‰‡çš„ ref
  const eventRefs = useRef({});

  // ç»™å­ç»„ä»¶ç”¨çš„ getterï¼Œè¿”å›ä¸€ä¸ª refã€‚è‹¥æ²¡æœ‰åˆ™åˆ›å»º
  const getEventRef = useCallback((eventId) => {
    if (!eventRefs.current[eventId]) {
      eventRefs.current[eventId] = React.createRef();
    }
    return eventRefs.current[eventId];
  }, []);

  // æµ‹é‡æ‰€æœ‰äº‹ä»¶å¡ç‰‡ä½ç½®
  useLayoutEffect(() => {
    let changed = false;
    const newPositions = { ...eventPositions };

    Object.entries(eventRefs.current).forEach(([eventId, refObj]) => {
      if (refObj.current) {
        const rect = refObj.current.getBoundingClientRect();
        // å››èˆäº”å…¥ä»¥å‡å°‘æµ®ç‚¹åç§»
        const centerPos = {
          x: Math.round(rect.left + rect.width / 2),
          y: Math.round(rect.top + rect.height / 2),
        };
        const oldPos = newPositions[eventId];
        if (!oldPos || oldPos.x !== centerPos.x || oldPos.y !== centerPos.y) {
          changed = true;
          newPositions[eventId] = centerPos;
        }
      }
    });

    if (changed) {
      setEventPositions(newPositions);
    }
  });

  // è¿æ¥ä¸­çš„ä¸´æ—¶çŠ¶æ€
  // pendingConnection = { startEventId }
  const [pendingConnection, setPendingConnection] = useState(null);
  // æŒ‡ç¤ºè¿çº¿ç»ˆç‚¹ï¼ˆéšé¼ æ ‡ç§»åŠ¨ï¼‰çš„ä½ç½®
  const [pendingConnectionEnd, setPendingConnectionEnd] = useState(null);

  // ä¸¤ä¸ªModalï¼šæ·»åŠ äº‹ä»¶ã€æ·»åŠ è¿æ¥åŸå› 
  const [showGlobalAddModal, setShowGlobalAddModal] = useState(false);
  const [showConnectionModal, setShowConnectionModal] = useState(false);
  // è®°å½•ç¬¬äºŒæ¬¡åŒå‡»å¡ç‰‡ID
  const [pendingTo, setPendingTo] = useState(null);

  // tooltip
  const [hoveredEvent, setHoveredEvent] = useState(null);

  // çˆ¶å®¹å™¨ refï¼Œç”¨äºè®¡ç®—é¼ æ ‡ç›¸å¯¹ä½ç½®
  const containerRef = useRef(null);

  // åŠ è½½ç¤ºä¾‹æ•°æ®
  useEffect(() => {
    fetch('/data.json')
      .then((r) => r.json())
      .then((data) => {
        setWaves(data.waves || []);
        setConnections(data.connections || []);
      })
      .catch((err) => console.error('Error loading data:', err));
  }, []);

  // åœ¨ waves åŠ è½½å®Œåï¼Œé»˜è®¤å…¨éƒ¨é€‰ä¸­
  useEffect(() => {
    if (waves.length > 0) {
      setSelectedWaveIndices(waves.map((_, idx) => idx)); // å…¨é€‰
    }
  }, [waves]);

  /**
   * å¤„ç†åŒå‡»äº‹ä»¶å¡ç‰‡
   * - ç¬¬ä¸€æ¬¡åŒå‡»ï¼šå¼€å§‹è¿çº¿ï¼Œåˆå§‹åŒ– pendingConnectionï¼Œè®¾ç½®åˆå§‹ç®­å¤´ä½ç½®
   * - ç¬¬äºŒæ¬¡åŒå‡»ï¼šå¦‚æœæ˜¯ä¸åŒå¡ç‰‡ï¼Œåˆ™å¼¹å‡º reason å¯¹è¯æ¡†ï¼Œå¦åˆ™å–æ¶ˆ
   */
  const handleEventDoubleClick = (eventId, e) => {
    if (!pendingConnection) {
      // ç¬¬ä¸€æ¬¡åŒå‡»ï¼šå¼€å§‹è¿çº¿
      setPendingConnection({ startEventId: eventId });

      // è®©ç®­å¤´ä»é¼ æ ‡å¤„å¼€å§‹
      if (containerRef.current) {
        const containerRect = containerRef.current.getBoundingClientRect();
        const x = e.clientX - containerRect.left;
        const y = e.clientY - containerRect.top;
        setPendingConnectionEnd({ x, y });
      }
    } else {
      // ç¬¬äºŒæ¬¡åŒå‡»
      if (pendingConnection.startEventId === eventId) {
        // å¦‚æœç‚¹åˆ°åŒä¸€ä¸ª event => å–æ¶ˆ
        setPendingConnection(null);
        setPendingConnectionEnd(null);
      } else {
        // ä¸åŒäº‹ä»¶ => æ‰“å¼€ reason è¾“å…¥æ¡†
        setPendingTo(eventId);
        setShowConnectionModal(true);

        // ç«‹åˆ»åœæ­¢ç®­å¤´è·Ÿéšé¼ æ ‡
        setPendingConnectionEnd(null);
      }
    }
  };

  // æäº¤æ–°äº‹ä»¶
  const handleSubmitNewEvent = (data) => {
    const newId = generateUniqueId();
    setWaves((prev) => {
      const newWaves = [...prev];
      const idx = data.timelineIndex;
      if (newWaves[idx]) {
        const waveCopy = {
          ...newWaves[idx],
          events: [...newWaves[idx].events],
        };
        waveCopy.events.push({
          id: newId,
          date: data.date,
          title: data.title,
          detail: data.detail,
          // å¦‚æœéœ€è¦ä¹Ÿå¯è®¾ç½® isFailure: false
        });
        newWaves[idx] = waveCopy;
      }
      return newWaves;
    });
  };

  // æäº¤è¿æ¥åŸå› 
  const handleSubmitConnection = (reason) => {
    setConnections((prev) => [
      ...prev,
      { from: pendingConnection.startEventId, to: pendingTo, reason },
    ]);
    // é‡ç½®
    setPendingConnection(null);
    setPendingConnectionEnd(null);
    setPendingTo(null);
  };

  // åˆ é™¤äº‹ä»¶
  const handleDeleteEvent = (eventId) => {
    setWaves((prev) =>
      prev.map((w) => ({
        ...w,
        events: w.events.filter((e) => e.id !== eventId),
      }))
    );
    // åˆ æ‰ç›¸å…³è¿æ¥
    setConnections((prev) =>
      prev.filter((c) => c.from !== eventId && c.to !== eventId)
    );
  };

  // åˆ é™¤è¿æ¥
  const handleDeleteConnection = (connIndex) => {
    setConnections((prev) => prev.filter((_, i) => i !== connIndex));
  };

  // é¼ æ ‡ç§»åŠ¨æ—¶ï¼Œå¦‚æœæ­£åœ¨è¿çº¿ä¸”ç®­å¤´æœªè¢«é”å®š(énull)ï¼Œå°±æ›´æ–°è¿çº¿ç»ˆç‚¹
  const handleMouseMove = (e) => {
    if (pendingConnection && pendingConnectionEnd !== null) {
      const containerRect = containerRef.current.getBoundingClientRect();
      setPendingConnectionEnd({
        x: e.clientX - containerRect.left,
        y: e.clientY - containerRect.top,
      });
    }
  };

  // ç”¨Memoæ„é€  timelineOptionsï¼ˆç»™ AddEventModalï¼‰
  const timelineOptions = useMemo(() => {
    return waves.map((wave, i) => ({ index: i, title: wave.title }));
  }, [waves]);

  // åªæ˜¾ç¤ºè¢«é€‰ä¸­çš„wave
  const displayedWaves = useMemo(() => {
    return waves.filter((_, idx) => selectedWaveIndices.includes(idx));
  }, [waves, selectedWaveIndices]);

  // æ”¶é›†æ‰€æœ‰è¢«é€‰ä¸­çš„waveé‡Œçš„äº‹ä»¶ID
  const displayedEventIds = useMemo(() => {
    const ids = new Set();
    displayedWaves.forEach((w) => {
      w.events.forEach((ev) => {
        ids.add(ev.id);
      });
    });
    return ids;
  }, [displayedWaves]);

  // åªæ˜¾ç¤ºè¢«é€‰ä¸­çš„waveé‡Œå¯¹åº”çš„è¿æ¥ï¼ˆfromã€toéƒ½å¿…é¡»æ˜¯é€‰ä¸­çš„waveï¼‰
  const displayedConnections = useMemo(() => {
    return connections.filter(
      (conn) => 
        displayedEventIds.has(conn.from) && 
        displayedEventIds.has(conn.to)
    );
  }, [connections, displayedEventIds]);

  // è·å–ä»Šå¤©çš„æ—¥æœŸï¼ˆä»…ç”¨äºæ˜¾ç¤ºï¼‰
  const todayStr = new Date().toLocaleDateString();

  // åˆ‡æ¢é€‰ä¸­çš„Wave
  const toggleWave = (idx) => {
    setSelectedWaveIndices((prev) => {
      if (prev.includes(idx)) {
        return prev.filter((i) => i !== idx);
      } else {
        return [...prev, idx];
      }
    });
  };

  return (
    <div
      ref={containerRef}
      onMouseMove={handleMouseMove}
      style={{ paddingBottom: '80px', position: 'relative' }}
    >
      {/* Add Event Modal */}
      <AddEventModal
        isOpen={showGlobalAddModal}
        onClose={() => setShowGlobalAddModal(false)}
        onSubmit={handleSubmitNewEvent}
        timelineOptions={timelineOptions}
      />

      {/* Connection Reason Modal */}
      <ConnectionReasonModal
        isOpen={showConnectionModal}
        onClose={() => {
          setShowConnectionModal(false);
          setPendingConnection(null);
          setPendingConnectionEnd(null);
        }}
        onSubmit={handleSubmitConnection}
      />

      {/* å…¨å±€ Hover å¼¹æ¡† */}
      <GlobalTooltip hoveredEvent={hoveredEvent} />

      {/* é¡µé¢æ ‡é¢˜ */}
      <h1
        style={{
          textAlign: 'center',
          marginBottom: '20px',
          fontSize: '2rem',
          color: '#333',
          textShadow: '1px 1px 2px rgba(0,0,0,0.2)',
        }}
      >
        Analogical Timeline of Tech Wave
      </h1>
      <h2
        style={{
          textAlign: 'center',
          fontStyle: 'italic',
          color: '#555',
          marginBottom: '30px',
        }}
      >
        History doesn't repeat itself, but it often rhymes.
      </h2>

      {/* æ˜¾ç¤ºä»Šå¤©æ—¥æœŸ */}
      {/* <h3 style={{ textAlign: 'center', marginBottom: '40px' }}>
        Today {todayStr}
      </h3> */}

      <div style={{textAlign: "center"}}>

          Zijian "Jason" Ding
        {/* </a> */}
        <div style={{ paddingTop: "0.2em" }}>PhD candidate</div>
        <div style={{ paddingTop: "0.2em" }}>University of Maryland, College Park</div>
      </div>

      <h3 style={{ textAlign: 'center', marginBottom: '40px' }}>
       Zijian "Jason" Ding
      </h3>

      {/* å³ä¸Šè§’ æ·»åŠ æŒ‰é’® */}
      <button
        onClick={() => setShowGlobalAddModal(true)}
        style={{
          position: 'fixed',
          top: '20px',
          right: '20px',
          width: '40px',
          height: '40px',
          borderRadius: '50%',
          border: 'none',
          color: '#3498db',
          fontSize: '24px',
          fontWeight: 'bold',
          cursor: 'pointer',
          zIndex: 10,
          boxShadow: '0 2px 5px rgba(0,0,0,0.3)',
        }}
        title="Add Event"
      >
        +
      </button>

      {/* æ—¶é—´çº¿é€‰æ‹©å™¨ï¼ˆå¤šé€‰ï¼‰ */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        {waves.map((wave, idx) => {
          const waveIcon = getWaveIcon(wave.title);
          const isSelected = selectedWaveIndices.includes(idx);
          const predictedTag = wave.predictionTime
            ? ` (${wave.predictionTime})`
            : '';

          return (
            <label
              key={idx}
              style={{
                marginRight: '15px',
                cursor: 'pointer',
                display: 'inline-flex',
                alignItems: 'center',
                padding: '4px',
                borderRadius: '4px',
                backgroundColor: isSelected ? '#ebedef' : 'transparent',
                transition: 'background-color 0.2s ease',
              }}
              title={wave.predictionTime ? `Predicted timeline at ${wave.predictionTime}` : ''}
            >
              <input
                type="checkbox"
                checked={isSelected}
                onChange={() => toggleWave(idx)}
                style={{ marginRight: '6px' }}
              />
              <span style={{ marginRight: '4px' }}>{waveIcon}</span>
              <span>
                {wave.title}
                {predictedTag}
              </span>
            </label>
          );
        })}
      </div>

      {/* å¤šæ¡ Wave (Timeline) - åªæ˜¾ç¤ºé€‰ä¸­çš„ */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-around',
          flexWrap: 'wrap',
          marginTop: '40px',
          position: 'relative',
          zIndex: 0,
        }}
      >
        {displayedWaves.map((wave, idx) => {
          // æ‰¾åˆ° wave åœ¨åŸæ•°ç»„é‡Œçš„ä¸‹æ ‡
          const originalIndex = waves.indexOf(wave);

          return (
            <Timeline
              key={originalIndex}
              timelineIndex={originalIndex}
              wave={wave}
              onEventDoubleClick={handleEventDoubleClick}
              onDeleteEvent={handleDeleteEvent}
              setHoveredEvent={setHoveredEvent}
              getEventRef={getEventRef}
            />
          );
        })}
      </div>

      {/* è¿çº¿å±‚ï¼ˆåªæ˜¾ç¤ºé€‰ä¸­çš„æ³¢é‡Œäº‹ä»¶æ‰€å½¢æˆçš„è¿æ¥ï¼‰ */}
      <ConnectionsLayer
        connections={displayedConnections}
        eventPositions={eventPositions}
        containerRef={containerRef}
        pendingConnection={pendingConnection}
        pendingConnectionEnd={pendingConnectionEnd}
        onDeleteConnection={handleDeleteConnection}
      />
    </div>
  );
}

export default Showcase;

