              const oldYMetricValue = d[yAxisMetric];
              const deltaX = newXMetricValue - oldXMetricValue;
              const deltaY = newYMetricValue - oldYMetricValue;

              delete d._lastX;
              delete d._lastY;

              let modifications = [];
              let behindNodeCandidate = null;

              if (Math.abs(deltaX) > 5) {
                modifications.push({
                  metric: xAxisMetric,
                  direction: deltaX > 0 ? 'increase' : 'decrease',
                });
                const smallerCandidates = nodes
                  .filter((node) => node[xAxisMetric] !== undefined && node.id !== d.id && node[xAxisMetric] < newXMetricValue)
                  .sort((a, b) => b[xAxisMetric] - a[xAxisMetric]);
                if (smallerCandidates[0]) {
                  behindNodeCandidate = smallerCandidates[0];
                }
              }

              if (Math.abs(deltaY) > 5) {
                modifications.push({
                  metric: yAxisMetric,
                  direction: deltaY > 0 ? 'increase' : 'decrease',
                });
                const smallerY = nodes
                  .filter((node) => node[yAxisMetric] !== undefined && node.id !== d.id && node[yAxisMetric] < newYMetricValue)
                  .sort((a, b) => b[yAxisMetric] - a[yAxisMetric]);
                if (smallerY[0]) {
                  behindNodeCandidate = smallerY[0] || behindNodeCandidate;
                }
              }

              d3.select(this).attr(
                'transform',
                `translate(${xScale(d[xAxisMetric])}, ${yScale(d[yAxisMetric])})`
              );

              if (modifications.length > 0) {
                const ghostNodeId = generateUniqueId();
                const ghostNode = {
                  ...d,
                  id: ghostNodeId,
                  x: d.x,
                  y: d.y,
                  [xAxisMetric]: newXMetricValue,
                  [yAxisMetric]: newYMetricValue,
                  isGhost: true,
                  isNewlyGenerated: true,
                };

                setNodes((prev) => [...prev, ghostNode]);
                setLinks((prev) => [...prev, { source: d.id, target: ghostNodeId }]);

                setPendingChange({
                  originalNode: d,
                  ghostNode,
                  modifications,
                  behindNode: behindNodeCandidate,
                  screenX: event.sourceEvent.clientX + 10,
                  screenY: event.sourceEvent.clientY + 10,
                });
              }
            })
        );

      nodeGroups
        .append('circle')
        .attr('r', 8)
        .style('fill', (d) => (d.isNewlyGenerated ? '#FFD700' : colorMap[d.type]))
        .style('opacity', (d) => (d.isGhost ? 0.5 : 1))
        .style('stroke', (d) => {
          if (selectedNode?.id === d.id) return '#000';
          else if (hoveredNode?.id === d.id) return '#555';
          else return '#fff';
        })
        .style('stroke-width', (d) => {
          if (selectedNode?.id === d.id) return 4;
          else if (hoveredNode?.id === d.id) return 3;
          else return 2;
        });
    }
  }, [
    showTree,
    nodes,
    links,
    selectedNode,
    hoveredNode,
    xAxisMetric,
    yAxisMetric,
    isGenerating,
    isEvaluating,
  ]);

