  // ============== 段落9.1：新的合并卡片组件 ==============
// ============== 段落9.1：HypothesisFactorsAndScoresCard（完整函数） ==============
const HypothesisFactorsAndScoresCard = ({ node, isEvaluating }) => {
  // 是否有“前后对比”（只有被修改过的 node 才有 previousState）
  const hasBefore = !!node.previousState;
  // 当前要显示 After（最新）还是 Before（修改前）
  const [isAfterFactors, setIsAfterFactors] = useState(true);

  // 如果正在评估，则先不显示分数与原因
  if (isEvaluating) {
    return (
      <div
        style={{
          border: '1px solid #e5e7eb',
          borderRadius: '8px',
          backgroundColor: '#FAFAFA',
          padding: '16px',
          marginBottom: '16px',
        }}
      >
        <p style={{ fontSize: '0.875rem', color: '#6b7280' }}>
          Scores will be displayed once evaluation is complete.
        </p>
      </div>
    );
  }

  // 是否需要显示 toggle
  const showToggle = hasBefore;

  // 取出每个阶段（Before/After）的 reason 和 score
  // 如果没有 previousState，就只用 After 数据
  const noveltyBefore = hasBefore ? node.previousState.noveltyScore ?? 'N/A' : null;
  const noveltyAfter = node.noveltyScore ?? 'N/A';
  const feasibilityBefore = hasBefore ? node.previousState.feasibilityScore ?? 'N/A' : null;
  const feasibilityAfter = node.feasibilityScore ?? 'N/A';
  const impactBefore = hasBefore ? node.previousState.impactScore ?? 'N/A' : null;
  const impactAfter = node.impactScore ?? 'N/A';

  const beforeNoveltyReason = hasBefore ? node.previousState.noveltyReason ?? '(No reason provided)' : null;
  const beforeFeasibilityReason = hasBefore
    ? node.previousState.feasibilityReason ?? '(No reason provided)'
    : null;
  const beforeImpactReason = hasBefore ? node.previousState.impactReason ?? '(No reason provided)' : null;

  const afterNoveltyReason = node.noveltyReason ?? '(No reason provided)';
  const afterFeasibilityReason = node.feasibilityReason ?? '(No reason provided)';
  const afterImpactReason = node.impactReason ?? '(No reason provided)';

  // 根据是否在 After/Befor 模式下，获取对应文本
  const getNoveltyText = () => (isAfterFactors ? afterNoveltyReason : beforeNoveltyReason);
  const getFeasibilityText = () =>
    isAfterFactors ? afterFeasibilityReason : beforeFeasibilityReason;
  const getImpactText = () => (isAfterFactors ? afterImpactReason : beforeImpactReason);

  // 画箭头的小函数
  const getArrowDiv = (beforeVal, afterVal) => {
    const b = parseFloat(beforeVal);
    const a = parseFloat(afterVal);
    if (isNaN(b) || isNaN(a)) return null;

    if (a > b) {
      // green arrow
      return (
        <svg
          width="17"
          height="17"
          viewBox="0 0 17 17"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          style={{ margin: '0 6px' }}
        >
          <g>
            <path
              d="M7.01221 4.32837L13.0122 4.32837L13.0122 10.3284"
              stroke="#37A219"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
            <path
              d="M4.52682 12.8137L13.0121 4.32845"
              stroke="#37A219"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </g>
        </svg>
      );
    } else if (a < b) {
      // red arrow
      return (
        <svg
          width="17"
          height="17"
          viewBox="0 0 17 17"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          style={{ margin: '0 6px' }}
        >
          <g>
            <path
              d="M12.9168 7.19702V13.197H6.91684"
              stroke="#A21919"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
            <path
              d="M4.43146 4.71167L12.9167 13.1969"
              stroke="#A21919"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </g>
        </svg>
      );
    } else {
      // 相同 -> 横线
      return (
        <svg
          width="16"
          height="17"
          viewBox="0 0 16 17"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          style={{ margin: '0 6px' }}
        >
          <g id="Frame">
            <path
              id="Vector"
              d="M2.97964 8.17383L13.0204 8.17383"
              stroke="#A2A2A2"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </g>
        </svg>
      );
    }
  };

  // 用来渲染一个要素区块
  const renderFactorBlock = (factorName, color, beforeVal, afterVal, beforeText, afterText) => {
    // 这里 text 取决于是否显示 After / Before
    const activeText = isAfterFactors ? afterText : beforeText;
    // 分数显示
    const scoreDisplay = hasBefore ? (
      <div style={{ display: 'flex', alignItems: 'center', fontSize: '1rem', color: '#374151' }}>
        <span>{beforeVal}</span>
        {getArrowDiv(beforeVal, afterVal)}
        <span>{afterVal}</span>
      </div>
    ) : (
      <div style={{ fontSize: '1rem', color: '#374151' }}>Score: {afterVal}</div>
    );

    return (
      <div
        key={factorName}
        style={{
          backgroundColor: '#F9FAFB',
          border: '1px solid #E5E7EB',
          borderRadius: '8px',
          padding: '16px',
          marginBottom: '16px',
        }}
      >
        {/* 标题 */}
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
          <div
            style={{
              width: '12px',
              height: '12px',
              borderRadius: '50%',
              backgroundColor: color,
              marginRight: '8px',
            }}
          ></div>
          <div style={{ fontSize: '1rem', fontWeight: '600', color }}>{factorName}</div>
        </div>

        {/* reason 文本内容 */}
        <div
          style={{
            fontSize: '0.875rem',
            color: '#111827',
            lineHeight: '1.4',
            marginBottom: '12px',
            whiteSpace: 'pre-wrap',
          }}
        >
          {activeText || '(No reason provided)'}
        </div>

        {/* 分数对比 */}
        {scoreDisplay}
      </div>
    );
  };

  // 最终返回
  return (
    <div
      style={{
        position: 'relative',
        border: '1px solid #e5e7eb',
        borderRadius: '8px',
        backgroundColor: '#FAFAFA',
        padding: '16px',
        marginBottom: '16px',
      }}
    >
      {showToggle && (
        <div
          onClick={() => setIsAfterFactors((prev) => !prev)}
          style={{
            position: 'absolute',
            top: '12px',
            right: '12px',
            width: '82px',
            height: '20px',
            background: '#EEEEEE',
            borderRadius: 62,
            overflow: 'hidden',
            display: 'flex',
            cursor: 'pointer',
          }}
        >
          {/* After */}
          <div
            style={{
              width: 42,
              height: 17,
              background: isAfterFactors ? '#2E2E2E' : '#EEEEEE',
              borderRadius: 62,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              transition: 'all 0.2s ease',
            }}
          >
            <div
              style={{
                color: isAfterFactors ? '#EEEEEE' : '#2E2E2E',
                fontSize: 10,
                fontWeight: 500,
              }}
            >
              After
            </div>
          </div>

          {/* Before */}
          <div
            style={{
              width: 40,
              height: 20,
              background: isAfterFactors ? '#EEEEEE' : '#2E2E2E',
              borderRadius: 62,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              transition: 'all 0.2s ease',
            }}
          >
            <div
              style={{
                color: isAfterFactors ? '#2E2E2E' : '#EEEEEE',
                fontSize: 10,
                fontWeight: 500,
              }}
            >
              Before
            </div>
          </div>
        </div>
      )}

      {/* Novelty */}
      {renderFactorBlock(
        'Novelty',
        '#FB7185',
        noveltyBefore,
        noveltyAfter,
        beforeNoveltyReason,
        afterNoveltyReason
      )}

      {/* Feasibility */}
      {renderFactorBlock(
        'Feasibility',
        '#FBBF24',
        feasibilityBefore,
        feasibilityAfter,
        beforeFeasibilityReason,
        afterFeasibilityReason
      )}

      {/* Impact */}
      {renderFactorBlock(
        'Impact',
        '#3B82F6',
        impactBefore,
        impactAfter,
        beforeImpactReason,
        afterImpactReason
      )}
    </div>
  );
};

