    );
  };

  // ============== 段落11：整体布局，渲染主界面 JSX ==============
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', position: 'relative' }}>
      <TopNav />

      {showTree && !isAnalysisSubmitted && (
        <div style={{ backgroundColor: '#1f2937', display: 'flex', alignItems: 'center', padding: '10px 20px' }}>
          <form onSubmit={handleAnalysisIntentSubmit} style={{ display: 'flex', alignItems: 'center' }}>
            <input
              type="text"
              value={analysisIntent}
              onChange={(e) => setAnalysisIntent(e.target.value)}
              placeholder="Enter analysis intent"
              style={{ padding: '6px 10px', borderRadius: '4px', border: '1px solid #d1d5db', marginRight: '8px' }}
            />
            <button
              type="submit"
              disabled={isGenerating || isEvaluating}
              style={{
                padding: '6px 12px',
                backgroundColor: '#4C84FF',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: isGenerating || isEvaluating ? 'not-allowed' : 'pointer',
                opacity: isGenerating || isEvaluating ? 0.7 : 1,
              }}
            >
              {isEvaluating ? 'Evaluating...' : isGenerating ? 'Generating...' : 'Submit'}
            </button>
            {error && <div style={{ marginLeft: '16px', color: '#dc2626', fontSize: '0.875rem' }}>{error}</div>}
          </form>
        </div>
      )}

      <div style={{ display: 'flex', padding: '20px', maxWidth: '1600px', margin: '0 auto', boxSizing: 'border-box' }}>
        {/* 左侧图 */}
        <div style={{ flexBasis: '60%', marginRight: '20px' }}>
          <svg ref={svgRef} />
        </div>

        {/* 右侧 Dashboard */}
        <div style={{ flexBasis: '40%' }}>
          {(isGenerating || isEvaluating) && (
            <div style={{ textAlign: 'center', marginBottom: '10px', color: '#4C84FF' }}>
              {isGenerating ? 'Generating...' : 'Evaluating...'}
            </div>
          )}
          <Dashboard node={nodes.find((n) => n.id === (hoveredNode?.id || selectedNode?.id))} isEvaluating={isEvaluating} showTree={showTree} />
        </div>
      </div>

      {/* ========== 段落12：悬浮确认修改的按钮 (pendingChange) ========== */}
      {pendingChange && (
        <div
          style={{
            position: 'absolute',
            left: pendingChange.screenX,
            top: pendingChange.screenY,
            backgroundColor: '#f3f4f6',
            border: '1px solid #d1d5db',
            borderRadius: '4px',
            padding: '6px 8px',
            zIndex: 999,
          }}
        >
          <div style={{ marginBottom: '4px', fontSize: '0.875rem', color: '#374151' }}>
            <strong>Confirm change?</strong>
          </div>
          <button
            style={{
              padding: '4px 8px',
              backgroundColor: '#4C84FF',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              marginRight: '8px',
              cursor: 'pointer',
            }}
            onClick={() => {
              modifyHypothesisBasedOnModifications(
                pendingChange.originalNode,
                pendingChange.ghostNode,
                pendingChange.modifications,
                pendingChange.behindNode
              );
              setPendingChange(null);
            }}
          >
            Confirm
          </button>

          <button
            style={{
              padding: '4px 8px',
              backgroundColor: '#fff',
              color: '#374151',
              border: '1px solid #d1d5db',
              borderRadius: '4px',
              cursor: 'pointer',
            }}
            onClick={() => {
              setNodes((prev) => prev.filter((nd) => nd.id !== pendingChange.ghostNode.id));
              setLinks((prev) => prev.filter((lk) => lk.target !== pendingChange.ghostNode.id));
              setPendingChange(null);
            }}
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

// ============== 段落13：默认导出 ==============
export default TreePlotVisualization;
