import React, { useState } from 'react';

function EventItem({ event }) {
  const [hover, setHover] = useState(false);

  return (
    <div
      style={{
        position: 'relative',
        marginBottom: '20px',
        cursor: 'pointer',
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      <div
        style={{
          width: '12px',
          height: '12px',
          borderRadius: '50%',
          backgroundColor: '#007BFF',
          margin: '0 auto',
        }}
      />
      <div
        style={{
          textAlign: 'center',
          fontSize: '12px',
          marginTop: '5px',
        }}
      >
        {event.date}
      </div>

      {hover && (
        <div
          style={{
            position: 'absolute',
            top: '-90px',
            left: '50%',
            transform: 'translateX(-50%)',
            padding: '8px 12px',
            backgroundColor: '#fff',
            border: '1px solid #ccc',
            borderRadius: '4px',
            whiteSpace: 'nowrap',
            zIndex: 10,
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
            minWidth: '180px',
          }}
        >
          <strong style={{ display: 'block', marginBottom: '4px' }}>
            {event.title}
          </strong>
          <div style={{ fontSize: '12px', color: '#333' }}>{event.detail}</div>
        </div>
      )}
    </div>
  );
}

function Timeline({ wave }) {
  // 时间线总高度（像素）
  const timelineHeight = 450;
  const totalEvents = wave.events.length;

  return (
    <div
      style={{
        margin: '10px',
        flex: '1',
        textAlign: 'center',
        backgroundColor: '#f7f7f7',
        borderRadius: '6px',
        padding: '10px 10px 20px',
      }}
    >
      <h2 style={{ fontSize: '16px', marginBottom: '20px' }}>{wave.wave}</h2>
      <div
        style={{
          position: 'relative',
          margin: '0 auto',
          width: '2px',
          backgroundColor: '#ddd',
          height: timelineHeight + 'px',
        }}
      >
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {wave.events.map((event, index) => {
            // 根据事件索引计算在时间线上的位置
            const pos =
              totalEvents === 1
                ? timelineHeight / 2
                : (index / (totalEvents - 1)) * timelineHeight;
            return (
              <div
                key={index}
                style={{
                  position: 'absolute',
                  top: pos + 'px',
                  width: '100%',
                }}
              >
                <EventItem event={event} />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

function Showcase() {
  const waves = [
    {
      wave: '个人电脑浪潮 (1979-1982)',
      events: [
        {
          date: '1979-02-12',
          title: '家用电脑萌芽',
          detail: 'Apple II 等机型在极小圈子里出现，市场初见端倪。',
        },
        {
          date: '1979-08-20',
          title: 'IBM 着手布局',
          detail: 'IBM 组建专门团队，准备进入个人电脑领域。',
        },
        {
          date: '1980-03-01',
          title: 'PC 原型测试',
          detail: '早期原型机在内部测试中性能初步达标。',
        },
        {
          date: '1981-08-12',
          title: 'IBM PC 推出',
          detail: '正式面市，引发产业链关注，MS-DOS 逐渐走向主流。',
        },
        {
          date: '1982-11-05',
          title: '克隆机涌现',
          detail: '兼容 IBM PC 的各类克隆机出现，生态进一步壮大。',
        },
      ],
    },
    {
      wave: '互联网浪潮 (1994-1997)',
      events: [
        {
          date: '1994-04-03',
          title: '网景浏览器发布',
          detail: 'Netscape Navigator 提供图形化浏览方式，降低上网门槛。',
        },
        {
          date: '1995-09-14',
          title: '雅虎成立',
          detail: '早期门户网站兴起，互联网公司开始涌现。',
        },
        {
          date: '1996-02-20',
          title: '搜索与邮件服务',
          detail: 'Hotmail、Excite 等服务上线，用户逐渐积累。',
        },
        {
          date: '1996-06-30',
          title: '宽带初步普及',
          detail: '美国及部分发达国家开始推广 DSL 等宽带接入。',
        },
        {
          date: '1997-11-21',
          title: '门户网站爆发',
          detail: '新浪、搜狐、网易等在华语圈快速发展。',
        },
      ],
    },
    {
      wave: '智能手机浪潮 (2007-2010)',
      events: [
        {
          date: '2007-01-09',
          title: 'iPhone 发布',
          detail: '苹果首款 iPhone，重新定义了手机的功能和交互体验。',
        },
        {
          date: '2008-09-23',
          title: 'Android 亮相',
          detail: 'Google 推出 Android 系统，开放生态开始形成。',
        },
        {
          date: '2009-05-15',
          title: 'App 生态发芽',
          detail: 'App Store 与 Android Market 上应用数量激增。',
        },
        {
          date: '2010-03-02',
          title: '移动支付萌芽',
          detail: '部分国家开始试点 NFC、二维码支付等功能。',
        },
        {
          date: '2010-11-18',
          title: '移动互联网爆发',
          detail: '智能手机用户量快速攀升，社交与电商模式变革。',
        },
      ],
    },
    {
      wave: '电动车浪潮 (2015-2018)',
      events: [
        {
          date: '2015-03-01',
          title: '特斯拉 Model S',
          detail: '特斯拉旗舰车型逐渐普及，引发行业和市场强烈关注。',
        },
        {
          date: '2015-10-10',
          title: '传统车企入场',
          detail: '大众、通用等传统厂商开始电动车项目，行业竞争升温。',
        },
        {
          date: '2016-05-20',
          title: '电池技术突破',
          detail: '动力电池能量密度提高，成本显著下降。',
        },
        {
          date: '2017-07-10',
          title: '政策补贴与鼓励',
          detail: '各国政府出台多项激励政策，扶持电动车产业。',
        },
        {
          date: '2018-09-15',
          title: '基础设施完善',
          detail: '快速充电网络加速建设，用户里程焦虑逐渐缓解。',
        },
      ],
    },
    {
      wave: '生成式 AI 浪潮 (2019-2022)',
      events: [
        {
          date: '2019-06-20',
          title: 'Transformer 广泛应用',
          detail: 'BERT、GPT 等模型在多个NLP任务中崭露头角。',
        },
        {
          date: '2020-07-15',
          title: 'GPT-3 发布',
          detail: 'OpenAI 推出 GPT-3，引发对大规模预训练模型的关注。',
        },
        {
          date: '2021-03-01',
          title: '多模态进展',
          detail: '视觉、语音、文本融合的多模态研究愈发活跃。',
        },
        {
          date: '2021-08-01',
          title: '产业落地加速',
          detail: '各大公司竞相推出基于大模型的生产力工具与平台。',
        },
        {
          date: '2022-11-30',
          title: 'ChatGPT 爆红',
          detail: 'ChatGPT 带来全民热议，让生成式 AI 真正进入公众视野。',
        },
      ],
    },
  ];

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ textAlign: 'center' }}>科技浪潮早期阶段互动时间线</h1>
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-around',
          flexWrap: 'wrap',
          marginTop: '40px',
        }}
      >
        {waves.map((wave, index) => (
          <Timeline key={index} wave={wave} />
        ))}
      </div>
    </div>
  );
}

export default Showcase;
