import React, { useState, useEffect, useRef } from 'react';

/** ç”Ÿæˆéšæœº ID çš„å°å·¥å…·å‡½æ•°ï¼Œç”¨äºç»™æ–°æ·»åŠ çš„äº‹ä»¶åˆ†é…ç¨³å®šçš„ event.id */
function generateUniqueId() {
  return Math.random().toString(36).substr(2, 9);
}

/** 
 * Helper function to generate a curved path (cubic Bezier curve example)
 */
function generateCurvePath(x1, y1, x2, y2) {
  const cx = (x1 + x2) / 2;
  return `M ${x1},${y1} C ${cx},${y1} ${cx},${y2} ${x2},${y2}`;
}

/**
 * Modal Component for adding new events
 */
function AddEventModal({ isOpen, onClose, onSubmit }) {
  const [date, setDate] = useState('');
  const [title, setTitle] = useState('');
  const [detail, setDetail] = useState('');

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (date && title) {
      onSubmit({ date, title, detail });
      onClose();
    }
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
    }}>
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '8px',
        width: '400px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
      }}>
        <h3 style={{ marginTop: 0 }}>Add New Event</h3>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Date (YYYY-MM-DD):</label>
            <input 
              type="date" 
              value={date} 
              onChange={(e) => setDate(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd'
              }}
              required
            />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Title:</label>
            <input 
              type="text" 
              value={title} 
              onChange={(e) => setTitle(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd'
              }}
              required
            />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Details:</label>
            <textarea 
              value={detail} 
              onChange={(e) => setDetail(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd',
                minHeight: '100px'
              }}
            />
          </div>
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
            <button 
              type="button" 
              onClick={onClose}
              style={{
                padding: '8px 16px',
                border: '1px solid #ddd',
                borderRadius: '4px',
                backgroundColor: '#f1f1f1',
                cursor: 'pointer'
              }}
            >
              Cancel
            </button>
            <button 
              type="submit"
              style={{
                padding: '8px 16px',
                border: 'none',
                borderRadius: '4px',
                backgroundColor: '#007BFF',
                color: 'white',
                cursor: 'pointer'
              }}
            >
              Add Event
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

/**
 * ConnectionLine ç»„ä»¶ï¼šç»˜åˆ¶æ­£å¼çš„çº¢è‰²è¿æ¥çº¿ï¼Œ
 * hover æ—¶åŠ ç²—å¹¶å¢åŠ  glow æ•ˆæœï¼Œç‚¹å‡»æ—¶å¼¹å‡ºç¡®è®¤ååˆ é™¤è¯¥è¿æ¥ã€‚
 */
function ConnectionLine({ fromPos, toPos, reason, onDelete }) {
  const [hovered, setHovered] = useState(false);
  const strokeColor = hovered ? 'rgba(220,20,60,0.9)' : 'rgba(255,0,0,0.4)';
  const strokeWidth = hovered ? 3 : 2;
  const pathData = generateCurvePath(fromPos.x, fromPos.y, toPos.x, toPos.y);

  return (
    <g
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      onClick={(e) => {
        e.stopPropagation();
        if (window.confirm('Delete this connection?')) {
          onDelete();
        }
      }}
      style={{ cursor: 'pointer' }}
    >
      <path 
        d={pathData} 
        stroke={strokeColor} 
        strokeWidth={strokeWidth} 
        fill="none" 
        style={{ 
          pointerEvents: 'all',
          filter: hovered ? 'drop-shadow(0 0 3px rgba(255,0,0,0.3))' : 'none',
        }} 
      />
      <text
        x={(fromPos.x + toPos.x) / 2}
        y={(fromPos.y + toPos.y) / 2 - 5}
        fill={strokeColor}
        fontSize={hovered ? 14 : 12}
        fontWeight={hovered ? 'bold' : 'normal'}
        textAnchor="middle"
        style={{ 
          pointerEvents: 'none',
          filter: hovered ? 'drop-shadow(0 1px 2px rgba(255,255,255,0.8))' : 'none',
        }}
      >
        {reason}
      </text>
    </g>
  );
}

/** 
 * ç”Ÿæˆä¸€ä¸ªä¸Šä¸‹äº¤é”™çš„åç§»æ•°ç»„ã€‚ä¾‹å¦‚ï¼š
 * count=1 => [0]
 * count=2 => [-40, 40]
 * count=3 => [-80, 0, 80]
 * ...
 * gap ç”¨äºæ§åˆ¶å¡ç‰‡é—´çš„å‚ç›´é—´è·
 */
function getOffsets(count, gap = 80) {
  const offsets = [];
  const half = Math.floor(count / 2);
  for (let i = 0; i < count; i++) {
    offsets.push((i - half) * gap);
  }
  // å¶æ•°ä¸ªè®©å®ƒä»¬å¯¹ç§°
  if (count % 2 === 0) {
    offsets.forEach((val, idx) => {
      offsets[idx] = val + gap / 2;
    });
  }
  return offsets;
}

/**
 * å…¨å±€æ‚¬æµ®å¼¹æ¡†ç»„ä»¶ï¼šæ¥æ”¶å½“å‰ hoveredEventï¼ˆåŒ…å« rectã€titleã€detail ç­‰ä¿¡æ¯ï¼‰ï¼Œåœ¨é¡µé¢æœ€é¡¶å±‚æ˜¾ç¤º
 */
function GlobalTooltip({ hoveredEvent }) {
  if (!hoveredEvent) return null;
  const { rect, title, detail } = hoveredEvent;
  if (!rect) return null;

  // è®©å¼¹æ¡†ä¸­å¿ƒå¯¹å‡†å¡ç‰‡çš„ä¸­ç‚¹ï¼Œå¹¶ç¨å¾®å¾€ä¸‹ç§»åŠ¨
  const left = rect.left + (rect.width / 2) - 100; // è®©å®½åº¦ 200 px å±…ä¸­
  const top = rect.bottom + 8; // å¾€ä¸‹ 8px

  return (
    <div 
      style={{
        position: 'fixed',
        top,
        left,
        width: '200px',
        backgroundColor: '#fff',
        border: '1px solid #ddd',
        borderRadius: '6px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.15)',
        padding: '8px',
        fontSize: '12px',
        zIndex: 9999, // ä¿è¯åœ¨æœ€é¡¶å±‚
        pointerEvents: 'none', // è®©é¼ æ ‡å¯ç©¿é€
      }}
    >
      <div style={{ fontWeight: 'bold', marginBottom: '4px' }}>{title}</div>
      <div>{detail}</div>
    </div>
  );
}

/**
 * EventGroup ç»„ä»¶ï¼š
 * - åŒä¸€ç»„ï¼ˆ<= 3 ä¸ªæœˆé—´éš”ï¼‰çš„äº‹ä»¶åœ¨åŒä¸€ä¸ªçºµå‘ä½ç½® groupTopï¼ˆç”± Timeline è®¡ç®—å¥½çš„ï¼‰
 * - æ¯ä¸ªäº‹ä»¶å¡ç‰‡ä¸Šä¸‹é”™è½æ’å¸ƒï¼ˆé¿å…æ ‡é¢˜äº’ç›¸é®æŒ¡ï¼‰
 * - Hover æ—¶åœ¨å…¨å±€æ˜¾ç¤ºå¼¹å‡ºæ¡†
 */
function EventGroup({ events, registerPosition, onEventClick, onDelete, scrollPos, groupTop, setHoveredEvent }) {
  const sortedEvents = [...events].sort((a, b) => new Date(a.date) - new Date(b.date));
  const cardRefs = useRef([]);

  // è¿™é‡Œå¯è°ƒ gap æ¥å¢åŠ /å‡å°‘ç»„å†…äº‹ä»¶é—´çš„å‚ç›´è·ç¦»
  const offsets = getOffsets(sortedEvents.length, 80);

  // å°†å¡ç‰‡ DOM ä¸­å¿ƒç‚¹æ³¨å†Œç»™å…¨å±€ï¼Œç”¨äºç”»è¿æ¥çº¿
  useEffect(() => {
    sortedEvents.forEach((ev, i) => {
      if (cardRefs.current[i]) {
        const rect = cardRefs.current[i].getBoundingClientRect();
        const centerPos = {
          x: rect.left + rect.width / 2,
          y: rect.top + rect.height / 2,
        };
        registerPosition(ev.id, centerPos);
      }
    });
  }, [sortedEvents, registerPosition, scrollPos]);

  // Hover æ—¶è°ƒç”¨ setHoveredEvent
  const handleMouseEnter = (ev, i) => {
    if (!ev.detail) return; // å¦‚æœæ²¡æœ‰è¯¦æƒ…ï¼Œä¸å¼¹æ¡†
    const rect = cardRefs.current[i].getBoundingClientRect();
    setHoveredEvent({
      rect,
      title: ev.title,
      detail: ev.detail
    });
  };
  const handleMouseLeave = () => {
    setHoveredEvent(null);
  };

  return (
    <div
      style={{
        position: 'absolute',
        top: groupTop,
        left: 0,
        width: '280px',
        zIndex: 500,
      }}
    >
      {sortedEvents.map((ev, i) => {
        const offsetTop = offsets[i];
        return (
          <div
            key={ev.id}
            ref={(el) => (cardRefs.current[i] = el)}
            style={{
              position: 'absolute',
              left: '10px', // è°ƒæ•´å¡ç‰‡ä¸æ—¶é—´çº¿çš„æ¨ªå‘è·ç¦»
              top: offsetTop + 'px',
              width: '180px',
              height: '60px',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '8px',
              backgroundColor: '#fff',
              boxShadow: '0 2px 4px rgba(0,0,0,0.08)',
              cursor: 'pointer',
              transition: 'all 0.2s ease',
            }}
            onClick={() => onEventClick(ev.id)}
            onMouseEnter={() => handleMouseEnter(ev, i)}
            onMouseLeave={handleMouseLeave}
          >
            {/* æ ‡é¢˜ */}
            <div style={{ 
              fontWeight: 'bold', 
              marginBottom: '4px',
              fontSize: '14px',
              color: '#007BFF',
            }}>
              {ev.title}
            </div>
            {/* æ—¥æœŸ */}
            <div style={{ 
              fontSize: '13px', 
              marginBottom: '6px', 
              color: '#555' 
            }}>
              {ev.date}
            </div>

            {/* åˆ é™¤æŒ‰é’®ï¼ˆé¢œè‰²æ”¹ä¸ºè“è‰²ï¼‰ */}
            <button
              onClick={(e) => {
                e.stopPropagation();
                onDelete(ev.id);
              }}
              style={{
                position: 'absolute',
                top: '6px',
                right: '6px',
                background: 'transparent',
                border: 'none',
                color: '#007BFF',
                fontSize: '14px',
                cursor: 'pointer',
                transition: 'color 0.2s ease',
              }}
            >
              Ã—
            </button>
          </div>
        );
      })}
    </div>
  );
}

/**
 * Timeline ç»„ä»¶ï¼šä¸€æ¡æ—¶é—´è½´ + æ³¢æ¬¡æ ‡é¢˜ + äº‹ä»¶åˆ†ç»„
 * åœ¨è¿™é‡Œæˆ‘ä»¬å¯¹æ¯ä¸ªåˆ†ç»„åšã€Œé˜²é‡å å¤„ç†ã€ï¼šè‹¥ç›¸é‚»åˆ†ç»„ä½ç½®è¿‡è¿‘ï¼Œå°±å¾€ä¸‹æ¨ã€‚
 */
function Timeline({
  wave,
  waveIndex,
  registerPosition,
  onEventClick,
  onAddEvent,
  onDeleteEvent,
  scrollPos,
  setHoveredEvent,
}) {
  const timelineHeight = 1600;

  // æ ¹æ® wave.events é‡Œæœ€æ—©çš„æ—¥æœŸï¼Œç¡®å®šæœ¬æ—¶é—´è½´çš„èµ·ç‚¹
  const timestamps = wave.events.map((e) => new Date(e.date).getTime());
  const firstEventTime = timestamps.length > 0 ? Math.min(...timestamps) : Date.now();
  const firstEventDate = new Date(firstEventTime);
  const timelineStart = new Date(firstEventDate.getFullYear(), 0, 1).getTime();

  // è¿™é‡Œå¯è‡ªå®šä¹‰æ—¶é—´è·¨åº¦ï¼Œæ¯”å¦‚ 8 å¹´
  const eightYearsInMs = 8 * 365.25 * 24 * 60 * 60 * 1000;
  const timelineEndTime = timelineStart + eightYearsInMs;
  const safeTimeSpan = timelineEndTime - timelineStart;

  // æŒ‰æ—¥æœŸæ’åº
  const sortedEvents = [...wave.events].sort((a, b) => new Date(a.date) - new Date(b.date));

  // å°†äº‹ä»¶æŒ‰ç…§â€œç›¸è· <= 3 ä¸ªæœˆâ€åˆ†ç»„
  const THREE_MONTHS_MS = 3 * 30 * 24 * 60 * 60 * 1000;
  const eventGroups = [];
  let currentGroup = [];

  sortedEvents.forEach((ev, index) => {
    if (index === 0) {
      currentGroup.push(ev);
    } else {
      const prevTime = new Date(sortedEvents[index - 1].date).getTime();
      const currTime = new Date(ev.date).getTime();
      if (currTime - prevTime <= THREE_MONTHS_MS) {
        currentGroup.push(ev);
      } else {
        eventGroups.push([...currentGroup]);
        currentGroup = [ev];
      }
    }
  });
  if (currentGroup.length > 0) {
    eventGroups.push(currentGroup);
  }

  // é˜²æ­¢åˆ†ç»„ä¹‹é—´äº’ç›¸é‡å 
  let lastUsedPos = 0;
  const groupPositions = [];
  const groupSpacing = 20;

  function estimateGroupHeight(group) {
    const n = group.length;
    return 80 * (n - 1) + 60;
  }

  eventGroups.forEach((group) => {
    const firstEv = group[0];
    const currentTime = new Date(firstEv.date).getTime();
    const ratio = (currentTime - timelineStart) / safeTimeSpan;
    const naivePos = ratio * timelineHeight;
    const groupHeight = estimateGroupHeight(group);
    const finalPos = Math.max(naivePos, lastUsedPos + groupSpacing);
    groupPositions.push(finalPos);
    lastUsedPos = finalPos + groupHeight;
  });

  // é»˜è®¤å›¾æ ‡åˆ—è¡¨ï¼Œå½“ wave.icon æœªè®¾ç½®æ—¶ä½¿ç”¨
  const defaultIcons = ['ğŸ’»', 'ğŸ“±', 'ğŸŒ', 'ğŸ®', 'ğŸ“¡'];
  const waveIcon = wave.icon || defaultIcons[waveIndex % defaultIcons.length];

  return (
    <div style={{ 
      flex: '1', 
      margin: '0 20px', 
      position: 'relative',
      padding: '15px',
      backgroundColor: 'rgba(255,255,255,0.7)',
      borderRadius: '8px',
      boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
    }}>
      {/* æ—¶é—´çº¿é¡¶éƒ¨æ ‡é¢˜åŒºåŸŸï¼šå·¦ä¾§æ˜¾ç¤ºæ ‡é¢˜ï¼Œå³ä¾§æ˜¾ç¤ºæ·»åŠ äº‹ä»¶æŒ‰é’®ï¼ˆè“è‰²"+"ï¼Œæ— èƒŒæ™¯ï¼‰å’Œæµªæ½®å›¾æ ‡ï¼ˆåœ†å½¢èƒŒæ™¯ï¼Œæ”¾å³ä¾§ï¼‰ */}
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '2px',
        borderBottom: '1px solid #eee',
        paddingBottom: '2px',
      }}>
        <h2 style={{ 
          fontSize: '18px', 
          margin: 0,
          color: '#2c3e50',
          fontWeight: '600',
        }}>
          {wave.title}
        </h2>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <button
            onClick={onAddEvent}
            style={{
              background: 'none',
              border: 'none',
              color: '#007BFF',
              fontSize: '24px',
              cursor: 'pointer',
              marginRight: '10px'
            }}
            title="Add Event"
          >
            +
          </button>
          <div style={{ 
            width: '30px',
            height: '30px',
            borderRadius: '50%',
            backgroundColor: '#eee',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '18px'
          }}>
            {waveIcon}
          </div>
        </div>
      </div>
      <div style={{ 
        fontSize: '14px', 
        color: '#666', 
        marginBottom: '70px', 
        textAlign: 'center',
        fontStyle: 'italic',
      }}>
        {wave.period}
      </div>

      {/* çºµå‘çš„ä¸»è½´ */}
      <div style={{ 
        position: 'relative', 
        width: '2px', 
        backgroundColor: '#bbb', 
        height: timelineHeight + 'px', 
        marginLeft: '0', 
        marginTop: '-70px',
        borderRadius: '1px' 
      }}>
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {eventGroups.map((group, idx) => {
            const finalPos = groupPositions[idx];
            return (
              <EventGroup
                key={idx}
                events={group}
                registerPosition={registerPosition}
                onEventClick={onEventClick}
                onDelete={(id) => onDeleteEvent(id)}
                scrollPos={scrollPos}
                groupTop={finalPos}
                setHoveredEvent={setHoveredEvent}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

/**
 * ConnectionsLayer ç»„ä»¶ï¼šæ¸²æŸ“æ‰€æœ‰æ­£å¼è¿æ¥ï¼ˆçº¢è‰²æ›²çº¿ï¼‰ä»¥åŠæ­£åœ¨å»ºç«‹çš„ pending connectionï¼ˆçº¢è‰²ç®­å¤´è·Ÿéšé¼ æ ‡ï¼‰ã€‚
 */
function ConnectionsLayer({
  connections,
  eventPositions,
  containerRef,
  pendingConnection,
  pendingConnectionEnd,
  onDeleteConnection,
}) {
  const containerRect = containerRef.current
    ? containerRef.current.getBoundingClientRect()
    : { left: 0, top: 0 };

  return (
    <svg
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: 1,
        pointerEvents: 'none',
      }}
    >
      <defs>
        <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="10" refY="3.5" orient="auto">
          <polygon points="0 0, 10 3.5, 0 7" fill="red" />
        </marker>
      </defs>
      {connections.map((conn, index) => {
        const fromPos = eventPositions[conn.from];
        const toPos = eventPositions[conn.to];
        if (!fromPos || !toPos) return null;

        const adjustedFrom = { x: fromPos.x - containerRect.left, y: fromPos.y - containerRect.top };
        const adjustedTo = { x: toPos.x - containerRect.left, y: toPos.y - containerRect.top };

        return (
          <ConnectionLine
            key={index}
            fromPos={adjustedFrom}
            toPos={adjustedTo}
            reason={conn.reason}
            onDelete={() => onDeleteConnection(index)}
          />
        );
      })}

      {/* å¦‚æœæ­£åœ¨å»ºç«‹è¿æ¥ï¼Œåˆ™ç”»ä¸€æ¡â€œä¸´æ—¶çº¿â€è·Ÿéšé¼ æ ‡ */}
      {pendingConnection &&
        pendingConnectionEnd &&
        eventPositions[pendingConnection.startEventId] &&
        (() => {
          const startPosGlobal = eventPositions[pendingConnection.startEventId];
          const startPos = {
            x: startPosGlobal.x - containerRect.left,
            y: startPosGlobal.y - containerRect.top,
          };
          return (
            <line
              x1={startPos.x}
              y1={startPos.y}
              x2={pendingConnectionEnd.x}
              y2={pendingConnectionEnd.y}
              stroke="red"
              strokeWidth="3"
              markerEnd="url(#arrowhead)"
              style={{ pointerEvents: 'none' }}
            />
          );
        })()}
    </svg>
  );
}

