import React, { useState, useEffect, useRef } from 'react';

/** 生成随机 ID 的小工具函数，用于给新添加的事件分配稳定的 event.id */
function generateUniqueId() {
  return Math.random().toString(36).substr(2, 9);
}

/** 
 * Helper function to generate a curved path (cubic Bezier curve example)
 */
function generateCurvePath(x1, y1, x2, y2) {
  const cx = (x1 + x2) / 2;
  return `M ${x1},${y1} C ${cx},${y1} ${cx},${y2} ${x2},${y2}`;
}

/**
 * Modal Component for adding new events
 * - 通过下拉菜单选择要添加到哪条时间线
 */
function AddEventModal({ isOpen, onClose, onSubmit, timelineOptions = [] }) {
  const [date, setDate] = useState('');
  const [title, setTitle] = useState('');
  const [detail, setDetail] = useState('');
  const [selectedTimeline, setSelectedTimeline] = useState(0);

  // 当 timelineOptions 变化时，默认选中第一个
  useEffect(() => {
    if (timelineOptions.length > 0) {
      setSelectedTimeline(timelineOptions[0].index);
    }
  }, [timelineOptions]);

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (date && title) {
      onSubmit({
        timelineIndex: selectedTimeline,
        date,
        title,
        detail,
      });
      onClose();
      // 重置表单
      setDate('');
      setTitle('');
      setDetail('');
    }
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
    }}>
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '8px',
        width: '400px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
      }}>
        <h3 style={{ marginTop: 0 }}>Add New Event</h3>
        <form onSubmit={handleSubmit}>
          {/* 下拉菜单，选择目标时间线 */}
          {timelineOptions.length > 0 && (
            <div style={{ marginBottom: '15px' }}>
              <label style={{ display: 'block', marginBottom: '5px' }}>Select Timeline:</label>
              <select 
                value={selectedTimeline}
                onChange={(e) => setSelectedTimeline(parseInt(e.target.value))}
                style={{ 
                  width: '100%', 
                  padding: '8px', 
                  borderRadius: '4px',
                  border: '1px solid #ddd'
                }}
              >
                {timelineOptions.map((option, idx) => (
                  <option key={idx} value={option.index}>{option.title}</option>
                ))}
              </select>
            </div>
          )}
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Date (YYYY-MM-DD):</label>
            <input 
              type="date" 
              value={date} 
              onChange={(e) => setDate(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd'
              }}
              required
            />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Title:</label>
            <input 
              type="text" 
              value={title} 
              onChange={(e) => setTitle(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd'
              }}
              required
            />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Details:</label>
            <textarea 
              value={detail} 
              onChange={(e) => setDetail(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd',
                minHeight: '100px'
              }}
            />
          </div>
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
            <button 
              type="button" 
              onClick={onClose}
              style={{
                padding: '8px 16px',
                border: '1px solid #ddd',
                borderRadius: '4px',
                backgroundColor: '#f1f1f1',
                cursor: 'pointer'
              }}
            >
              Cancel
            </button>
            <button 
              type="submit"
              style={{
                padding: '8px 16px',
                border: 'none',
                borderRadius: '4px',
                backgroundColor: '#007BFF',
                color: 'white',
                cursor: 'pointer'
              }}
            >
              Add Event
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

/**
 * ConnectionLine 组件：绘制正式的红色连接线，
 * hover 时加粗并增加 glow 效果，点击时弹出确认后删除该连接。
 */
function ConnectionLine({ fromPos, toPos, reason, onDelete }) {
  const [hovered, setHovered] = useState(false);
  const strokeColor = hovered ? 'rgba(220,20,60,0.9)' : 'rgba(255,0,0,0.4)';
  const strokeWidth = hovered ? 3 : 2;
  const pathData = generateCurvePath(fromPos.x, fromPos.y, toPos.x, toPos.y);

  return (
    <g
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      onClick={(e) => {
        e.stopPropagation();
        if (window.confirm('Delete this connection?')) {
          onDelete();
        }
      }}
      style={{ cursor: 'pointer' }}
    >
      <path 
        d={pathData} 
        stroke={strokeColor} 
        strokeWidth={strokeWidth} 
        fill="none" 
        style={{ 
          pointerEvents: 'all',
          filter: hovered ? 'drop-shadow(0 0 3px rgba(255,0,0,0.3))' : 'none',
        }} 
      />
      <text
        x={(fromPos.x + toPos.x) / 2}
        y={(fromPos.y + toPos.y) / 2 - 5}
        fill={strokeColor}
        fontSize={hovered ? 14 : 12}
        fontWeight={hovered ? 'bold' : 'normal'}
        textAnchor="middle"
        style={{ 
          pointerEvents: 'none',
          filter: hovered ? 'drop-shadow(0 1px 2px rgba(255,255,255,0.8))' : 'none',
        }}
      >
        {reason}
      </text>
    </g>
  );
}

/** 
 * 生成一个上下交错的偏移数组。
 * count=1 => [0]
 * count=2 => [-40, 40]
 * count=3 => [-80, 0, 80]
 * gap 用于控制卡片间的垂直间距
 */
function getOffsets(count, gap = 80) {
  const offsets = [];
  const half = Math.floor(count / 2);
  for (let i = 0; i < count; i++) {
    offsets.push((i - half) * gap);
  }
  // 偶数个让它们对称
  if (count % 2 === 0) {
    offsets.forEach((val, idx) => {
      offsets[idx] = val + gap / 2;
    });
  }
  return offsets;
}

/**
 * 全局悬浮弹框组件：接收当前 hoveredEvent（包含 rect、title、detail 等信息），在页面最顶层显示
 */
function GlobalTooltip({ hoveredEvent }) {
  if (!hoveredEvent) return null;
  const { rect, title, detail } = hoveredEvent;
  if (!rect) return null;

  // 让弹框中心对准卡片的中点，并稍微往下移动
  const left = rect.left + (rect.width / 2) - 100; // 让宽度 200 px 居中
  const top = rect.bottom + 8; // 往下 8px

  return (
    <div 
      style={{
        position: 'fixed',
        top,
        left,
        width: '200px',
        backgroundColor: '#fff',
        border: '1px solid #ddd',
        borderRadius: '6px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.15)',
        padding: '8px',
        fontSize: '12px',
        zIndex: 9999,
        pointerEvents: 'none',
      }}
    >
      <div style={{ fontWeight: 'bold', marginBottom: '4px' }}>{title}</div>
      <div>{detail}</div>
    </div>
  );
}

/**
 * EventGroup 组件：
 * - 同一组（<= 3 个月间隔）的事件在同一个纵向位置 groupTop
 * - 每个事件卡片上下错落排布
 * - Hover 时在全局显示弹出框
 */
function EventGroup({ events, registerPosition, onEventClick, onDelete, scrollPos, groupTop, setHoveredEvent }) {
  const sortedEvents = [...events].sort((a, b) => new Date(a.date) - new Date(b.date));
  const cardRefs = useRef([]);

  // 这里可调 gap 来增加/减少组内事件间的垂直距离
  const offsets = getOffsets(sortedEvents.length, 80);

  // 将卡片 DOM 中心点注册给全局，用于画连接线
  useEffect(() => {
    sortedEvents.forEach((ev, i) => {
      if (cardRefs.current[i]) {
        const rect = cardRefs.current[i].getBoundingClientRect();
        const centerPos = {
          x: rect.left + rect.width / 2,
          y: rect.top + rect.height / 2,
        };
        registerPosition(ev.id, centerPos);
      }
    });
  }, [sortedEvents, registerPosition, scrollPos]);

  // Hover 时调用 setHoveredEvent
  const handleMouseEnter = (ev, i) => {
    if (!ev.detail) return;
    const rect = cardRefs.current[i].getBoundingClientRect();
    setHoveredEvent({
      rect,
      title: ev.title,
      detail: ev.detail
    });
  };
  const handleMouseLeave = () => {
    setHoveredEvent(null);
  };

  return (
    <div
      style={{
        position: 'absolute',
        top: groupTop,
        left: 0,
        width: '280px',
        zIndex: 500,
      }}
    >
      {sortedEvents.map((ev, i) => {
        const offsetTop = offsets[i];
        return (
          <div
            key={ev.id}
            ref={(el) => (cardRefs.current[i] = el)}
            style={{
              position: 'absolute',
              left: '10px', 
              top: offsetTop + 'px',
              width: '180px',
              height: '60px',
              padding: '10px',
              border: '2px solid #ddd',
              borderRadius: '8px',
              backgroundColor: '#fff',
              boxShadow: '0 2px 4px rgba(0,0,0,0.08)',
              cursor: 'pointer',
              transition: 'all 0.2s ease',
            }}
            onClick={() => onEventClick(ev.id)}
            onMouseEnter={() => handleMouseEnter(ev, i)}
            onMouseLeave={handleMouseLeave}
          >
            {/* 标题 */}
            <div style={{ 
              fontWeight: 'bold', 
              marginBottom: '4px',
              fontSize: '14px',
              color: '#007BFF',
            }}>
              {ev.title}
            </div>
            {/* 日期 */}
            <div style={{ 
              fontSize: '13px', 
              marginBottom: '6px', 
              color: '#555' 
            }}>
              {ev.date}
            </div>

            {/* 删除按钮：颜色改为蓝色 */}
            <button
              onClick={(e) => {
                e.stopPropagation();
                onDelete(ev.id);
              }}
              style={{
                position: 'absolute',
                top: '6px',
                right: '6px',
                background: 'transparent',
                border: 'none',
                color: 'rgba(0,0,255,0.6)',
                fontSize: '14px',
                cursor: 'pointer',
                transition: 'color 0.2s ease',
              }}
            >
              ×
            </button>
          </div>
        );
      })}
    </div>
  );
}

/**
 * Timeline 组件：一条时间轴 + 标题 + 事件分组
 * - 不再提供添加按钮（统一放到右上角）
 */
function Timeline({
  timelineIndex,
  wave,
  registerPosition,
  onEventClick,
  onDeleteEvent,
  scrollPos,
  setHoveredEvent,
}) {
  const timelineHeight = 1600;

  // 根据 wave.events 里最早的日期，确定本时间轴的起点
  const timestamps = wave.events.map((e) => new Date(e.date).getTime());
  const firstEventTime = timestamps.length > 0 ? Math.min(...timestamps) : Date.now();
  const firstEventDate = new Date(firstEventTime);
  const timelineStart = new Date(firstEventDate.getFullYear(), 0, 1).getTime();

  // 这里定义时间跨度，比如 8 年
  const eightYearsInMs = 8 * 365.25 * 24 * 60 * 60 * 1000;
  const timelineEndTime = timelineStart + eightYearsInMs;
  const safeTimeSpan = timelineEndTime - timelineStart;

  // 按日期排序
  const sortedEvents = [...wave.events].sort((a, b) => new Date(a.date) - new Date(b.date));

  // 将事件按照“相距 <= 3 个月”分组
  const THREE_MONTHS_MS = 3 * 30 * 24 * 60 * 60 * 1000;
  const eventGroups = [];
  let currentGroup = [];

  sortedEvents.forEach((ev, index) => {
    if (index === 0) {
      currentGroup.push(ev);
    } else {
      const prevTime = new Date(sortedEvents[index - 1].date).getTime();
      const currTime = new Date(ev.date).getTime();
      if (currTime - prevTime <= THREE_MONTHS_MS) {
        currentGroup.push(ev);
      } else {
        eventGroups.push([...currentGroup]);
        currentGroup = [ev];
      }
    }
  });
  if (currentGroup.length > 0) {
    eventGroups.push(currentGroup);
  }

  // 防止分组重叠
  let lastUsedPos = 0;
  const groupPositions = [];
  const groupSpacing = 20;

  function estimateGroupHeight(group) {
    const n = group.length;
    return 80 * (n - 1) + 60;
  }

  eventGroups.forEach((group) => {
    const firstEv = group[0];
    const currentTime = new Date(firstEv.date).getTime();
    const ratio = (currentTime - timelineStart) / safeTimeSpan;
    const naivePos = ratio * timelineHeight;
    const groupHeight = estimateGroupHeight(group);
    const finalPos = Math.max(naivePos, lastUsedPos + groupSpacing);
    groupPositions.push(finalPos);
    lastUsedPos = finalPos + groupHeight;
  });

  // 为各时间线指定图标映射
  const iconMap = {
    'PC': '🖥',
    'Internet': '🌐',
    'Smartphones': '📱',
    'Elec Vehicles': '🚗',
    'Generative AI': '💡',
    'Robot': '🤖',
  };
  // 如果 wave.icon 存在则用 wave.icon，否则看 title 是否能匹配到 iconMap，否则默认 💻
  const icon = iconMap[wave.title] || '💻';

  return (
    <div style={{ 
      flex: '1', 
      margin: '0 20px', 
      position: 'relative',
      padding: '15px',
      backgroundColor: 'rgba(255,255,255,0.7)',
      borderRadius: '8px',
      boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
    }}>
      {/* 时间线顶部标题 + 图标 */}
      <div style={{ 
        display: 'flex', 
        alignItems: 'center', 
        marginBottom: '2px',
        borderBottom: '1px solid #eee',
        paddingBottom: '2px',
      }}>
        <h2 style={{ 
          fontSize: '18px', 
          margin: 0,
          color: '#2c3e50',
          fontWeight: '600',
          display: 'flex',
          alignItems: 'center'
        }}>
          <span style={{ marginRight: '8px' }}>{icon}</span>
          {wave.title}
        </h2>
      </div>
      <div style={{ 
        fontSize: '14px', 
        color: '#666', 
        marginBottom: '70px', 
        textAlign: 'center',
        fontStyle: 'italic',
      }}>
        {wave.period}
      </div>

      {/* 纵向的主轴 */}
      <div style={{ 
        position: 'relative', 
        width: '2px', 
        backgroundColor: '#bbb', 
        height: timelineHeight + 'px', 
        marginLeft: '0', 
        marginTop: '-70px',
        borderRadius: '1px' 
      }}>
        <div style={{ position: 'absolute', top: '0', left: '-10px', right: '-10px' }}>
          {eventGroups.map((group, idx) => {
            const finalPos = groupPositions[idx];
            return (
              <EventGroup
                key={idx}
                events={group}
                registerPosition={registerPosition}
                onEventClick={onEventClick}
                onDelete={(id) => onDeleteEvent(id)}
                scrollPos={scrollPos}
                groupTop={finalPos}
                setHoveredEvent={setHoveredEvent}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

/**
 * ConnectionsLayer 组件：渲染所有正式连接以及正在建立的临时连接
 */
function ConnectionsLayer({
  connections,
  eventPositions,
  containerRef,
  pendingConnection,
  pendingConnectionEnd,
  onDeleteConnection,
}) {
  const containerRect = containerRef.current
    ? containerRef.current.getBoundingClientRect()
    : { left: 0, top: 0 };

  return (
    <svg
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: 1,
        pointerEvents: 'none',
      }}
    >
      <defs>
        <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="10" refY="3.5" orient="auto">
          <polygon points="0 0, 10 3.5, 0 7" fill="red" />
        </marker>
      </defs>
      {connections.map((conn, index) => {
        const fromPos = eventPositions[conn.from];
        const toPos = eventPositions[conn.to];
        if (!fromPos || !toPos) return null;

        const adjustedFrom = { x: fromPos.x - containerRect.left, y: fromPos.y - containerRect.top };
        const adjustedTo = { x: toPos.x - containerRect.left, y: toPos.y - containerRect.top };

        return (
          <ConnectionLine
            key={index}
            fromPos={adjustedFrom}
            toPos={adjustedTo}
            reason={conn.reason}
            onDelete={() => onDeleteConnection(index)}
          />
        );
      })}

      {/* 如果正在连线，则画一条“临时线”跟随鼠标 */}
      {pendingConnection &&
        pendingConnectionEnd &&
        eventPositions[pendingConnection.startEventId] &&
        (() => {
          const startPosGlobal = eventPositions[pendingConnection.startEventId];
          const startPos = {
            x: startPosGlobal.x - containerRect.left,
            y: startPosGlobal.y - containerRect.top,
          };
          return (
            <line
              x1={startPos.x}
              y1={startPos.y}
              x2={pendingConnectionEnd.x}
              y2={pendingConnectionEnd.y}
              stroke="red"
              strokeWidth="3"
              markerEnd="url(#arrowhead)"
              style={{ pointerEvents: 'none' }}
            />
          );
        })()}
    </svg>
  );
}

/**
 * ConnectionReasonModal - Modal for entering connection reason
 */
function ConnectionReasonModal({ isOpen, onClose, onSubmit }) {
  const [reason, setReason] = useState('Manual connection');

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(reason || 'Manual connection');
    onClose();
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
    }}>
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '8px',
        width: '400px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
      }}>
        <h3 style={{ marginTop: 0 }}>Connection Reason</h3>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Reason for connection:</label>
            <input 
              type="text" 
              value={reason} 
              onChange={(e) => setReason(e.target.value)}
              style={{ 
                width: '100%', 
                padding: '8px', 
                borderRadius: '4px',
                border: '1px solid #ddd'
              }}
              autoFocus
            />
          </div>
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
            <button 
              type="button" 
              onClick={onClose}
              style={{
                padding: '8px 16px',
                border: '1px solid #ddd',
                borderRadius: '4px',
                backgroundColor: '#f1f1f1',
                cursor: 'pointer'
              }}
            >
              Cancel
            </button>
            <button 
              type="submit"
              style={{
                padding: '8px 16px',
                border: 'none',
                borderRadius: '4px',
                backgroundColor: '#007BFF',
                color: 'white',
                cursor: 'pointer'
              }}
            >
              Create Connection
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

/**
 * Showcase 主容器组件
 */
function Showcase() {
  // 波次 (waves) 与连接 (connections)
  const [waves, setWaves] = useState([]);
  const [connections, setConnections] = useState([]);

  // 注册事件节点的位置信息 (eventPositions[eventId] = { x, y })
  const [eventPositions, setEventPositions] = useState({});
  const registerPosition = (id, pos) => {
    setEventPositions((prev) => ({ ...prev, [id]: pos }));
  };

  // 用于连接事件的临时状态
  const [pendingConnection, setPendingConnection] = useState(null); // { startEventId }
  const [pendingConnectionEnd, setPendingConnectionEnd] = useState(null);

  // 全局添加事件 Modal
  const [showGlobalAddModal, setShowGlobalAddModal] = useState(false);

  // 连接原因 Modal
  const [showConnectionModal, setShowConnectionModal] = useState(false);
  // 用于标记连接终点
  const [pendingTo, setPendingTo] = useState(null);

  // 全局 hover 事件：{ rect, title, detail }
  const [hoveredEvent, setHoveredEvent] = useState(null);

  // 从 /data.json 加载数据
  useEffect(() => {
    fetch('/data.json')
      .then((res) => res.json())
      .then((data) => {
        setWaves(data.waves);
        setConnections(data.connections);
      })
      .catch((error) => console.error("Error loading data:", error));
  }, []);

  // 点击事件时，若无 pendingConnection，则设为起点；若已有，则设为终点
  const handleEventClick = (eventId) => {
    if (!pendingConnection) {
      setPendingConnection({ startEventId: eventId });
    } else {
      if (pendingConnection.startEventId === eventId) {
        // 如果再次点到同一个事件，取消连接
        setPendingConnection(null);
        setPendingConnectionEnd(null);
        return;
      }
      // 否则进入「填写连接原因」阶段
      setPendingTo(eventId);
      setShowConnectionModal(true);
    }
  };

  // 提交新事件
  const handleSubmitNewEvent = (eventData) => {
    const newId = generateUniqueId();
    setWaves((prev) => {
      const newWaves = [...prev];
      // Make sure we're using the correct timeline index from the form
      const idx = parseInt(eventData.timelineIndex, 10);
      console.log("Adding event to timeline index:", idx);
      if (newWaves[idx]) {
        newWaves[idx].events.push({ 
          id: newId,
          date: eventData.date,
          title: eventData.title,
          detail: eventData.detail 
        });
      }
      return newWaves;
    });
  };

  // 填写连接原因后，添加新连接
  const handleSubmitConnection = (reason) => {
    setConnections((prev) => [
      ...prev,
      { from: pendingConnection.startEventId, to: pendingTo, reason },
    ]);
    setPendingConnection(null);
    setPendingConnectionEnd(null);
    setPendingTo(null);
  };

  // 删除事件
  const handleDeleteEvent = (eventId) => {
    // 在所有 wave 中查找并删除
    setWaves((prev) => {
      return prev.map(w => ({
        ...w,
        events: w.events.filter(e => e.id !== eventId)
      }));
    });
    // 删除与之相关的连接
    setConnections((prev) =>
      prev.filter((conn) => conn.from !== eventId && conn.to !== eventId)
    );
  };

  // 删除连接
  const handleDeleteConnection = (connIndex) => {
    setConnections((prev) => prev.filter((_, index) => index !== connIndex));
  };

  // 跟踪滚动位置（用于在 window.scrollY 改变时重新计算坐标）
  const [scrollPos, setScrollPos] = useState(0);
  useEffect(() => {
    const handleScroll = () => setScrollPos(window.scrollY);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // 如果正在连线，则鼠标移动时更新临时连线终点
  const containerRef = useRef(null);
  const handleMouseMove = (e) => {
    if (pendingConnection) {
      const containerRect = containerRef.current.getBoundingClientRect();
      setPendingConnectionEnd({
        x: e.clientX - containerRect.left,
        y: e.clientY - containerRect.top,
      });
    }
  };

  // 构造 timelineOptions 供全局添加事件时选择
  const timelineOptions = waves.map((wave, index) => ({
    index,
    title: wave.title,
  }));

  return (
    <div 
      ref={containerRef} 
      onMouseMove={handleMouseMove} 
      style={{ 
        padding: '20px', 
        position: 'relative'
      }}
    >
      {/* 全局添加事件 Modal */}
      <AddEventModal 
        isOpen={showGlobalAddModal} 
        onClose={() => setShowGlobalAddModal(false)} 
        onSubmit={handleSubmitNewEvent} 
        timelineOptions={timelineOptions}
      />

      {/* 填写连接原因 Modal */}
      <ConnectionReasonModal 
        isOpen={showConnectionModal}
        onClose={() => {
          setShowConnectionModal(false);
          setPendingConnection(null);
          setPendingConnectionEnd(null);
        }}
        onSubmit={handleSubmitConnection}
      />

      {/* 全局 Hover 弹框 */}
      <GlobalTooltip hoveredEvent={hoveredEvent} />

      <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>
        Analogical Timeline of Tech Wave
      </h1>
      <h2 style={{ textAlign: 'center'}}>History doesn't repeat itself, but it often rhymes.</h2>

      {/* 右上角 全局添加事件按钮 */}
      <button
        onClick={() => setShowGlobalAddModal(true)}
        style={{
          position: 'fixed',
          top: '20px',
          right: '20px',
          width: '40px',
          height: '40px',
          borderRadius: '50%',
          backgroundColor: '#28a745',
          border: 'none',
          color: '#fff',
          fontSize: '24px',
          fontWeight: 'bold',
          cursor: 'pointer',
          zIndex: 1001,
          boxShadow: '0 2px 5px rgba(0,0,0,0.3)',
        }}
        title="Add Event"
      >
        +
      </button>

      {/* 多个 wave 并排显示 */}
      <div 
        style={{ 
          display: 'flex', 
          justifyContent: 'space-around', 
          flexWrap: 'wrap', 
          marginTop: '40px', 
          position: 'relative',
          zIndex: 0,
        }}
      >
        {waves.map((wave, index) => (
          <Timeline
            key={index}
            timelineIndex={index}
            wave={wave}
            registerPosition={registerPosition}
            onEventClick={handleEventClick}
            onDeleteEvent={handleDeleteEvent}
            scrollPos={scrollPos}
            setHoveredEvent={setHoveredEvent}
          />
        ))}
      </div>

      {/* 画所有连接的 SVG 层 */}
      <ConnectionsLayer
        connections={connections}
        eventPositions={eventPositions}
        containerRef={containerRef}
        pendingConnection={pendingConnection}
        pendingConnectionEnd={pendingConnectionEnd}
        onDeleteConnection={handleDeleteConnection}
      />
    </div>
  );
}

export default Showcase;
