import React from 'react';

function Showcase() {
  // 每条时间线的数据结构：name 表示浪潮名称与年份区间，timeline 数组包含 4 年的关键事件
  const waves = [
    {
      name: '个人电脑（PC）浪潮：1979 - 1982',
      timeline: [
        { year: 1979, event: '早期家用电脑兴起，市场初见端倪' },
        { year: 1980, event: 'IBM 投入个人电脑研发并组建团队' },
        { year: 1981, event: 'IBM PC 正式推出，引发行业关注' },
        { year: 1982, event: '克隆机和 MS-DOS 生态开始繁荣' },
      ],
    },
    {
      name: '互联网浪潮：1994 - 1997',
      timeline: [
        { year: 1994, event: '网景（Netscape）浏览器发布，初代互联网热潮' },
        { year: 1995, event: '雅虎、亚马逊等互联网公司崛起' },
        { year: 1996, event: '电信基础设施升级，更多人接入互联网' },
        { year: 1997, event: '门户网站爆发，互联网泡沫初现' },
      ],
    },
    {
      name: '智能手机浪潮：2007 - 2010',
      timeline: [
        { year: 2007, event: 'iPhone 发布，标志智能手机时代开启' },
        { year: 2008, event: 'Android 系统推出，App Store 上线' },
        { year: 2009, event: '智能手机硬件迭代加速，用户规模激增' },
        { year: 2010, event: '移动互联网兴起，应用生态大爆发' },
      ],
    },
    {
      name: '生成式 AI 浪潮：2019 - 2022',
      timeline: [
        { year: 2019, event: 'Transformer 模型进一步成熟，多模态研究活跃' },
        { year: 2020, event: 'GPT-3 面世，超大规模预训练引发关注' },
        { year: 2021, event: '多家科技公司布局，AI 创业与投融资升温' },
        { year: 2022, event: 'ChatGPT 等产品爆红，AI 进入公众视野' },
      ],
    },
    {
      name: '电动车（EV）浪潮：2015 - 2018',
      timeline: [
        { year: 2015, event: '特斯拉 Model S 逐渐普及，带动市场认知' },
        { year: 2016, event: '传统车企大规模投入电动车研发' },
        { year: 2017, event: '电池成本下降，充电基础设施建设加速' },
        { year: 2018, event: '各国出台政策补贴，电动车进入爆发前夜' },
      ],
    },
    {
      name: '新能源浪潮：2008 - 2011',
      timeline: [
        { year: 2008, event: '全球金融危机背景下，清洁能源概念升温' },
        { year: 2009, event: '光伏、风电技术进步，成本开始逐步下降' },
        { year: 2010, event: '各国政府出台可再生能源补贴与政策' },
        { year: 2011, event: '新能源产业链逐渐形成，规模化应用临近' },
      ],
    },
    {
      name: '社交媒体浪潮：2004 - 2007',
      timeline: [
        { year: 2004, event: 'Facebook 创立，社交网络雏形显现' },
        { year: 2005, event: 'YouTube 出现，UGC（用户生成内容）兴起' },
        { year: 2006, event: 'Twitter 上线，碎片化信息传播模式走红' },
        { year: 2007, event: '移动社交开始萌芽，社交媒体用户激增' },
      ],
    },
    {
      name: '区块链浪潮：2015 - 2018',
      timeline: [
        { year: 2015, event: '以太坊推出智能合约，引发 DApp 概念' },
        { year: 2016, event: '区块链技术初探，企业与金融机构试水' },
        { year: 2017, event: 'ICO 热潮与泡沫并存，监管逐步收紧' },
        { year: 2018, event: '市场大幅回调，技术落地与合规化探索' },
      ],
    },
    {
      name: '太空探索浪潮：2012 - 2015',
      timeline: [
        { year: 2012, event: 'SpaceX 开始进行可回收火箭试验' },
        { year: 2013, event: '商业航天吸引资本关注，NASA 等机构合作' },
        { year: 2014, event: '卫星互联网概念酝酿，更多火箭发射测试' },
        { year: 2015, event: '猎鹰 9 号首次成功回收，开启太空商业化新篇章' },
      ],
    },
    {
      name: '生物技术浪潮：2015 - 2018',
      timeline: [
        { year: 2015, event: 'CRISPR 基因编辑技术走红，引发科研热潮' },
        { year: 2016, event: '精准医疗、个性化治疗等概念兴起' },
        { year: 2017, event: '合成生物学与免疫疗法发展迅猛' },
        { year: 2018, event: '伦理与监管争议加剧，产业进入调整期' },
      ],
    },
  ];

  return (
    <div style={{ padding: '20px' }}>
      <h1>科技浪潮「早期 4 年」平行时间线示例</h1>
      <p>下面用表格的形式，对比各大科技浪潮在“前期 4 年”内的关键事件或趋势。</p>
      <table 
        style={{ 
          borderCollapse: 'collapse', 
          width: '100%', 
          marginTop: '20px' 
        }}
      >
        <thead>
          <tr style={{ background: '#f0f0f0' }}>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>科技浪潮</th>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>第 1 年</th>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>第 2 年</th>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>第 3 年</th>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>第 4 年</th>
          </tr>
        </thead>
        <tbody>
          {waves.map((wave) => (
            <tr key={wave.name}>
              <td 
                style={{ 
                  border: '1px solid #ccc', 
                  padding: '8px', 
                  fontWeight: 'bold',
                  verticalAlign: 'top',
                  background: '#fafafa',
                  width: '20%'
                }}
              >
                {wave.name}
              </td>
              {wave.timeline.map((item) => (
                <td 
                  key={item.year} 
                  style={{ 
                    border: '1px solid #ccc', 
                    padding: '8px', 
                    verticalAlign: 'top',
                    width: '20%'
                  }}
                >
                  <strong>{item.year}</strong>
                  <br />
                  {item.event}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Showcase;
