import React, { useState, useEffect } from 'react';
import Confetti from 'react-confetti';

function Showcase() {
  const [showConfetti, setShowConfetti] = useState(false);
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  useEffect(() => {
    function handleResize() {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    }
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Example timeline data
  const llmTimeline = [
    { year: '2021', content: 'GPT-3 text-davinci-002 API' },
    { year: '2022', content: 'ChatGPT with GPT-3.5' },
    { year: '2023', content: 'GPT-4 released' },
    { year: '2024', content: 'Enhanced reasoning (GPT-4.5?)' },
  ];

  const robotTimeline = [
    { year: 'Feb 2024', content: 'Figure valuation at $2.6B' },
    { year: 'Jan 2025', content: 'Cursor $2.5B -> Anthropic $60B' },
    { year: 'Feb 2025', content: 'Figure jumps from $2.6B to $39.5B' },
    { year: 'Feb 2025', content: 'Helix released: reasoning robot with collaboration' },
  ];

  return (
    <div style={{ position: 'relative', padding: '20px' }}>
      {/* Inline style for the entire component */}
      <style>{`
        * {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
        }
        body {
          font-family: Arial, sans-serif;
        }
        .showcase-header {
          text-align: center;
          margin-bottom: 2rem;
        }
        .showcase-header h1 {
          font-size: 2rem;
          margin-bottom: 0.5rem;
        }
        .showcase-header h2 {
          font-size: 1.3rem;
          font-weight: 400;
          margin-bottom: 1.5rem;
          color: #666;
        }
        .showcase-header button {
          background-color: #ff7f50;
          color: #fff;
          border: none;
          padding: 10px 20px;
          cursor: pointer;
          font-size: 1rem;
          border-radius: 8px;
          transition: background-color 0.3s;
        }
        .showcase-header button:hover {
          background-color: #ff9b75;
        }

        .showcase-text {
          max-width: 800px;
          margin: 0 auto 3rem auto;
          text-align: left;
          line-height: 1.6;
          font-size: 1rem;
          color: #333;
        }

        /* Timeline container: two columns side by side */
        .timeline-section {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          gap: 2rem;
          max-width: 1100px;
          margin: 0 auto;
        }

        /* Each timeline wrapper */
        .timeline-wrapper {
          flex: 1;
          position: relative;
          padding: 0 20px;
        }

        .timeline-wrapper h2 {
          text-align: center;
          margin-bottom: 1.5rem;
        }

        /* Vertical line for each timeline */
        .timeline-wrapper::before {
          content: '';
          position: absolute;
          left: 30px;
          top: 0;
          width: 2px;
          height: 100%;
          background-color: #ddd;
        }

        /* Timeline items */
        .timeline-item {
          position: relative;
          margin: 1rem 0;
          padding-left: 50px;
          opacity: 0; /* for animation start */
          animation-duration: 0.8s;
          animation-fill-mode: forwards;
          /* default to fadeInUp, can override by adding a class */
        }

        /* Dot on the line */
        .timeline-item::before {
          content: '';
          position: absolute;
          left: 22px;
          top: 10px;
          width: 15px;
          height: 15px;
          background-color: #ff7f50;
          border-radius: 50%;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
        }

        .timeline-year {
          display: inline-block;
          background-color: #ff7f50;
          color: #fff;
          padding: 6px 12px;
          border-radius: 20px;
          margin-bottom: 0.5rem;
          font-weight: bold;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
        }

        .timeline-content {
          background-color: #f7f7f7;
          padding: 10px 15px;
          border-radius: 8px;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
          margin-top: 0.5rem;
        }

        /* Keyframes for the animations */
        @keyframes fadeInLeft {
          from {
            transform: translateX(-30px);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }
        @keyframes fadeInRight {
          from {
            transform: translateX(30px);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }

        /* Classes that we can conditionally add for each timeline side */
        .fade-in-left {
          animation-name: fadeInLeft;
        }
        .fade-in-right {
          animation-name: fadeInRight;
        }
      `}</style>

      {/* Confetti */}
      {showConfetti && (
        <Confetti
          width={windowSize.width}
          height={windowSize.height}
        />
      )}

      {/* Header */}
      <div className="showcase-header">
        <h1>Robot in 2025 as GenAI in 2022</h1>
        <h2>Riding the robot wave with lessons learned from GenAI boom</h2>
        <button onClick={() => setShowConfetti(!showConfetti)}>
          {showConfetti ? 'Stop Confetti' : 'Celebrate!'}
        </button>
      </div>

      {/* Text Section */}
      <div className="showcase-text">
        <p>
          Don't regret missing the GenAI boom—we are about to embrace an even bigger robotics wave soon.
        </p>
        <p>
          Over the past year, we witnessed many AI advancements: VLM, open-source reasoning models,
          and multi-agent frameworks. All of these point towards robotics as a key application,
          as reflected in Figure’s valuation surge from $2.6B (Feb 2024) to $39.5B (Feb 2025).
          With great waves come loud tides—too many exciting opportunities in the robot wave
          makes it difficult to focus on the right things.
        </p>
        <p>
          Yet, when we look back at the history of GenAI, things becomes much clearer.
          Let’s discuss together the trial and error in GenAI back in 2022 to better ride the robot wave in 2025.
        </p>
      </div>

      {/* Timeline Section */}
      <div className="timeline-section">
        {/* LLM Timeline */}
        <div className="timeline-wrapper">
          <h2>LLM Timeline</h2>
          {llmTimeline.map((item, index) => (
            <div
              className={`timeline-item fade-in-left`}
              style={{ animationDelay: `${index * 0.2}s` }}
              key={index}
            >
              <div className="timeline-year">{item.year}</div>
              <div className="timeline-content">{item.content}</div>
            </div>
          ))}
        </div>

        {/* Robot Timeline */}
        <div className="timeline-wrapper">
          <h2>Robot Timeline</h2>
          {robotTimeline.map((item, index) => (
            <div
              className={`timeline-item fade-in-right`}
              style={{ animationDelay: `${index * 0.2}s` }}
              key={index}
            >
              <div className="timeline-year">{item.year}</div>
              <div className="timeline-content">{item.content}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Showcase;
